          </div>
        </DialogContent>
      </Dialog>
    </div>
  );
}

import { useState, useEffect } from "react";
import { Search, Eye, CheckCircle, XCircle, AlertCircle } from "lucide-react";
import { Input } from "@/components/ui/input";
import { Button } from "@/components/ui/button";
import {
  Table,
  TableBody,
  TableCell,
  TableHead,
  TableHeader,
  TableRow,
} from "@/components/ui/table";
import {
  Select,
  SelectContent,
  SelectItem,
  SelectTrigger,
  SelectValue,
} from "@/components/ui/select";
import { Badge } from "@/components/ui/badge";
import {
  Dialog,
  DialogContent,
  DialogDescription,
  DialogFooter,
  DialogHeader,
  DialogTitle,
} from "@/components/ui/dialog";
import { Label } from "@/components/ui/label";
import { Textarea } from "@/components/ui/textarea";
import { toast } from "sonner";
import { useNavigate } from "react-router-dom";
import { adminService, Charity } from "@/services/admin";


export default function Charities() {
  const navigate = useNavigate();
  const [searchTerm, setSearchTerm] = useState("");
  const [filterStatus, setFilterStatus] = useState("all");
  const [selectedCharity, setSelectedCharity] = useState<Charity | null>(null);
  const [isDetailDialogOpen, setIsDetailDialogOpen] = useState(false);
  const [isRejectDialogOpen, setIsRejectDialogOpen] = useState(false);
  const [rejectReason, setRejectReason] = useState("");
  const [charities, setCharities] = useState<Charity[]>([]);
  const [isLoading, setIsLoading] = useState(true);
  const [currentPage, setCurrentPage] = useState(1);

  useEffect(() => {
    fetchCharities();
  }, [currentPage, filterStatus]);

  const fetchCharities = async () => {
    setIsLoading(true);
    try {
      const response = await adminService.getAllCharities(currentPage, {
        status: filterStatus !== 'all' ? filterStatus : undefined
      });
      setCharities(response.data);
    } catch (error: any) {
      console.error('Failed to fetch charities:', error);
      toast.error('Failed to load charities');
    } finally {
      setIsLoading(false);
    }
  };

  const handleViewDetail = async (charity: Charity) => {
    try {
      const details = await adminService.getCharityDetails(charity.id);
      setSelectedCharity(details);
      setIsDetailDialogOpen(true);
    } catch (error) {
      toast.error('Failed to load charity details');
    }
  };

  const handleApprove = async (charityId: number) => {
    try {
      await adminService.approveCharity(charityId);
      toast.success("Charity approved successfully");
      setIsDetailDialogOpen(false);
      fetchCharities(); // Refresh list
    } catch (error: any) {
      toast.error(error.response?.data?.message || 'Failed to approve charity');
    }
  };

  const handleReject = async () => {
    if (!rejectReason.trim()) {
      toast.error("Please provide a rejection reason");
      return;
    }
    if (!selectedCharity) return;

    try {
      await adminService.rejectCharity(selectedCharity.id, rejectReason);
      toast.success("Charity rejected");
      setIsRejectDialogOpen(false);
      setIsDetailDialogOpen(false);
      setRejectReason("");
      fetchCharities(); // Refresh list
    } catch (error: any) {
      toast.error(error.response?.data?.message || 'Failed to reject charity');
    }
  };

  const handleRequestInfo = (charityId: number) => {
    toast.info("Information request sent to charity");
    setIsDetailDialogOpen(false);
  };

  const getStatusBadge = (status: string) => {
    switch (status.toLowerCase()) {
      case "approved":
        return <Badge className="bg-green-600">Approved</Badge>;
      case "rejected":
        return <Badge variant="destructive">Rejected</Badge>;
      case "pending":
        return <Badge variant="secondary">Pending</Badge>;
      default:
        return <Badge>{status}</Badge>;
    }
  };

  const filteredCharities = charities.filter(charity => {
    const matchesSearch = charity.name.toLowerCase().includes(searchTerm.toLowerCase()) ||
                         charity.contact_email.toLowerCase().includes(searchTerm.toLowerCase());
    return matchesSearch;
  });

  if (isLoading) {
    return (
      <div className="flex items-center justify-center h-64">
        <div className="text-muted-foreground">Loading charities...</div>
      </div>
    );
  }

  return (
    <div className="space-y-6">
      <div>
        <h1 className="text-3xl font-bold tracking-tight">Charity Registrations</h1>
        <p className="text-muted-foreground">
          Review and manage charity applications
        </p>
      </div>

      <div className="flex gap-4">
        <div className="relative flex-1">
          <Search className="absolute left-3 top-1/2 h-4 w-4 -translate-y-1/2 text-muted-foreground" />
          <Input
            placeholder="Search charities..."
            value={searchTerm}
            onChange={(e) => setSearchTerm(e.target.value)}
            className="pl-9"
          />
        </div>
        <Select value={filterStatus} onValueChange={setFilterStatus}>
          <SelectTrigger className="w-[180px]">
            <SelectValue placeholder="Filter by status" />
          </SelectTrigger>
          <SelectContent>
            <SelectItem value="all">All Status</SelectItem>
            <SelectItem value="pending">Pending</SelectItem>
            <SelectItem value="approved">Approved</SelectItem>
            <SelectItem value="rejected">Rejected</SelectItem>
          </SelectContent>
        </Select>
      </div>

      <div className="rounded-md border bg-card">
        <Table>
          <TableHeader>
            <TableRow>
              <TableHead>ID</TableHead>
              <TableHead>Name</TableHead>
              <TableHead>Organization Email</TableHead>
              <TableHead>Status</TableHead>
              <TableHead>Submitted</TableHead>
              <TableHead className="text-right">Actions</TableHead>
            </TableRow>
          </TableHeader>
          <TableBody>
            {filteredCharities.length === 0 ? (
              <TableRow>
                <TableCell colSpan={6} className="text-center text-muted-foreground">
                  No charities found
                </TableCell>
              </TableRow>
            ) : filteredCharities.map((charity) => (
              <TableRow key={charity.id}>
                <TableCell className="font-medium">{charity.id}</TableCell>
                <TableCell>{charity.name}</TableCell>
                <TableCell>{charity.contact_email}</TableCell>
                <TableCell>{getStatusBadge(charity.verification_status)}</TableCell>
                <TableCell>{new Date(charity.created_at).toLocaleDateString()}</TableCell>
                <TableCell className="text-right">
                  <Button
                    variant="ghost"
                    size="icon"
                    onClick={() => handleViewDetail(charity)}
                  >
                    <Eye className="h-4 w-4" />
                  </Button>
                </TableCell>
              </TableRow>
            ))}
          </TableBody>
        </Table>
      </div>

      <Dialog open={isDetailDialogOpen} onOpenChange={setIsDetailDialogOpen}>
        <DialogContent className="max-w-2xl">
          <DialogHeader>
            <DialogTitle>Charity Application Details</DialogTitle>
            <DialogDescription>
              Review the charity registration information
            </DialogDescription>
          </DialogHeader>
          {selectedCharity && (
            <div className="grid gap-4 py-4">
              <div className="grid gap-2">
                <Label className="font-semibold">Organization Name</Label>
                <p>{selectedCharity.name}</p>
              </div>
              <div className="grid gap-2">
                <Label className="font-semibold">Email</Label>
                <p>{selectedCharity.contact_email}</p>
              </div>
              <div className="grid gap-2">
                <Label className="font-semibold">Registration Number</Label>
                <p className="text-sm text-muted-foreground">{selectedCharity.reg_no || 'N/A'}</p>
              </div>
              <div className="grid gap-2">
                <Label className="font-semibold">Mission</Label>
                <p className="text-sm text-muted-foreground">{selectedCharity.mission || 'N/A'}</p>
              </div>
              <div className="grid gap-2">
                <Label className="font-semibold">Status</Label>
                {getStatusBadge(selectedCharity.verification_status)}
              </div>
              {selectedCharity.verification_notes && (
                <div className="grid gap-2">
                  <Label className="font-semibold">Notes</Label>
                  <p className="text-sm text-muted-foreground">{selectedCharity.verification_notes}</p>
                </div>
              )}
              {selectedCharity.documents && selectedCharity.documents.length > 0 && (
                <div className="grid gap-2">
                  <Label className="font-semibold">Submitted Documents</Label>
                  <div className="space-y-2">
                    {selectedCharity.documents.map((doc, index) => (
                      <div key={index} className="flex items-center justify-between rounded-md border p-2">
                        <span className="text-sm">{doc.document_type}</span>
                        <Button variant="ghost" size="sm">
                          View
                        </Button>
                      </div>
                    ))}
                  </div>
                </div>
              )}
            </div>
          )}
          <DialogFooter className="flex gap-2">
            {selectedCharity?.verification_status === "pending" && (
              <>
                <Button
                  variant="outline"
                  onClick={() => handleRequestInfo(selectedCharity.id)}
                >
                  <AlertCircle className="h-4 w-4 mr-2" />
                  Request Info
                </Button>
                <Button
                  variant="destructive"
                  onClick={() => setIsRejectDialogOpen(true)}
                >
                  <XCircle className="h-4 w-4 mr-2" />
                  Reject
                </Button>
                <Button onClick={() => handleApprove(selectedCharity.id)}>
                  <CheckCircle className="h-4 w-4 mr-2" />
                  Approve
                </Button>
              </>
            )}
          </DialogFooter>
        </DialogContent>
      </Dialog>

      <Dialog open={isRejectDialogOpen} onOpenChange={setIsRejectDialogOpen}>
        <DialogContent>
          <DialogHeader>
            <DialogTitle>Reject Charity Application</DialogTitle>
            <DialogDescription>
              Please provide a reason for rejection
            </DialogDescription>
          </DialogHeader>
          <div className="grid gap-4 py-4">
            <Textarea
              placeholder="Enter rejection reason..."
              value={rejectReason}
              onChange={(e) => setRejectReason(e.target.value)}
              rows={4}
            />
          </div>
          <DialogFooter>
            <Button variant="outline" onClick={() => setIsRejectDialogOpen(false)}>
              Cancel
            </Button>
            <Button variant="destructive" onClick={handleReject}>
              Confirm Rejection
            </Button>
          </DialogFooter>
        </DialogContent>
      </Dialog>
    </div>
  );
}

import { useState, useEffect } from "react";
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Badge } from "@/components/ui/badge";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { Dialog, DialogContent, DialogDescription, DialogHeader, DialogTitle, DialogTrigger } from "@/components/ui/dialog";
import { AlertTriangle, Calendar, Clock, FileText, Eye, Edit, Filter } from "lucide-react";
import { toast } from "sonner";
import axios from "axios";

interface ExpiringDocument {
  id: number;
  doc_type: string;
  expiry_date: string;
  days_until_expiry: number;
  charity: {
    id: number;
    name: string;
    contact_email: string;
  };
}

interface ExpiredDocument {
  id: number;
  doc_type: string;
  expiry_date: string;
  days_overdue: number;
  charity: {
    id: number;
    name: string;
    contact_email: string;
    verification_status: string;
  };
}

interface ExpiryStatistics {
  expiring_in_7_days: number;
  expiring_in_30_days: number;
  expired: number;
  charities_with_expired_docs: number;
}

export default function AdminDocumentExpiry() {
  const [expiringDocuments, setExpiringDocuments] = useState<ExpiringDocument[]>([]);
  const [expiredDocuments, setExpiredDocuments] = useState<ExpiredDocument[]>([]);
  const [statistics, setStatistics] = useState<ExpiryStatistics | null>(null);
  const [loading, setLoading] = useState(true);
  const [activeTab, setActiveTab] = useState("expiring");
  const [daysFilter, setDaysFilter] = useState("30");
  const API_URL = import.meta.env.VITE_API_URL;

  useEffect(() => {
    fetchData();
  }, [daysFilter]);

  const fetchData = async () => {
    try {
      setLoading(true);
      const token = localStorage.getItem('auth_token') || sessionStorage.getItem('auth_token');

      const authHeaders = token
        ? { headers: { Authorization: `Bearer ${token}` } }
        : {};

      // Fetch expiring documents
      const expiringResponse = await axios.get(
        `${API_URL}/api/admin/documents/expiring?days=${daysFilter}`,
        authHeaders
      );
      setExpiringDocuments(expiringResponse.data?.expiring_documents ?? []);

      // Fetch expired documents
      const expiredResponse = await axios.get(
        `${API_URL}/api/admin/documents/expired`,
        authHeaders
      );
      setExpiredDocuments(expiredResponse.data?.expired_documents ?? []);

      // Fetch statistics
      const statsResponse = await axios.get(
        `${API_URL}/api/admin/documents/expiry-statistics`,
        authHeaders
      );
      setStatistics(statsResponse.data ?? null);

    } catch (error) {
      toast.error("Failed to fetch document expiry data");
    } finally {
      setLoading(false);
    }
  };

  const updateDocumentExpiry = async (documentId: number, expires: boolean, expiryDate?: string) => {
    try {
      const token = localStorage.getItem('auth_token') || sessionStorage.getItem('auth_token');
      const authHeaders = token ? { headers: { Authorization: `Bearer ${token}` } } : {};
      await axios.patch(
        `${API_URL}/api/admin/documents/${documentId}/expiry`,
        {
          expires,
          expiry_date: expiryDate,
        },
        authHeaders
      );
      toast.success("Document expiry updated successfully");
      fetchData();
    } catch (error) {
      toast.error("Failed to update document expiry");
    }
  };

  const formatDocType = (docType: string) => {
    return docType.replace(/_/g, " ").replace(/\b\w/g, l => l.toUpperCase());
  };

  const getUrgencyBadge = (days: number, isExpired: boolean = false) => {
    if (isExpired) {
      return <Badge className="bg-red-100 text-red-800">Expired</Badge>;
    }
    if (days <= 7) {
      return <Badge className="bg-red-100 text-red-800">Critical</Badge>;
    }
    if (days <= 14) {
      return <Badge className="bg-orange-100 text-orange-800">Urgent</Badge>;
    }
    if (days <= 30) {
      return <Badge className="bg-yellow-100 text-yellow-800">Warning</Badge>;
    }
    return <Badge className="bg-blue-100 text-blue-800">Notice</Badge>;
  };

  if (loading) {
    return (
      <div className="flex items-center justify-center h-64">
        <div className="animate-spin rounded-full h-8 w-8 border-b-2 border-primary"></div>
      </div>
    );
  }

  return (
    <div className="space-y-6">
      <div className="flex justify-between items-center">
        <div>
          <h1 className="text-3xl font-bold">Document Expiry Management</h1>
          <p className="text-muted-foreground">Monitor and manage charity document expirations</p>
        </div>
      </div>

      {/* Statistics Cards */}
      {statistics && (
        <div className="grid grid-cols-1 md:grid-cols-4 gap-4">
          <Card>
            <CardHeader className="pb-2">
              <CardTitle className="text-sm font-medium">Expiring in 7 Days</CardTitle>
            </CardHeader>
            <CardContent>
              <div className="text-2xl font-bold text-red-600">{statistics.expiring_in_7_days}</div>
            </CardContent>
          </Card>
          <Card>
            <CardHeader className="pb-2">
              <CardTitle className="text-sm font-medium">Expiring in 30 Days</CardTitle>
            </CardHeader>
            <CardContent>
              <div className="text-2xl font-bold text-yellow-600">{statistics.expiring_in_30_days}</div>
            </CardContent>
          </Card>
          <Card>
            <CardHeader className="pb-2">
              <CardTitle className="text-sm font-medium">Already Expired</CardTitle>
            </CardHeader>
            <CardContent>
              <div className="text-2xl font-bold text-red-600">{statistics.expired}</div>
            </CardContent>
          </Card>
          <Card>
            <CardHeader className="pb-2">
              <CardTitle className="text-sm font-medium">Affected Charities</CardTitle>
            </CardHeader>
            <CardContent>
              <div className="text-2xl font-bold text-gray-600">{statistics.charities_with_expired_docs}</div>
            </CardContent>
          </Card>
        </div>
      )}

      {/* Filters and Tabs */}
      <Card>
        <CardHeader>
          <CardTitle className="flex items-center gap-2">
            <Filter className="h-5 w-5" />
            Filters
          </CardTitle>
        </CardHeader>
        <CardContent>
          <div className="flex gap-4 items-center">
            <div className="flex gap-2">
              <Button
                variant={activeTab === "expiring" ? "default" : "outline"}
                onClick={() => setActiveTab("expiring")}
              >
                <Clock className="h-4 w-4 mr-2" />
                Expiring Documents
              </Button>
              <Button
                variant={activeTab === "expired" ? "default" : "outline"}
                onClick={() => setActiveTab("expired")}
              >
                <AlertTriangle className="h-4 w-4 mr-2" />
                Expired Documents
              </Button>
            </div>
            {activeTab === "expiring" && (
              <Select value={daysFilter} onValueChange={setDaysFilter}>
                <SelectTrigger className="w-48">
                  <SelectValue />
                </SelectTrigger>
                <SelectContent>
                  <SelectItem value="7">Next 7 days</SelectItem>
                  <SelectItem value="14">Next 14 days</SelectItem>
                  <SelectItem value="30">Next 30 days</SelectItem>
                  <SelectItem value="60">Next 60 days</SelectItem>
                </SelectContent>
              </Select>
            )}
          </div>
        </CardContent>
      </Card>

      {/* Documents List */}
      <Card>
        <CardHeader>
          <CardTitle>
            {activeTab === "expiring" ? "Expiring Documents" : "Expired Documents"} 
            ({activeTab === "expiring" ? (expiringDocuments?.length ?? 0) : (expiredDocuments?.length ?? 0)})
          </CardTitle>
        </CardHeader>
        <CardContent>
          <div className="space-y-4">
            {activeTab === "expiring" ? (
              (expiringDocuments ?? []).map((document) => (
                <div key={document.id} className="border rounded-lg p-4 space-y-3">
                  <div className="flex justify-between items-start">
                    <div className="space-y-1">
                      <div className="flex items-center gap-2">
                        <FileText className="h-4 w-4 text-blue-500" />
                        <span className="font-medium">{formatDocType(document.doc_type)}</span>
                        {getUrgencyBadge(document.days_until_expiry)}
                      </div>
                      <p className="text-sm text-muted-foreground">
                        <strong>Charity:</strong> {document.charity.name}
                      </p>
                      <p className="text-sm text-muted-foreground">
                        <strong>Contact:</strong> {document.charity.contact_email}
                      </p>
                      <div className="flex items-center gap-2 text-sm">
                        <Calendar className="h-4 w-4 text-muted-foreground" />
                        <span>Expires: {new Date(document.expiry_date).toLocaleDateString()}</span>
                        <span className="text-yellow-600 font-medium">
                          ({document.days_until_expiry} days remaining)
                        </span>
                      </div>
                    </div>
                    <div className="flex gap-2">
                      <Button
                        variant="outline"
                        size="sm"
                        onClick={() => window.open(`/admin/charities/${document.charity.id}`, '_blank')}
                      >
                        <Eye className="h-4 w-4 mr-1" />
                        View Charity
                      </Button>
                    </div>
                  </div>
                </div>
              ))
            ) : (
              (expiredDocuments ?? []).map((document) => (
                <div key={document.id} className="border border-red-200 rounded-lg p-4 space-y-3 bg-red-50">
                  <div className="flex justify-between items-start">
                    <div className="space-y-1">
                      <div className="flex items-center gap-2">
                        <FileText className="h-4 w-4 text-red-500" />
                        <span className="font-medium">{formatDocType(document.doc_type)}</span>
                        {getUrgencyBadge(0, true)}
                      </div>
                      <p className="text-sm text-muted-foreground">
                        <strong>Charity:</strong> {document.charity.name}
                      </p>
                      <p className="text-sm text-muted-foreground">
                        <strong>Contact:</strong> {document.charity.contact_email}
                      </p>
                      <p className="text-sm text-muted-foreground">
                        <strong>Status:</strong> 
                        <Badge className={document.charity.verification_status === 'approved' ? 'bg-green-100 text-green-800' : 'bg-red-100 text-red-800'}>
                          {document.charity.verification_status}
                        </Badge>
                      </p>
                      <div className="flex items-center gap-2 text-sm">
                        <Calendar className="h-4 w-4 text-red-500" />
                        <span>Expired: {new Date(document.expiry_date).toLocaleDateString()}</span>
                        <span className="text-red-600 font-medium">
                          ({document.days_overdue} days overdue)
                        </span>
                      </div>
                    </div>
                    <div className="flex gap-2">
                      <Button
                        variant="outline"
                        size="sm"
                        onClick={() => window.open(`/admin/charities/${document.charity.id}`, '_blank')}
                      >
                        <Eye className="h-4 w-4 mr-1" />
                        View Charity
                      </Button>
                    </div>
                  </div>
                </div>
              ))
            )}

            {((activeTab === "expiring" && (expiringDocuments?.length ?? 0) === 0) || 
              (activeTab === "expired" && (expiredDocuments?.length ?? 0) === 0)) && (
              <div className="text-center py-8 text-muted-foreground">
                <FileText className="h-12 w-12 mx-auto mb-4 opacity-50" />
                <p>No {activeTab} documents found.</p>
              </div>
            )}
          </div>
        </CardContent>
      </Card>

      {/* Action Recommendations */}
      <Card className="bg-blue-50 border-blue-200">
        <CardHeader>
          <CardTitle className="text-blue-800">Recommended Actions</CardTitle>
        </CardHeader>
        <CardContent className="text-blue-700">
          <ul className="space-y-2 text-sm">
            <li> Contact charities with documents expiring in 7 days immediately</li>
            <li> Send renewal reminders to charities with documents expiring in 30 days</li>
            <li> Review verification status of charities with expired documents</li>
            <li> Consider suspending charities with critical expired documents</li>
            <li> Set up automated email reminders for document renewals</li>
          </ul>
        </CardContent>
      </Card>
    </div>
  );
}

import { useEffect, useState } from 'react';
import { Card, CardContent, CardHeader, CardTitle } from '@/components/ui/card';
import { Button } from '@/components/ui/button';
import { Badge } from '@/components/ui/badge';
import { Loader2 } from 'lucide-react';

interface NotificationItem {
  id: number;
  type: string;
  title?: string;
  message?: string;
  read: boolean;
  created_at: string;
}

export default function AdminNotifications() {
  const [items, setItems] = useState<NotificationItem[]>([]);
  const [loading, setLoading] = useState(true);
  const API_URL = import.meta.env.VITE_API_URL;

  const fetchNotifications = async () => {
    try {
      setLoading(true);
      const token = localStorage.getItem('auth_token') || sessionStorage.getItem('auth_token');
      const res = await fetch(`${API_URL}/api/me/notifications?unread=false`, {
        headers: token ? { Authorization: `Bearer ${token}` } : undefined,
      });
      if (!res.ok) throw new Error('Failed');
      const data = await res.json();
      const list = Array.isArray(data?.data) ? data.data : (Array.isArray(data) ? data : []);
      setItems(list as NotificationItem[]);
    } finally {
      setLoading(false);
    }
  };

  const markAllRead = async () => {
    const token = localStorage.getItem('auth_token') || sessionStorage.getItem('auth_token');
    await fetch(`${API_URL}/api/notifications/mark-all-read`, {
      method: 'POST',
      headers: token ? { Authorization: `Bearer ${token}` } : undefined,
    });
    fetchNotifications();
  };

  useEffect(() => {
    fetchNotifications();
  }, []);

  const unreadCount = items.filter(i => !i.read).length;

  return (
    <div className="space-y-6">
      <div className="flex items-center justify-between">
        <div>
          <h1 className="text-3xl font-bold">Notifications</h1>
          <p className="text-muted-foreground">Review recent system updates and alerts</p>
        </div>
        <div className="flex items-center gap-3">
          <Badge variant="secondary">Unread: {unreadCount}</Badge>
          <Button variant="outline" onClick={markAllRead} disabled={unreadCount === 0}>Mark all read</Button>
          <Button variant="outline" onClick={fetchNotifications}>Refresh</Button>
        </div>
      </div>

      <Card>
        <CardHeader>
          <CardTitle>All Notifications ({items.length})</CardTitle>
        </CardHeader>
        <CardContent>
          {loading ? (
            <div className="flex items-center justify-center py-16 text-muted-foreground">
              <Loader2 className="h-5 w-5 mr-2 animate-spin" /> Loading...
            </div>
          ) : items.length === 0 ? (
            <div className="text-center py-16 text-muted-foreground">No notifications</div>
          ) : (
            <div className="space-y-3">
              {items.map(n => (
                <div key={n.id} className={`border rounded-md p-4 ${n.read ? '' : 'bg-muted/40'}`}>
                  <div className="flex items-center justify-between">
                    <div className="font-medium">
                      {n.title || n.type}
                    </div>
                    {!n.read && <Badge className="bg-blue-100 text-blue-800">New</Badge>}
                  </div>
                  {n.message && <div className="mt-1 text-sm text-muted-foreground">{n.message}</div>}
                  <div className="mt-2 text-xs text-muted-foreground">{new Date(n.created_at).toLocaleString()}</div>
                </div>
              ))}
            </div>
          )}
        </CardContent>
      </Card>
    </div>
  );
}

