                    </Label>
                  </div>
                  {errors.confirm_truthfulness && (
                    <p className="text-sm text-destructive ml-6">{errors.confirm_truthfulness}</p>
                  )}

                  <div className="flex items-start space-x-2">
                    <Checkbox
                      id="accept_terms"
                      checked={formData.accept_terms}
                      onCheckedChange={(checked) => handleChange('accept_terms', checked === true)}
                      className={cn(errors.accept_terms && 'border-destructive')}
                    />
                    <Label htmlFor="accept_terms" className="font-normal cursor-pointer">
                      I accept the{' '}
                      <Link to="/terms" className="text-primary hover:underline">
                        Terms of Service
                      </Link>{' '}
                      and{' '}
                      <Link to="/privacy" className="text-primary hover:underline">
                        Privacy Policy
                      </Link>
                      <span className="text-destructive ml-1">*</span>
                    </Label>
                  </div>
                  {errors.accept_terms && (
                    <p className="text-sm text-destructive ml-6">{errors.accept_terms}</p>
                  )}
                </div>

                <div className="p-4 bg-accent/50 border border-accent rounded-lg">
                  <p className="text-sm">
                    <strong>Next steps:</strong> After submission, your application will be
                    reviewed by our team. This typically takes 3-5 business days. We'll notify you
                    by email once your review is complete.
                  </p>
                </div>
              </div>
            </div>
          )}

          {/* Navigation */}
          <div className="flex items-center justify-between pt-6 border-t mt-8">
            <div className="flex gap-2">
              {currentStep > 1 && (
                <Button type="button" variant="outline" onClick={prevStep}>
                  <ArrowLeft className="mr-2 h-4 w-4" />
                  Back
                </Button>
              )}
              <Button
                type="button"
                variant="ghost"
                onClick={handleSaveDraft}
                disabled={isSavingDraft}
              >
                {isSavingDraft ? (
                  <>
                    <Loader2 className="mr-2 h-4 w-4 animate-spin" />
                    Saving...
                  </>
                ) : (
                  <>
                    <Save className="mr-2 h-4 w-4" />
                    Save draft
                  </>
                )}
              </Button>
            </div>

            {currentStep < STEPS.length ? (
              <Button type="button" onClick={nextStep}>
                Continue
                <ArrowRight className="ml-2 h-4 w-4" />
              </Button>
            ) : (
              <Button type="button" onClick={handleSubmit} disabled={isLoading}>
                {isLoading ? (
                  <>
                    <Loader2 className="mr-2 h-4 w-4 animate-spin" />
                    Submitting...
                  </>
                ) : (
                  'Submit application'
                )}
              </Button>
            )}
          </div>
        </div>

        {/* Sign In Link */}
        <p className="text-center mt-6 text-sm text-muted-foreground">
          Already have an account?{' '}
          <Link to="/auth/login" className="text-primary font-medium hover:underline">
            Sign in
          </Link>
        </p>
      </div>
    </div>
  );
}

import { useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { ArrowLeft, Eye, EyeOff, Loader2 } from 'lucide-react';
import { Button } from '@/components/ui/button';
import { Input } from '@/components/ui/input';
import { Label } from '@/components/ui/label';
import { Checkbox } from '@/components/ui/checkbox';
import { RadioGroup, RadioGroupItem } from '@/components/ui/radio-group';
import { PasswordStrengthMeter } from '@/components/auth/PasswordStrengthMeter';
import { authService, type DonorRegistrationData } from '@/services/auth';
import { toast } from 'sonner';
import { cn } from '@/lib/utils';

export default function RegisterDonor() {
  const navigate = useNavigate();
  const [showPassword, setShowPassword] = useState(false);
  const [showConfirmPassword, setShowConfirmPassword] = useState(false);
  const [isLoading, setIsLoading] = useState(false);
  const [errors, setErrors] = useState<Record<string, string>>({});

  const [formData, setFormData] = useState<DonorRegistrationData>({
    full_name: '',
    email: '',
    password: '',
    password_confirmation: '',
    phone: '',
    address: '',
    preferred_contact_method: 'email',
    anonymous_giving: false,
    accept_terms: false,
  });

  const handleChange = (field: string, value: any) => {
    setFormData((prev) => ({ ...prev, [field]: value }));
    if (errors[field]) {
      setErrors((prev) => ({ ...prev, [field]: '' }));
    }
  };

  const validateForm = (): boolean => {
    const newErrors: Record<string, string> = {};

    if (formData.password !== formData.password_confirmation) {
      newErrors.password_confirmation = 'Passwords do not match';
    }

    if (!formData.accept_terms) {
      newErrors.accept_terms = 'You must accept the terms and privacy policy';
    }

    setErrors(newErrors);
    return Object.keys(newErrors).length === 0;
  };

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();

    if (isLoading) return; // guard against double submit

    if (!validateForm()) {
      return;
    }

    setIsLoading(true);

    try {
      await authService.registerDonor(formData);

      toast.success('Registration successful!', {
        description: 'You can now login with your credentials.',
      });

      // Redirect to login page after successful registration
      navigate('/auth/login');
    } catch (error: any) {
      // Parse 422 validation errors from backend if available
      const apiErrors = error?.response?.data?.errors as Record<string, string[]> | undefined;
      if (apiErrors) {
        const flatErrors: Record<string, string> = {};
        Object.entries(apiErrors).forEach(([k, v]) => { flatErrors[k] = v?.[0] ?? 'Invalid'; });
        setErrors(flatErrors);
        toast.error('Registration failed', { description: Object.values(flatErrors)[0] });
      } else if (error instanceof Error) {
        setErrors({ general: error.message });
        toast.error('Registration failed', { description: error.message });
      }
    } finally {
      setIsLoading(false);
    }
  };

  return (
    <div className="min-h-screen flex items-center justify-center bg-gradient-to-br from-background via-background to-muted/20 p-4 py-12">
      <div className="w-full max-w-2xl">
        {/* Header */}
        <div className="mb-8">
          <Link
            to="/auth/register"
            className="inline-flex items-center gap-2 text-sm text-muted-foreground hover:text-foreground mb-4"
          >
            <ArrowLeft className="h-4 w-4" />
            Back to registration options
          </Link>
          <h1 className="text-3xl font-bold mb-2">Create donor account</h1>
          <p className="text-muted-foreground">
            Join our community and start making a difference
          </p>
        </div>

        {/* Form Card */}
        <div className="auth-card">
          <form onSubmit={handleSubmit} className="space-y-6">
            {errors.general && (
              <div className="p-3 text-sm bg-destructive/10 text-destructive border border-destructive/20 rounded-lg">
                {errors.general}
              </div>
            )}

            {/* Basic Info */}
            <div className="space-y-4">
              <h3 className="text-lg font-semibold">Basic Information</h3>

              <div className="space-y-2">
                <Label htmlFor="full_name">
                  Full name <span className="text-destructive">*</span>
                </Label>
                <Input
                  id="full_name"
                  value={formData.full_name}
                  onChange={(e) => handleChange('full_name', e.target.value)}
                  required
                  placeholder="John Doe"
                />
              </div>

              <div className="space-y-2">
                <Label htmlFor="email">
                  Email address <span className="text-destructive">*</span>
                </Label>
                <Input
                  id="email"
                  type="email"
                  value={formData.email}
                  onChange={(e) => handleChange('email', e.target.value)}
                  required
                  placeholder="you@example.com"
                />
              </div>

              <div className="space-y-2">
                <Label htmlFor="phone">Phone (optional)</Label>
                <Input
                  id="phone"
                  type="tel"
                  value={formData.phone}
                  onChange={(e) => handleChange('phone', e.target.value)}
                  placeholder="+1 (555) 123-4567"
                />
              </div>

              <div className="space-y-2">
                <Label htmlFor="address">Address (optional)</Label>
                <Input
                  id="address"
                  value={formData.address}
                  onChange={(e) => handleChange('address', e.target.value)}
                  placeholder="123 Main St, City, State ZIP"
                />
              </div>
            </div>

            {/* Password */}
            <div className="space-y-4">
              <h3 className="text-lg font-semibold">Security</h3>

              <div className="space-y-2">
                <Label htmlFor="password">
                  Password <span className="text-destructive">*</span>
                </Label>
                <div className="relative">
                  <Input
                    id="password"
                    type={showPassword ? 'text' : 'password'}
                    value={formData.password}
                    onChange={(e) => handleChange('password', e.target.value)}
                    required
                    className="pr-10"
                  />
                  <button
                    type="button"
                    onClick={() => setShowPassword(!showPassword)}
                    className="absolute right-3 top-1/2 -translate-y-1/2 text-muted-foreground hover:text-foreground"
                    tabIndex={-1}
                  >
                    {showPassword ? <EyeOff className="h-4 w-4" /> : <Eye className="h-4 w-4" />}
                  </button>
                </div>
              </div>

              <PasswordStrengthMeter password={formData.password} />

              <div className="space-y-2">
                <Label htmlFor="password_confirmation">
                  Confirm password <span className="text-destructive">*</span>
                </Label>
                <div className="relative">
                  <Input
                    id="password_confirmation"
                    type={showConfirmPassword ? 'text' : 'password'}
                    value={formData.password_confirmation}
                    onChange={(e) => handleChange('password_confirmation', e.target.value)}
                    required
                    className={cn(errors.password_confirmation && 'border-destructive', 'pr-10')}
                  />
                  <button
                    type="button"
                    onClick={() => setShowConfirmPassword(!showConfirmPassword)}
                    className="absolute right-3 top-1/2 -translate-y-1/2 text-muted-foreground hover:text-foreground"
                    tabIndex={-1}
                  >
                    {showConfirmPassword ? <EyeOff className="h-4 w-4" /> : <Eye className="h-4 w-4" />}
                  </button>
                </div>
                {errors.password_confirmation && (
                  <p className="text-sm text-destructive">{errors.password_confirmation}</p>
                )}
              </div>
            </div>

            {/* Preferences */}
            <div className="space-y-4">
              <h3 className="text-lg font-semibold">Preferences</h3>

              <div className="space-y-3">
                <Label>Preferred contact method</Label>
                <RadioGroup
                  value={formData.preferred_contact_method}
                  onValueChange={(value) =>
                    handleChange('preferred_contact_method', value as 'email' | 'sms')
                  }
                >
                  <div className="flex items-center space-x-2">
                    <RadioGroupItem value="email" id="email_contact" />
                    <Label htmlFor="email_contact" className="font-normal cursor-pointer">
                      Email
                    </Label>
                  </div>
                  <div className="flex items-center space-x-2">
                    <RadioGroupItem value="sms" id="sms_contact" />
                    <Label htmlFor="sms_contact" className="font-normal cursor-pointer">
                      SMS
                    </Label>
                  </div>
                </RadioGroup>
              </div>

              <div className="flex items-center space-x-2">
                <Checkbox
                  id="anonymous_giving"
                  checked={formData.anonymous_giving}
                  onCheckedChange={(checked) => handleChange('anonymous_giving', checked === true)}
                />
                <Label htmlFor="anonymous_giving" className="font-normal cursor-pointer">
                  Enable anonymous giving by default
                </Label>
              </div>
            </div>

            {/* Terms */}
            <div className="space-y-4">
              <div className="flex items-start space-x-2">
                <Checkbox
                  id="accept_terms"
                  checked={formData.accept_terms}
                  onCheckedChange={(checked) => handleChange('accept_terms', checked === true)}
                  className={cn(errors.accept_terms && 'border-destructive')}
                />
                <div className="space-y-1">
                  <Label htmlFor="accept_terms" className="font-normal cursor-pointer">
                    I agree to the{' '}
                    <Link to="/terms" className="text-primary hover:underline">
                      Terms of Service
                    </Link>{' '}
                    and{' '}
                    <Link to="/privacy" className="text-primary hover:underline">
                      Privacy Policy
                    </Link>
                    <span className="text-destructive ml-1">*</span>
                  </Label>
                  {errors.accept_terms && (
                    <p className="text-sm text-destructive">{errors.accept_terms}</p>
                  )}
                </div>
              </div>
            </div>

            {/* Submit */}
            <Button type="submit" className="w-full" size="lg" disabled={isLoading}>
              {isLoading ? (
                <>
                  <Loader2 className="mr-2 h-4 w-4 animate-spin" />
                  Creating account...
                </>
              ) : (
                'Create account'
              )}
            </Button>
          </form>
        </div>

        {/* Sign In Link */}
        <p className="text-center mt-6 text-sm text-muted-foreground">
          Already have an account?{' '}
          <Link to="/auth/login" className="text-primary font-medium hover:underline">
            Sign in
          </Link>
        </p>
      </div>
    </div>
  );
}

import { useEffect, useState } from "react";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Badge } from "@/components/ui/badge";
import {
  Select,
  SelectContent,
  SelectItem,
  SelectTrigger,
  SelectValue,
} from "@/components/ui/select";
import {
  Table,
  TableBody,
  TableCell,
  TableHead,
  TableHeader,
  TableRow,
} from "@/components/ui/table";
import {
  Dialog,
  DialogContent,
  DialogDescription,
  DialogFooter,
  DialogHeader,
  DialogTitle,
} from "@/components/ui/dialog";
import { Skeleton } from "@/components/ui/skeleton";
import { Collapsible, CollapsibleContent, CollapsibleTrigger } from "@/components/ui/collapsible";
import { listAuditLogs } from "@/services/apiCharity";
import type { AuditLogEntry } from "@/types/charity";
import { ChevronDown, ChevronRight, Search, Eye } from "lucide-react";
import { toast } from "@/hooks/use-toast";

/**
 * Audit Logs Page
 * Paginated list of audit events with filters and expandable details
 */
const AuditLogsPage = () => {
  const [logs, setLogs] = useState<AuditLogEntry[]>([]);
  const [loading, setLoading] = useState(true);
  const [page, setPage] = useState(1);
  const [totalPages, setTotalPages] = useState(1);
  const [filters, setFilters] = useState({
    userId: "",
    action: "",
    entityType: "all",
    startDate: "",
    endDate: "",
  });
  const [expandedIds, setExpandedIds] = useState<Set<string>>(new Set());
  const [detailDialogOpen, setDetailDialogOpen] = useState(false);
  const [selectedLog, setSelectedLog] = useState<AuditLogEntry | null>(null);

  useEffect(() => {
    loadLogs();
  }, [page]);

  const loadLogs = async () => {
    try {
      setLoading(true);
      const cleanFilters = {
        ...filters,
        entityType: filters.entityType === "all" ? undefined : filters.entityType,
      };
      const response = await listAuditLogs({
        page,
        pageSize: 50,
        ...cleanFilters,
        sortBy: "timestamp",
        sortOrder: "desc",
      });
      setLogs(response.data);
      setTotalPages(response.pagination.totalPages);
    } catch (err) {
      toast({
        title: "Error",
        description: err instanceof Error ? err.message : "Failed to load logs",
        variant: "destructive",
      });
    } finally {
      setLoading(false);
    }
  };

  const handleFilter = () => {
    setPage(1);
    loadLogs();
  };

  const toggleExpand = (id: string) => {
    setExpandedIds((prev) => {
      const next = new Set(prev);
      if (next.has(id)) {
        next.delete(id);
      } else {
        next.add(id);
      }
      return next;
    });
  };

  const getEntityBadge = (entityType: string) => {
    const variants: Record<string, string> = {
      donation: "bg-success/10 text-success",
      campaign: "bg-primary/10 text-primary",
      document: "bg-accent/10 text-accent-foreground",
      user: "bg-warning/10 text-warning",
    };
    return (
      <Badge variant="outline" className={variants[entityType] || ""}>
        {entityType}
      </Badge>
    );
  };

  if (loading && logs.length === 0) {
    return (
      <div className="p-lg">
        <Card>
          <CardHeader>
            <Skeleton className="h-8 w-48" />
          </CardHeader>
          <CardContent>
            <Skeleton className="h-64 w-full" />
          </CardContent>
        </Card>
      </div>
    );
  }

  return (
    <div className="p-lg space-y-lg">
      <Card>
        <CardHeader>
          <CardTitle>Audit Logs</CardTitle>
        </CardHeader>
        <CardContent className="space-y-md">
          {/* Filters */}
          <div className="grid grid-cols-2 md:grid-cols-3 gap-md">
            <div>
              <Label htmlFor="filter-user">User ID</Label>
              <Input
                id="filter-user"
                value={filters.userId}
                onChange={(e) =>
                  setFilters({ ...filters, userId: e.target.value })
                }
                placeholder="Filter by user..."
                className="mt-sm"
              />
            </div>
            <div>
              <Label htmlFor="filter-action">Action</Label>
              <Input
                id="filter-action"
                value={filters.action}
                onChange={(e) =>
                  setFilters({ ...filters, action: e.target.value })
                }
                placeholder="e.g., create, update..."
                className="mt-sm"
              />
            </div>
            <div>
              <Label htmlFor="filter-entity">Entity Type</Label>
              <Select
                value={filters.entityType}
                onValueChange={(value) =>
                  setFilters({ ...filters, entityType: value })
                }
              >
                <SelectTrigger id="filter-entity" className="mt-sm">
                  <SelectValue placeholder="All types" />
                </SelectTrigger>
                <SelectContent>
                  <SelectItem value="all">All types</SelectItem>
                  <SelectItem value="donation">Donation</SelectItem>
                  <SelectItem value="campaign">Campaign</SelectItem>
                  <SelectItem value="document">Document</SelectItem>
                  <SelectItem value="user">User</SelectItem>
                </SelectContent>
              </Select>
            </div>
            <div>
              <Label htmlFor="filter-start">Start Date</Label>
              <Input
                id="filter-start"
                type="date"
                value={filters.startDate}
                onChange={(e) =>
                  setFilters({ ...filters, startDate: e.target.value })
                }
                className="mt-sm"
              />
            </div>
            <div>
              <Label htmlFor="filter-end">End Date</Label>
              <Input
                id="filter-end"
                type="date"
                value={filters.endDate}
                onChange={(e) =>
                  setFilters({ ...filters, endDate: e.target.value })
                }
                className="mt-sm"
              />
            </div>
            <div className="flex items-end">
              <Button onClick={handleFilter} className="w-full">
                <Search className="h-4 w-4 mr-xs" />
                Filter
              </Button>
            </div>
          </div>

          {/* Table */}
          <div className="border rounded-md">
            <Table>
              <TableHeader>
                <TableRow>
                  <TableHead className="w-8"></TableHead>
                  <TableHead>Timestamp</TableHead>
                  <TableHead>User</TableHead>
                  <TableHead>Action</TableHead>
                  <TableHead>Entity</TableHead>
                  <TableHead>Entity ID</TableHead>
                  <TableHead className="text-right">Details</TableHead>
                </TableRow>
              </TableHeader>
              <TableBody>
                {logs.length === 0 ? (
                  <TableRow>
                    <TableCell colSpan={7} className="text-center py-lg">
                      <p className="text-muted-foreground">No audit logs found</p>
                    </TableCell>
                  </TableRow>
                ) : (
                  logs.map((log) => (
                    <>
                      <TableRow key={log.id} className="cursor-pointer hover:bg-muted/30">
                        <TableCell>
                          <Button
                            variant="ghost"
                            size="sm"
                            onClick={() => toggleExpand(log.id)}
                          >
                            {expandedIds.has(log.id) ? (
                              <ChevronDown className="h-4 w-4" />
                            ) : (
                              <ChevronRight className="h-4 w-4" />
                            )}
                          </Button>
                        </TableCell>
                        <TableCell className="text-sm">
                          {new Date(log.timestamp).toLocaleString()}
                        </TableCell>
                        <TableCell className="font-medium">{log.userName}</TableCell>
                        <TableCell className="capitalize">{log.action}</TableCell>
                        <TableCell>{getEntityBadge(log.entityType)}</TableCell>
                        <TableCell className="font-mono text-xs">
                          {log.entityId.slice(0, 8)}...
                        </TableCell>
                        <TableCell>
                          <Button
                            variant="ghost"
                            size="sm"
                            onClick={() => {
                              setSelectedLog(log);
                              setDetailDialogOpen(true);
                            }}
                          >
                            <Eye className="h-4 w-4" />
                          </Button>
                        </TableCell>
                      </TableRow>
                      {expandedIds.has(log.id) && (
                        <TableRow>
                          <TableCell colSpan={7} className="bg-muted/20">
                            <div className="py-sm px-md space-y-xs text-sm">
                              {log.ipAddress && (
                                <p>
                                  <span className="text-muted-foreground">IP:</span>{" "}
                                  {log.ipAddress}
                                </p>
                              )}
                              <p>
                                <span className="text-muted-foreground">Details:</span>
                              </p>
                              <pre className="text-xs bg-muted p-sm rounded overflow-auto">
                                {JSON.stringify(log.details, null, 2)}
                              </pre>
                            </div>
                          </TableCell>
                        </TableRow>
                      )}
                    </>
                  ))
                )}
              </TableBody>
            </Table>
          </div>

          {/* Pagination */}
          <div className="flex items-center justify-between">
            <p className="text-sm text-muted-foreground">
              Page {page} of {totalPages}
            </p>
            <div className="flex gap-sm">
              <Button
                variant="outline"
                size="sm"
                disabled={page === 1}
                onClick={() => setPage(page - 1)}
              >
                Previous
              </Button>
              <Button
                variant="outline"
                size="sm"
                disabled={page === totalPages}
                onClick={() => setPage(page + 1)}
              >
                Next
              </Button>
            </div>
          </div>
        </CardContent>
      </Card>

      {/* Detail Dialog */}
      {selectedLog && (
        <Dialog open={detailDialogOpen} onOpenChange={setDetailDialogOpen}>
          <DialogContent className="max-w-3xl">
            <DialogHeader>
              <DialogTitle>Audit Log Details</DialogTitle>
              <DialogDescription>
                {new Date(selectedLog.timestamp).toLocaleString()}
              </DialogDescription>
            </DialogHeader>
            <div className="space-y-md py-md">
              <div className="grid grid-cols-2 gap-md text-sm">
                <div>
                  <p className="text-muted-foreground">User:</p>
                  <p className="font-medium">{selectedLog.userName}</p>
                </div>
                <div>
                  <p className="text-muted-foreground">Action:</p>
                  <p className="font-medium capitalize">{selectedLog.action}</p>
                </div>
                <div>
                  <p className="text-muted-foreground">Entity:</p>
                  {getEntityBadge(selectedLog.entityType)}
                </div>
                <div>
                  <p className="text-muted-foreground">Entity ID:</p>
                  <code className="text-xs font-mono">{selectedLog.entityId}</code>
                </div>
                {selectedLog.ipAddress && (
                  <div>
                    <p className="text-muted-foreground">IP Address:</p>
                    <p className="font-medium">{selectedLog.ipAddress}</p>
                  </div>
                )}
              </div>
              <div>
                <p className="text-sm text-muted-foreground mb-sm">Event Details:</p>
                <pre className="text-xs bg-muted p-md rounded overflow-auto max-h-96">
                  {JSON.stringify(selectedLog.details, null, 2)}
                </pre>
              </div>
            </div>
            <DialogFooter>
              <Button variant="outline" onClick={() => setDetailDialogOpen(false)}>
                Close
              </Button>
            </DialogFooter>
          </DialogContent>
        </Dialog>
      )}
    </div>
  );
};

export default AuditLogsPage;

import { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Skeleton } from "@/components/ui/skeleton";
import { Badge } from "@/components/ui/badge";
import { Progress } from "@/components/ui/progress";
import {
  AlertDialog,
  AlertDialogAction,
  AlertDialogCancel,
  AlertDialogContent,
  AlertDialogDescription,
  AlertDialogFooter,
  AlertDialogHeader,
  AlertDialogTitle,
} from "@/components/ui/alert-dialog";
import {
  getCampaign,
  pauseCampaign,
  resumeCampaign,
  closeCampaign,
} from "@/services/apiCharity";
import type { CampaignDetail } from "@/types/charity";
import { Edit, Pause, Play, StopCircle, Download, AlertCircle } from "lucide-react";
import { PieChart, Pie, Cell, ResponsiveContainer, Legend, Tooltip } from "recharts";
import { toast } from "@/hooks/use-toast";

/**
 * Campaign Detail Page
 * Shows campaign info, progress, media gallery, donor breakdown, and actions
 */
const CampaignDetailPage = () => {
  const { id } = useParams<{ id: string }>();
  const [campaign, setCampaign] = useState<CampaignDetail | null>(null);
  const [loading, setLoading] = useState(true);
  const [actionDialogOpen, setActionDialogOpen] = useState(false);
  const [actionType, setActionType] = useState<"pause" | "resume" | "close" | null>(null);

  useEffect(() => {
    if (id) {
      loadCampaign();
    }
  }, [id]);

  const loadCampaign = async () => {
    if (!id) return;
    try {
      setLoading(true);
      const data = await getCampaign(id);
      setCampaign(data);
    } catch (err) {
      toast({
        title: "Error",
        description: err instanceof Error ? err.message : "Failed to load campaign",
        variant: "destructive",
      });
    } finally {
      setLoading(false);
    }
  };

  const handleAction = async () => {
    if (!id || !actionType) return;
    try {
      if (actionType === "pause") {
        await pauseCampaign(id);
        toast({ title: "Success", description: "Campaign paused" });
      } else if (actionType === "resume") {
        await resumeCampaign(id);
        toast({ title: "Success", description: "Campaign resumed" });
      } else if (actionType === "close") {
        await closeCampaign(id);
        toast({ title: "Success", description: "Campaign closed" });
      }
      loadCampaign();
      setActionDialogOpen(false);
      setActionType(null);
    } catch (err) {
      toast({
        title: "Error",
        description: err instanceof Error ? err.message : "Action failed",
        variant: "destructive",
      });
    }
  };

  if (loading) {
    return (
      <div className="p-lg space-y-lg">
        <Card>
          <CardHeader>
            <Skeleton className="h-8 w-64" />
          </CardHeader>
          <CardContent>
            <Skeleton className="h-48 w-full" />
          </CardContent>
        </Card>
      </div>
    );
  }

  if (!campaign) {
    return (
      <div className="p-lg">
        <Card>
          <CardContent className="flex flex-col items-center justify-center py-2xl">
            <AlertCircle className="h-12 w-12 text-destructive mb-md" />
            <p className="text-lg font-medium">Campaign not found</p>
          </CardContent>
        </Card>
      </div>
    );
  }

  const progress = (campaign.raised / campaign.goal) * 100;
  const COLORS = ["hsl(var(--primary))", "hsl(var(--accent))", "hsl(var(--success))"];

  return (
    <div className="p-lg space-y-lg">
      {/* Header */}
      <Card>
        <CardHeader>
          <div className="flex items-start justify-between">
            <div>
              <CardTitle className="text-3xl mb-sm">{campaign.title}</CardTitle>
              <Badge
                variant="outline"
                className={
                  campaign.status === "active"
                    ? "bg-success/10 text-success"
                    : campaign.status === "paused"
                    ? "bg-warning/10 text-warning"
                    : "bg-muted text-muted-foreground"
                }
              >
                {campaign.status}
              </Badge>
            </div>
            <div className="flex gap-sm">
              <Button variant="outline" size="sm">
                <Edit className="h-4 w-4 mr-xs" />
                Edit
              </Button>
              {campaign.status === "active" && (
                <Button
                  variant="outline"
                  size="sm"
                  onClick={() => {
                    setActionType("pause");
                    setActionDialogOpen(true);
                  }}
                >
                  <Pause className="h-4 w-4 mr-xs" />
                  Pause
                </Button>
              )}
              {campaign.status === "paused" && (
                <Button
                  variant="outline"
                  size="sm"
                  onClick={() => {
                    setActionType("resume");
                    setActionDialogOpen(true);
                  }}
                >
                  <Play className="h-4 w-4 mr-xs" />
                  Resume
                </Button>
              )}
              <Button
                variant="outline"
                size="sm"
                onClick={() => {
                  setActionType("close");
                  setActionDialogOpen(true);
                }}
              >
                <StopCircle className="h-4 w-4 mr-xs" />
                Close
              </Button>
            </div>
          </div>
        </CardHeader>
        <CardContent className="space-y-md">
          <div className="space-y-sm">
            <div className="flex items-center justify-between text-sm">
              <span className="text-muted-foreground">Progress</span>
              <span className="font-medium">
                ${campaign.raised.toLocaleString()} / ${campaign.goal.toLocaleString()}
              </span>
            </div>
            <Progress value={progress} className="h-3" />
            <p className="text-xs text-muted-foreground text-right">
              {progress.toFixed(1)}% of goal reached
            </p>
          </div>
        </CardContent>
      </Card>

      <div className="grid gap-lg lg:grid-cols-2">
        {/* Description & Media */}
        <Card>
          <CardHeader>
            <CardTitle>Campaign Details</CardTitle>
          </CardHeader>
          <CardContent className="space-y-md">
            {campaign.mediaGallery && campaign.mediaGallery.length > 0 && (
              <div className="grid grid-cols-2 gap-sm">
                {campaign.mediaGallery.slice(0, 4).map((url, idx) => (
                  <img
                    key={idx}
                    src={url}
                    alt={`Campaign media ${idx + 1}`}
                    className="w-full h-32 object-cover rounded-md border border-border"
                  />
                ))}
              </div>
            )}
            <div>
              <h3 className="font-medium mb-sm">Description</h3>
              <p className="text-sm text-muted-foreground">{campaign.description}</p>
            </div>
            <div className="grid grid-cols-2 gap-md pt-md border-t border-border">
              <div>
                <p className="text-xs text-muted-foreground">Deadline</p>
                <p className="text-sm font-medium">
                  {new Date(campaign.deadline).toLocaleDateString()}
                </p>
              </div>
              <div>
                <p className="text-xs text-muted-foreground">Created</p>
                <p className="text-sm font-medium">
                  {new Date(campaign.createdAt).toLocaleDateString()}
                </p>
              </div>
            </div>
          </CardContent>
        </Card>

        {/* Donor Breakdown */}
        <Card>
          <CardHeader>
            <CardTitle>Donor Breakdown</CardTitle>
          </CardHeader>
          <CardContent>
            {campaign.donorBreakdown && campaign.donorBreakdown.length > 0 ? (
              <ResponsiveContainer width="100%" height={250}>
                <PieChart>
                  <Pie
                    data={campaign.donorBreakdown}
                    cx="50%"
                    cy="50%"
                    labelLine={false}
                    label={(entry) => `${entry.range}: ${entry.count}`}
                    outerRadius={80}
                    fill="hsl(var(--primary))"
                    dataKey="totalAmount"
                  >
                    {campaign.donorBreakdown.map((_, index) => (
                      <Cell key={`cell-${index}`} fill={COLORS[index % COLORS.length]} />
                    ))}
                  </Pie>
                  <Tooltip />
                  <Legend />
                </PieChart>
              </ResponsiveContainer>
            ) : (
              <p className="text-sm text-muted-foreground text-center py-lg">
                No donor data available
              </p>
            )}
          </CardContent>
        </Card>
      </div>

      {/* Recent Donations */}
      <Card>
        <CardHeader>
          <div className="flex items-center justify-between">
            <CardTitle>Recent Donations</CardTitle>
            <Button variant="outline" size="sm">
              <Download className="h-4 w-4 mr-xs" />
              Export
            </Button>
          </div>
        </CardHeader>
        <CardContent>
          <div className="space-y-md">
            {campaign.recentDonations && campaign.recentDonations.length > 0 ? (
              campaign.recentDonations.map((donation) => (
                <div
                  key={donation.id}
                  className="flex items-center justify-between border-b border-border pb-md last:border-0 last:pb-0"
                >
                  <div>
                    <p className="font-medium">{donation.donorName}</p>
                    <p className="text-xs text-muted-foreground">
                      {new Date(donation.submittedAt).toLocaleString()}
                    </p>
                  </div>
                  <div className="text-right">
                    <p className="font-medium">${donation.amount.toLocaleString()}</p>
                    <Badge
                      variant="outline"
                      className={
                        donation.status === "confirmed"
                          ? "bg-success/10 text-success"
                          : donation.status === "pending"
                          ? "bg-warning/10 text-warning"
                          : "bg-destructive/10 text-destructive"
                      }
                    >
                      {donation.status}
                    </Badge>
                  </div>
                </div>
              ))
            ) : (
              <p className="text-sm text-muted-foreground text-center py-lg">
                No donations yet
              </p>
            )}
          </div>
        </CardContent>
      </Card>

      {/* Action Confirmation Dialog */}
      <AlertDialog open={actionDialogOpen} onOpenChange={setActionDialogOpen}>
        <AlertDialogContent>
          <AlertDialogHeader>
            <AlertDialogTitle>
              {actionType === "pause"
                ? "Pause Campaign"
                : actionType === "resume"
                ? "Resume Campaign"
                : "Close Campaign"}
            </AlertDialogTitle>
            <AlertDialogDescription>
              {actionType === "pause"
                ? "This will temporarily stop accepting new donations."
                : actionType === "resume"
                ? "This will resume accepting donations."
                : "This action will permanently close the campaign and stop all donations."}
            </AlertDialogDescription>
          </AlertDialogHeader>
          <AlertDialogFooter>
            <AlertDialogCancel>Cancel</AlertDialogCancel>
            <AlertDialogAction onClick={handleAction}>Confirm</AlertDialogAction>
          </AlertDialogFooter>
        </AlertDialogContent>
      </AlertDialog>
    </div>
  );
};

export default CampaignDetailPage;

import { useState, useEffect } from "react";
import { Plus, Edit, Trash2, Eye, Calendar, Target } from "lucide-react";
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Textarea } from "@/components/ui/textarea";
import { Badge } from "@/components/ui/badge";
import { Dialog, DialogContent, DialogDescription, DialogFooter, DialogHeader, DialogTitle } from "@/components/ui/dialog";
import { Table, TableBody, TableCell, TableHead, TableHeader, TableRow } from "@/components/ui/table";
import { Progress } from "@/components/ui/progress";
import { toast } from "sonner";
import { useAuth } from "@/context/AuthContext";
import { campaignService, Campaign as ApiCampaign } from "@/services/campaigns";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";

interface Campaign {
  id: number;
  title: string;
  description: string;
  target_amount: number;
  current_amount: number;
  start_date: string;
  end_date: string;
  status: 'draft' | 'published' | 'closed' | 'archived';
  donation_type: 'one_time' | 'recurring';
  cover_image_path?: string;
  charity_id: number;
}

export default function CampaignManagement() {
  const { user } = useAuth();
  const [campaigns, setCampaigns] = useState<Campaign[]>([]);
  const [loading, setLoading] = useState(true);
  const [submitting, setSubmitting] = useState(false);

  const [isCreateDialogOpen, setIsCreateDialogOpen] = useState(false);
  const [isEditDialogOpen, setIsEditDialogOpen] = useState(false);
  const [isViewDialogOpen, setIsViewDialogOpen] = useState(false);
  const [selectedCampaign, setSelectedCampaign] = useState<Campaign | null>(null);

  const [formData, setFormData] = useState({
    title: "",
    description: "",
    targetAmount: "",
    startDate: "",
    endDate: "",
    donationType: "one_time" as "one_time" | "recurring",
    status: "draft" as "draft" | "published" | "closed" | "archived",
    image: null as File | null
  });

  useEffect(() => {
    loadCampaigns();
  }, []);

  const loadCampaigns = async () => {
    try {
      if (!user?.charity?.id) {
        toast.error("No charity found for your account");
        setLoading(false);
        return;
      }

      const response = await campaignService.getCampaigns(user.charity.id);
      const formattedCampaigns = response.data.map((campaign: ApiCampaign) => ({
        id: campaign.id,
        title: campaign.title,
        description: campaign.description || "",
        target_amount: campaign.target_amount || 0,
        current_amount: campaign.current_amount || 0,
        start_date: campaign.start_date || "",
        end_date: campaign.end_date || "",
        status: campaign.status,
        donation_type: campaign.donation_type,
        cover_image_path: campaign.cover_image_path,
        charity_id: campaign.charity_id
      }));
      setCampaigns(formattedCampaigns);
    } catch (error: any) {
      console.error("Failed to load campaigns:", error);
      toast.error(error.response?.data?.message || "Failed to load campaigns");
    } finally {
      setLoading(false);
    }
  };

  const handleCreate = async () => {
    try {
      if (!user?.charity?.id) {
        toast.error("No charity found for your account");
        return;
      }

      if (!formData.title || !formData.targetAmount) {
        toast.error("Please fill in all required fields");
        return;
      }

      setSubmitting(true);

      const data = {
        title: formData.title,
        description: formData.description,
        target_amount: parseFloat(formData.targetAmount),
        start_date: formData.startDate,
        end_date: formData.endDate,
        donation_type: formData.donationType,
        status: formData.status,
        cover_image: formData.image || undefined
      };

      await campaignService.createCampaign(user.charity.id, data);
      toast.success("Campaign created successfully");
      setIsCreateDialogOpen(false);
      resetForm();
      loadCampaigns();
    } catch (error: any) {
      console.error("Failed to create campaign:", error);
      toast.error(error.response?.data?.message || "Failed to create campaign");
    } finally {
      setSubmitting(false);
    }
  };

  const handleEdit = (campaign: Campaign) => {
    setSelectedCampaign(campaign);
    setFormData({
      title: campaign.title,
      description: campaign.description,
      targetAmount: campaign.target_amount.toString(),
      startDate: campaign.start_date,
      endDate: campaign.end_date,
      donationType: campaign.donation_type,
      status: campaign.status,
      image: null
    });
    setIsEditDialogOpen(true);
  };

  const handleUpdate = async () => {
    try {
      if (!selectedCampaign) return;

      if (!formData.title || !formData.targetAmount) {
        toast.error("Please fill in all required fields");
        return;
      }

      setSubmitting(true);

      const data = {
        title: formData.title,
        description: formData.description,
        target_amount: parseFloat(formData.targetAmount),
        start_date: formData.startDate,
        end_date: formData.endDate,
        donation_type: formData.donationType,
        status: formData.status,
        cover_image: formData.image || undefined
      };

      await campaignService.updateCampaign(selectedCampaign.id, data);
      toast.success("Campaign updated successfully");
      setIsEditDialogOpen(false);
      resetForm();
      loadCampaigns();
    } catch (error: any) {
      console.error("Failed to update campaign:", error);
      toast.error(error.response?.data?.message || "Failed to update campaign");
    } finally {
      setSubmitting(false);
    }
  };

  const handleDelete = async (id: number) => {
    if (!confirm("Are you sure you want to delete this campaign?")) return;

    try {
      await campaignService.deleteCampaign(id);
      toast.success("Campaign deleted successfully");
      loadCampaigns();
    } catch (error: any) {
      console.error("Failed to delete campaign:", error);
      toast.error(error.response?.data?.message || "Failed to delete campaign");
    }
  };

  const handleView = (campaign: Campaign) => {
    setSelectedCampaign(campaign);
    setIsViewDialogOpen(true);
  };

  const resetForm = () => {
    setFormData({
      title: "",
      description: "",
      targetAmount: "",
      startDate: "",
      endDate: "",
      donationType: "one_time",
      status: "draft" as "draft" | "published" | "closed" | "archived",
      image: null
    });
    setSelectedCampaign(null);
  };

  const getStatusBadge = (status: string) => {
    switch (status) {
      case 'published':
        return <Badge className="bg-green-600">Published</Badge>;
      case 'closed':
        return <Badge className="bg-blue-600">Closed</Badge>;
      case 'draft':
        return <Badge variant="secondary">Draft</Badge>;
      case 'archived':
        return <Badge variant="outline">Archived</Badge>;
      default:
        return <Badge>{status}</Badge>;
    }
  };

  const getDonationTypeBadge = (type: string) => {
    return type === 'recurring' 
      ? <Badge variant="outline" className="bg-purple-50 text-purple-700 border-purple-200">Recurring</Badge>
      : <Badge variant="outline" className="bg-blue-50 text-blue-700 border-blue-200">One-Time</Badge>;
  };

  const getProgress = (current: number, target: number) => {
    return (current / target) * 100;
  };

  return (
    <div className="max-w-7xl mx-auto px-4 py-6 space-y-6">
      {/* Header */}
      <div className="flex items-center justify-between">
        <div>
          <h1 className="text-2xl font-bold">Campaign Management</h1>
          <p className="text-muted-foreground text-sm">
            Create and manage your fundraising campaigns
          </p>
        </div>
        <Button onClick={() => setIsCreateDialogOpen(true)}>
          <Plus className="mr-2 h-4 w-4" />
          Create Campaign
        </Button>
      </div>

      {/* Stats */}
      <div className="grid gap-4 md:grid-cols-3">
        <Card>
          <CardHeader className="pb-2">
            <CardTitle className="text-sm font-medium">Total Campaigns</CardTitle>
          </CardHeader>
          <CardContent>
            <div className="text-2xl font-bold">{campaigns.length}</div>
          </CardContent>
        </Card>
        <Card>
          <CardHeader className="pb-2">
            <CardTitle className="text-sm font-medium">Published Campaigns</CardTitle>
          </CardHeader>
          <CardContent>
            <div className="text-2xl font-bold">
              {campaigns.filter(c => c.status === 'published').length}
            </div>
          </CardContent>
        </Card>
        <Card>
          <CardHeader className="pb-2">
            <CardTitle className="text-sm font-medium">Total Raised</CardTitle>
          </CardHeader>
          <CardContent>
            <div className="text-2xl font-bold">
              {campaigns.reduce((sum, c) => sum + c.current_amount, 0).toLocaleString()}
            </div>
          </CardContent>
        </Card>
      </div>

      {/* Campaigns Table */}
      <Card>
        <CardHeader>
          <CardTitle>All Campaigns</CardTitle>
          <CardDescription>Manage your fundraising campaigns</CardDescription>
        </CardHeader>
        <CardContent>
          {loading ? (
            <div className="py-12 text-center text-muted-foreground">Loading campaigns...</div>
          ) : campaigns.length === 0 ? (
            <div className="py-12 text-center text-muted-foreground">No campaigns yet. Create your first campaign to start fundraising!</div>
          ) : (
            <Table>
            <TableHeader>
              <TableRow>
                <TableHead>Campaign</TableHead>
                <TableHead>Type</TableHead>
                <TableHead>Target</TableHead>
                <TableHead>Progress</TableHead>
                <TableHead>Duration</TableHead>
                <TableHead>Status</TableHead>
                <TableHead className="text-right">Actions</TableHead>
              </TableRow>
            </TableHeader>
            <TableBody>
              {campaigns.map((campaign) => (
                <TableRow key={campaign.id}>
                  <TableCell>
                    <div>
                      <p className="font-medium">{campaign.title}</p>
                      <p className="text-sm text-muted-foreground line-clamp-1">
                        {campaign.description}
                      </p>
                    </div>
                  </TableCell>
                  <TableCell>{getDonationTypeBadge(campaign.donation_type)}</TableCell>
                  <TableCell>{campaign.target_amount.toLocaleString()}</TableCell>
                  <TableCell>
                    <div className="space-y-1">
                      <Progress value={getProgress(campaign.current_amount, campaign.target_amount)} />
                      <p className="text-xs text-muted-foreground">
                        {campaign.current_amount.toLocaleString()} / {campaign.target_amount.toLocaleString()}
                      </p>
                    </div>
                  </TableCell>
                  <TableCell>
                    <div className="text-sm">
                      {campaign.start_date && <p>{new Date(campaign.start_date).toLocaleDateString()}</p>}
                      {campaign.end_date && <p className="text-muted-foreground">to {new Date(campaign.end_date).toLocaleDateString()}</p>}
                    </div>
                  </TableCell>
                  <TableCell>{getStatusBadge(campaign.status)}</TableCell>
                  <TableCell className="text-right">
                    <div className="flex justify-end gap-2">
                      <Button variant="ghost" size="icon" onClick={() => handleView(campaign)}>
                        <Eye className="h-4 w-4" />
                      </Button>
                      <Button variant="ghost" size="icon" onClick={() => handleEdit(campaign)}>
                        <Edit className="h-4 w-4" />
                      </Button>
                      <Button variant="ghost" size="icon" onClick={() => handleDelete(campaign.id)}>
                        <Trash2 className="h-4 w-4 text-destructive" />
                      </Button>
                    </div>
                  </TableCell>
                </TableRow>
              ))}
            </TableBody>
            </Table>
          )}
        </CardContent>
      </Card>

      {/* Create Campaign Dialog */}
      <Dialog open={isCreateDialogOpen} onOpenChange={setIsCreateDialogOpen}>
        <DialogContent className="max-w-2xl max-h-[90vh] overflow-y-auto">
          <DialogHeader>
            <DialogTitle>Create New Campaign</DialogTitle>
            <DialogDescription>Fill in the details for your new fundraising campaign</DialogDescription>
          </DialogHeader>
          <div className="grid gap-4 py-4">
            <div className="space-y-2">
              <Label htmlFor="title">Campaign Title *</Label>
              <Input
                id="title"
                value={formData.title}
                onChange={(e) => setFormData({ ...formData, title: e.target.value })}
                placeholder="e.g., Education Fund 2024"
              />
            </div>
            <div className="space-y-2">
              <Label htmlFor="description">Description *</Label>
              <Textarea
                id="description"
                value={formData.description}
                onChange={(e) => setFormData({ ...formData, description: e.target.value })}
                placeholder="Describe your campaign goals and impact"
                rows={3}
              />
            </div>
            <div className="grid grid-cols-2 gap-4">
              <div className="space-y-2">
                <Label htmlFor="donationType">Donation Type *</Label>
                <Select
                  value={formData.donationType}
                  onValueChange={(value: "one_time" | "recurring") => setFormData({ ...formData, donationType: value })}
                >
                  <SelectTrigger id="donationType">
                    <SelectValue placeholder="Select donation type" />
                  </SelectTrigger>
                  <SelectContent>
                    <SelectItem value="one_time">One-Time Donations</SelectItem>
                    <SelectItem value="recurring">Recurring Donations</SelectItem>
                  </SelectContent>
                </Select>
              </div>
              <div className="space-y-2">
                <Label htmlFor="status">Status *</Label>
                <Select
                  value={formData.status}
                  onValueChange={(value: "draft" | "published" | "closed" | "archived") => setFormData({ ...formData, status: value })}
                >
                  <SelectTrigger id="status">
                    <SelectValue placeholder="Select status" />
                  </SelectTrigger>
                  <SelectContent>
                    <SelectItem value="draft">Draft</SelectItem>
                    <SelectItem value="published">Published</SelectItem>
                  </SelectContent>
                </Select>
              </div>
            </div>
            <div className="grid grid-cols-2 gap-4">
              <div className="space-y-2">
                <Label htmlFor="targetAmount">Target Amount () *</Label>
                <Input
                  id="targetAmount"
                  type="number"
                  value={formData.targetAmount}
                  onChange={(e) => setFormData({ ...formData, targetAmount: e.target.value })}
