          <Button variant="outline" onClick={() => navigate('/charity/donations')}>
            View Donations
          </Button>
          <Button variant="outline" onClick={() => navigate('/charity/fund-tracking')}>
            Log Fund Usage
          </Button>
        </CardContent>
      </Card>

      {/* Recent Donations - removed demo content */}
    </div>
  );
}

import { useState, useEffect } from "react";
