interface Document {
  id: number;
  doc_type: string;
  file_path: string;
  expires: boolean;
  expiry_date?: string;
  renewal_reminder_sent_at?: string;
  uploaded_at: string;
  is_expired?: boolean;
  is_expiring_soon?: boolean;
  days_until_expiry?: number;
}

interface DocumentStatus {
  documents: Document[];
  expired_count: number;
  expiring_soon_count: number;
  total_expirable_documents: number;
}

export default function CharityDocuments() {
  const { user } = useAuth();
  const [documents, setDocuments] = useState<Document[]>([]);
  const [documentStatus, setDocumentStatus] = useState<DocumentStatus | null>(null);
  const [loading, setLoading] = useState(true);
  const [isUploadOpen, setIsUploadOpen] = useState(false);
  const [uploadFile, setUploadFile] = useState<File | null>(null);
  const [uploadType, setUploadType] = useState("");
  const [expiryDate, setExpiryDate] = useState("");
  const [hasExpiry, setHasExpiry] = useState(false);

  useEffect(() => {
    if (user?.charity?.id) {
      fetchDocuments();
      fetchDocumentStatus();
    }
  }, [user]);

  const fetchDocuments = async () => {
    try {
      const response = await axios.get(`/api/charities/${user?.charity?.id}/documents`);
      // Ensure response.data is always an array to avoid runtime errors on render
      const documentsData = Array.isArray(response.data) ? response.data : [];
      setDocuments(documentsData);
    } catch (error) {
      console.error("Failed to fetch documents:", error);
      toast.error("Failed to fetch documents");
      // Keep documents as an empty array on error
      setDocuments([]);
    } finally {
      setLoading(false);
    }
  };

  const fetchDocumentStatus = async () => {
    try {
      const response = await axios.get(`/api/charities/${user?.charity?.id}/documents/expiry-status`);
      setDocumentStatus(response.data);
    } catch (error) {
      console.error("Failed to fetch document status:", error);
      // Provide a safe default to avoid undefined access in UI
      setDocumentStatus({
        documents: [],
        expired_count: 0,
        expiring_soon_count: 0,
        total_expirable_documents: 0,
      });
    }
  };

  const handleUpload = async () => {
    if (!uploadFile || !uploadType) {
      toast.error("Please select a file and document type");
      return;
    }

    try {
      const formData = new FormData();
      formData.append("document", uploadFile);
      formData.append("doc_type", uploadType);
      if (hasExpiry && expiryDate) {
        formData.append("expires", "true");
        formData.append("expiry_date", expiryDate);
      }

      await axios.post(`/api/charities/${user?.charity?.id}/documents`, formData, {
        headers: { "Content-Type": "multipart/form-data" },
      });

      toast.success("Document uploaded successfully");
      setIsUploadOpen(false);
      resetUploadForm();
      fetchDocuments();
      fetchDocumentStatus();
    } catch (error: any) {
      toast.error(error.response?.data?.message || "Failed to upload document");
    }
  };

  const resetUploadForm = () => {
    setUploadFile(null);
    setUploadType("");
    setExpiryDate("");
    setHasExpiry(false);
  };

  const downloadDocument = async (document: Document) => {
    try {
      const response = await axios.get(`/storage/${document.file_path}`, {
        responseType: 'blob'
      });

      const url = window.URL.createObjectURL(new Blob([response.data]));
      const link = window.document.createElement('a');
      link.href = url;
      link.setAttribute('download', `${document.doc_type}.pdf`);
      window.document.body.appendChild(link);
      link.click();
      link.remove();
    } catch (error) {
      toast.error("Failed to download document");
    }
  };

  const viewDocument = (document: Document) => {
    window.open(`/storage/${document.file_path}`, '_blank');
  };

  const getDocumentBadge = (document: Document) => {
    if (document.is_expired) {
      return <Badge className="bg-red-100 text-red-800">Expired</Badge>;
    }
    if (document.is_expiring_soon) {
      return <Badge className="bg-yellow-100 text-yellow-800">Expiring Soon</Badge>;
    }
    if (document.expires) {
      return <Badge className="bg-green-100 text-green-800">Valid</Badge>;
    }
    return <Badge className="bg-blue-100 text-blue-800">No Expiry</Badge>;
  };

  const formatDocType = (docType: string) => {
    return docType.replace(/_/g, " ").replace(/\b\w/g, l => l.toUpperCase());
  };

  const documentTypes = [
    "certificate_of_registration",
    "tax_exemption_certificate",
    "financial_report",
    "board_resolution",
    "valid_id",
    "bank_certificate",
    "other_legal_document"
  ];

  if (loading) {
    return (
      <div className="flex items-center justify-center h-64">
        <div className="animate-spin rounded-full h-8 w-8 border-b-2 border-primary"></div>
      </div>
    );
  }

  return (
    <div className="space-y-6">
      <div className="flex justify-between items-center">
        <div>
          <h1 className="text-3xl font-bold">Document Management</h1>
          <p className="text-muted-foreground">Manage your organization's legal documents</p>
        </div>
        <Dialog open={isUploadOpen} onOpenChange={setIsUploadOpen}>
          <DialogTrigger asChild>
            <Button>
              <Upload className="h-4 w-4 mr-2" />
              Upload Document
            </Button>
          </DialogTrigger>
          <DialogContent>
            <DialogHeader>
              <DialogTitle>Upload New Document</DialogTitle>
              <DialogDescription>
                Upload legal documents for your organization
              </DialogDescription>
            </DialogHeader>
            <div className="space-y-4">
              <div>
                <label className="text-sm font-medium">Document Type</label>
                <select 
                  className="w-full mt-1 p-2 border rounded-md"
                  value={uploadType}
                  onChange={(e) => setUploadType(e.target.value)}
                >
                  <option value="">Select document type</option>
                  {documentTypes.map((type) => (
                    <option key={type} value={type}>
                      {formatDocType(type)}
                    </option>
                  ))}
                </select>
              </div>
              <div>
                <label className="text-sm font-medium">Document File</label>
                <Input
                  type="file"
                  accept=".pdf,.jpg,.jpeg,.png"
                  onChange={(e) => setUploadFile(e.target.files?.[0] || null)}
                  className="mt-1"
                />
                <p className="text-xs text-muted-foreground mt-1">
                  Accepted formats: PDF, JPG, PNG (Max 10MB)
                </p>
              </div>
              <div className="flex items-center space-x-2">
                <input
                  type="checkbox"
                  id="hasExpiry"
                  checked={hasExpiry}
                  onChange={(e) => setHasExpiry(e.target.checked)}
                />
                <label htmlFor="hasExpiry" className="text-sm font-medium">
                  This document has an expiry date
                </label>
              </div>
              {hasExpiry && (
                <div>
                  <label className="text-sm font-medium">Expiry Date</label>
                  <Input
                    type="date"
                    value={expiryDate}
                    onChange={(e) => setExpiryDate(e.target.value)}
                    min={new Date().toISOString().split('T')[0]}
                    className="mt-1"
                  />
                </div>
              )}
              <div className="flex gap-2 justify-end">
                <Button variant="outline" onClick={() => setIsUploadOpen(false)}>
                  Cancel
                </Button>
                <Button onClick={handleUpload}>
                  Upload Document
                </Button>
              </div>
            </div>
          </DialogContent>
        </Dialog>
      </div>

      {/* Document Status Alerts */}
      {documentStatus && (
        <div className="space-y-4">
          {documentStatus.expired_count > 0 && (
            <Alert className="border-red-200 bg-red-50">
              <AlertTriangle className="h-4 w-4 text-red-600" />
              <AlertDescription className="text-red-800">
                <strong>Urgent:</strong> You have {documentStatus.expired_count} expired document(s). 
                Please renew them immediately to maintain your verification status.
              </AlertDescription>
            </Alert>
          )}
          {documentStatus.expiring_soon_count > 0 && (
            <Alert className="border-yellow-200 bg-yellow-50">
              <Clock className="h-4 w-4 text-yellow-600" />
              <AlertDescription className="text-yellow-800">
                <strong>Reminder:</strong> You have {documentStatus.expiring_soon_count} document(s) 
                expiring within 30 days. Please prepare renewals.
              </AlertDescription>
            </Alert>
          )}
        </div>
      )}

      {/* Documents Grid */}
      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4">
        {documents.map((document) => (
          <Card key={document.id} className={document.is_expired ? "border-red-200" : document.is_expiring_soon ? "border-yellow-200" : ""}>
            <CardHeader className="pb-3">
              <div className="flex justify-between items-start">
                <div className="flex items-center gap-2">
                  <FileText className="h-5 w-5 text-blue-600" />
                  <div>
                    <CardTitle className="text-base">{formatDocType(document.doc_type)}</CardTitle>
                    <CardDescription className="text-xs">
                      Uploaded {new Date(document.uploaded_at).toLocaleDateString()}
                    </CardDescription>
                  </div>
                </div>
                {getDocumentBadge(document)}
              </div>
            </CardHeader>
            <CardContent className="space-y-3">
              {document.expires && document.expiry_date && (
                <div className="text-sm">
                  <div className="flex items-center gap-2">
                    <Calendar className="h-4 w-4 text-muted-foreground" />
                    <span>Expires: {new Date(document.expiry_date).toLocaleDateString()}</span>
                  </div>
                  {document.days_until_expiry !== undefined && (
                    <p className={`text-xs mt-1 ${document.is_expired ? 'text-red-600' : document.is_expiring_soon ? 'text-yellow-600' : 'text-green-600'}`}>
                      {document.is_expired 
                        ? `Expired ${Math.abs(document.days_until_expiry)} days ago`
                        : `${document.days_until_expiry} days remaining`
                      }
                    </p>
                  )}
                </div>
              )}

              <div className="flex gap-2">
                <Button
                  variant="outline"
                  size="sm"
                  onClick={() => viewDocument(document)}
                  className="flex-1"
                >
                  <Eye className="h-3 w-3 mr-1" />
                  View
                </Button>
                <Button
                  variant="outline"
                  size="sm"
                  onClick={() => downloadDocument(document)}
                >
                  <Download className="h-3 w-3" />
                </Button>
              </div>
            </CardContent>
          </Card>
        ))}
      </div>

      {documents.length === 0 && (
        <Card>
          <CardContent className="text-center py-8">
            <FileText className="h-12 w-12 mx-auto text-muted-foreground mb-4" />
            <p className="text-muted-foreground">No documents uploaded yet.</p>
            <p className="text-sm text-muted-foreground mt-2">
              Upload your legal documents to maintain verification status.
            </p>
          </CardContent>
        </Card>
      )}

      {/* Required Documents Info */}
      <Card>
        <CardHeader>
          <CardTitle>Required Documents</CardTitle>
          <CardDescription>
            These documents are typically required for charity verification
          </CardDescription>
        </CardHeader>
        <CardContent>
          <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
            {documentTypes.map((type) => {
              const hasDocument = documents.some(doc => doc.doc_type === type);
              return (
                <div key={type} className="flex items-center gap-2">
                  {hasDocument ? (
                    <CheckCircle className="h-4 w-4 text-green-600" />
                  ) : (
                    <Clock className="h-4 w-4 text-gray-400" />
                  )}
                  <span className={`text-sm ${hasDocument ? 'text-green-800' : 'text-muted-foreground'}`}>
                    {formatDocType(type)}
                  </span>
                </div>
              );
            })}
          </div>
        </CardContent>
      </Card>
    </div>
  );
}

import { useEffect, useState } from "react";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Badge } from "@/components/ui/badge";
import { Skeleton } from "@/components/ui/skeleton";
import {
  Dialog,
  DialogContent,
  DialogDescription,
  DialogFooter,
  DialogHeader,
  DialogTitle,
} from "@/components/ui/dialog";
import { Label } from "@/components/ui/label";
import { Textarea } from "@/components/ui/textarea";
import {
  Table,
  TableBody,
  TableCell,
  TableHead,
  TableHeader,
  TableRow,
} from "@/components/ui/table";
import {
  Select,
  SelectContent,
  SelectItem,
  SelectTrigger,
  SelectValue,
} from "@/components/ui/select";
import {
  listDocuments,
  uploadDocument,
  verifyDocument,
  requestDocumentInfo,
  computeChecksum,
} from "@/services/apiCharity";
import type { Document } from "@/types/charity";
import { Upload, CheckCircle, AlertCircle, Download, FileText, Image as ImageIcon } from "lucide-react";
import { toast } from "@/hooks/use-toast";

/**
 * Documents Page
 * Upload, view, and verify charity documents
 */
const DocumentsPage = () => {
  const [documents, setDocuments] = useState<Document[]>([]);
  const [loading, setLoading] = useState(true);
  const [page, setPage] = useState(1);
  const [totalPages, setTotalPages] = useState(1);
  const [statusFilter, setStatusFilter] = useState("all");
  const [uploadDialogOpen, setUploadDialogOpen] = useState(false);
  const [viewDialogOpen, setViewDialogOpen] = useState(false);
  const [selectedDocument, setSelectedDocument] = useState<Document | null>(null);
  const [noteDialogOpen, setNoteDialogOpen] = useState(false);
  const [adminNote, setAdminNote] = useState("");
  const [uploading, setUploading] = useState(false);

  useEffect(() => {
    loadDocuments();
  }, [page, statusFilter]);

  const loadDocuments = async () => {
    try {
      setLoading(true);
      const response = await listDocuments({
        page,
        pageSize: 20,
        status: statusFilter === "all" ? undefined : statusFilter,
      });
      setDocuments(response.data);
      setTotalPages(response.pagination.totalPages);
    } catch (err) {
      toast({
        title: "Error",
        description: err instanceof Error ? err.message : "Failed to load documents",
        variant: "destructive",
      });
    } finally {
      setLoading(false);
    }
  };

  const handleUpload = async (event: React.ChangeEvent<HTMLInputElement>) => {
    const file = event.target.files?.[0];
    if (!file) return;

    // Validate file type
    const allowedTypes = ["application/pdf", "image/png", "image/jpeg", "image/jpg"];
    if (!allowedTypes.includes(file.type)) {
      toast({
        title: "Error",
        description: "Only PDF, PNG, JPG, and JPEG files are allowed",
        variant: "destructive",
      });
      return;
    }

    // Validate file size (10MB)
    if (file.size > 10 * 1024 * 1024) {
      toast({
        title: "Error",
        description: "File size must be less than 10MB",
        variant: "destructive",
      });
      return;
    }

    try {
      setUploading(true);
      const checksum = await computeChecksum(file);

      const formData = new FormData();
      formData.append("file", file);
      formData.append("checksum", checksum);
      formData.append("documentType", "verification");

      await uploadDocument(formData);
      toast({ title: "Success", description: "Document uploaded successfully" });
      setUploadDialogOpen(false);
      loadDocuments();
    } catch (err) {
      toast({
        title: "Error",
        description: err instanceof Error ? err.message : "Upload failed",
        variant: "destructive",
      });
    } finally {
      setUploading(false);
    }
  };

  const handleVerify = async (id: string) => {
    try {
      await verifyDocument(id);
      toast({ title: "Success", description: "Document verified" });
      loadDocuments();
    } catch (err) {
      toast({
        title: "Error",
        description: err instanceof Error ? err.message : "Failed to verify",
        variant: "destructive",
      });
    }
  };

  const handleRequestInfo = async () => {
    if (!selectedDocument || !adminNote.trim()) {
      toast({
        title: "Error",
        description: "Please provide a message",
        variant: "destructive",
      });
      return;
    }
    try {
      await requestDocumentInfo(selectedDocument.id, adminNote);
      toast({ title: "Success", description: "Information requested" });
      setNoteDialogOpen(false);
      setAdminNote("");
      setSelectedDocument(null);
      loadDocuments();
    } catch (err) {
      toast({
        title: "Error",
        description: err instanceof Error ? err.message : "Request failed",
        variant: "destructive",
      });
    }
  };

  const getStatusBadge = (status: Document["status"]) => {
    const variants: Record<Document["status"], string> = {
      pending: "bg-warning/10 text-warning",
      verified: "bg-success/10 text-success",
      rejected: "bg-destructive/10 text-destructive",
      info_requested: "bg-accent/10 text-accent-foreground",
    };
    return (
      <Badge variant="outline" className={variants[status]}>
        {status.replace("_", " ")}
      </Badge>
    );
  };

  if (loading && documents.length === 0) {
    return (
      <div className="p-lg">
        <Card>
          <CardHeader>
            <Skeleton className="h-8 w-48" />
          </CardHeader>
          <CardContent>
            <Skeleton className="h-64 w-full" />
          </CardContent>
        </Card>
      </div>
    );
  }

  return (
    <div className="p-lg space-y-lg">
      <Card>
        <CardHeader>
          <div className="flex items-center justify-between">
            <CardTitle>Documents</CardTitle>
            <Button onClick={() => setUploadDialogOpen(true)}>
              <Upload className="h-4 w-4 mr-xs" />
              Upload Document
            </Button>
          </div>
        </CardHeader>
        <CardContent className="space-y-md">
          {/* Filters */}
          <div className="flex gap-md items-center">
            <Select value={statusFilter} onValueChange={setStatusFilter}>
              <SelectTrigger className="w-48">
                <SelectValue placeholder="Filter by status" />
              </SelectTrigger>
              <SelectContent>
                <SelectItem value="all">All statuses</SelectItem>
                <SelectItem value="pending">Pending</SelectItem>
                <SelectItem value="verified">Verified</SelectItem>
                <SelectItem value="rejected">Rejected</SelectItem>
                <SelectItem value="info_requested">Info Requested</SelectItem>
              </SelectContent>
            </Select>
          </div>

          {/* Table */}
          <div className="border rounded-md">
            <Table>
              <TableHeader>
                <TableRow>
                  <TableHead>Preview</TableHead>
                  <TableHead>Filename</TableHead>
                  <TableHead>Type</TableHead>
                  <TableHead>Checksum</TableHead>
                  <TableHead>Status</TableHead>
                  <TableHead>Uploaded</TableHead>
                  <TableHead className="text-right">Actions</TableHead>
                </TableRow>
              </TableHeader>
              <TableBody>
                {documents.length === 0 ? (
                  <TableRow>
                    <TableCell colSpan={7} className="text-center py-lg">
                      <p className="text-muted-foreground">No documents found</p>
                    </TableCell>
                  </TableRow>
                ) : (
                  documents.map((doc) => (
                    <TableRow key={doc.id}>
                      <TableCell>
                        {doc.thumbnailUrl ? (
                          <img
                            src={doc.thumbnailUrl}
                            alt="Preview"
                            className="h-10 w-10 object-cover rounded border"
                          />
                        ) : doc.mimeType.startsWith("image/") ? (
                          <ImageIcon className="h-10 w-10 text-muted-foreground" />
                        ) : (
                          <FileText className="h-10 w-10 text-muted-foreground" />
                        )}
                      </TableCell>
                      <TableCell className="font-medium max-w-xs truncate">
                        {doc.filename}
                      </TableCell>
                      <TableCell className="capitalize">{doc.documentType}</TableCell>
                      <TableCell>
                        <div className="flex items-center gap-xs">
                          <code className="text-xs font-mono">
                            {doc.checksum.slice(0, 8)}...
                          </code>
                          {doc.checksumVerified && (
                            <CheckCircle className="h-3 w-3 text-success" />
                          )}
                        </div>
                      </TableCell>
                      <TableCell>{getStatusBadge(doc.status)}</TableCell>
                      <TableCell className="text-sm text-muted-foreground">
                        {new Date(doc.uploadedAt).toLocaleDateString()}
                      </TableCell>
                      <TableCell>
                        <div className="flex justify-end gap-xs">
                          <Button
                            variant="ghost"
                            size="sm"
                            onClick={() => {
                              setSelectedDocument(doc);
                              setViewDialogOpen(true);
                            }}
                          >
                            <FileText className="h-4 w-4" />
                          </Button>
                          {doc.status === "pending" && (
                            <>
                              <Button
                                variant="ghost"
                                size="sm"
                                onClick={() => handleVerify(doc.id)}
                              >
                                <CheckCircle className="h-4 w-4 text-success" />
                              </Button>
                              <Button
                                variant="ghost"
                                size="sm"
                                onClick={() => {
                                  setSelectedDocument(doc);
                                  setNoteDialogOpen(true);
                                }}
                              >
                                <AlertCircle className="h-4 w-4 text-warning" />
                              </Button>
                            </>
                          )}
                        </div>
                      </TableCell>
                    </TableRow>
                  ))
                )}
              </TableBody>
            </Table>
          </div>

          {/* Pagination */}
          <div className="flex items-center justify-between">
            <p className="text-sm text-muted-foreground">
              Page {page} of {totalPages}
            </p>
            <div className="flex gap-sm">
              <Button
                variant="outline"
                size="sm"
                disabled={page === 1}
                onClick={() => setPage(page - 1)}
              >
                Previous
              </Button>
              <Button
                variant="outline"
                size="sm"
                disabled={page === totalPages}
                onClick={() => setPage(page + 1)}
              >
                Next
              </Button>
            </div>
          </div>
        </CardContent>
      </Card>

      {/* Upload Dialog */}
      <Dialog open={uploadDialogOpen} onOpenChange={setUploadDialogOpen}>
        <DialogContent>
          <DialogHeader>
            <DialogTitle>Upload Document</DialogTitle>
            <DialogDescription>
              Upload a verification document (PDF, PNG, JPG, max 10MB)
            </DialogDescription>
          </DialogHeader>
          <div className="py-md">
            <Label htmlFor="file-upload" className="block mb-sm">
              Select File
            </Label>
            <input
              id="file-upload"
              type="file"
              accept=".pdf,.png,.jpg,.jpeg"
              onChange={handleUpload}
              disabled={uploading}
              className="block w-full text-sm file:mr-md file:py-sm file:px-md file:rounded-md file:border-0 file:text-sm file:font-medium file:bg-primary file:text-primary-foreground hover:file:bg-primary/90"
            />
            {uploading && (
              <p className="text-sm text-muted-foreground mt-sm">
                Computing checksum and uploading...
              </p>
            )}
          </div>
        </DialogContent>
      </Dialog>

      {/* View Dialog */}
      {selectedDocument && (
        <Dialog open={viewDialogOpen} onOpenChange={setViewDialogOpen}>
          <DialogContent className="max-w-3xl">
            <DialogHeader>
              <DialogTitle>{selectedDocument.filename}</DialogTitle>
              <DialogDescription>Document details and preview</DialogDescription>
            </DialogHeader>
            <div className="space-y-md py-md">
              <div className="grid grid-cols-2 gap-md text-sm">
                <div>
                  <p className="text-muted-foreground">Type:</p>
                  <p className="font-medium capitalize">{selectedDocument.documentType}</p>
                </div>
                <div>
                  <p className="text-muted-foreground">Status:</p>
                  {getStatusBadge(selectedDocument.status)}
                </div>
                <div>
                  <p className="text-muted-foreground">Uploaded:</p>
                  <p className="font-medium">
                    {new Date(selectedDocument.uploadedAt).toLocaleString()}
                  </p>
                </div>
                <div>
                  <p className="text-muted-foreground">Size:</p>
                  <p className="font-medium">
                    {(selectedDocument.fileSize / 1024).toFixed(2)} KB
                  </p>
                </div>
              </div>
              <div>
                <p className="text-sm text-muted-foreground mb-xs">SHA-256 Checksum:</p>
                <code className="text-xs font-mono bg-muted p-sm rounded block break-all">
                  {selectedDocument.checksum}
                </code>
              </div>
              {selectedDocument.adminNotes && (
                <div>
                  <p className="text-sm text-muted-foreground mb-xs">Admin Notes:</p>
                  <p className="text-sm p-sm bg-muted rounded">{selectedDocument.adminNotes}</p>
                </div>
              )}
              <div className="border rounded-md p-md bg-muted/30">
                {selectedDocument.mimeType.startsWith("image/") ? (
                  <img
                    src={selectedDocument.fileUrl}
                    alt="Document preview"
                    className="w-full rounded-md"
                  />
                ) : (
                  <div className="flex flex-col items-center justify-center py-2xl">
                    <FileText className="h-16 w-16 text-muted-foreground mb-md" />
                    <p className="text-sm text-muted-foreground">
                      PDF Preview (download to view)
                    </p>
                    <Button variant="outline" size="sm" className="mt-md" asChild>
                      <a href={selectedDocument.fileUrl} download target="_blank" rel="noopener noreferrer">
                        <Download className="h-4 w-4 mr-xs" />
                        Download
                      </a>
                    </Button>
                  </div>
                )}
              </div>
            </div>
            <DialogFooter>
              <Button variant="outline" onClick={() => setViewDialogOpen(false)}>
                Close
              </Button>
            </DialogFooter>
          </DialogContent>
        </Dialog>
      )}

      {/* Request Info Dialog */}
      <Dialog open={noteDialogOpen} onOpenChange={setNoteDialogOpen}>
        <DialogContent>
          <DialogHeader>
            <DialogTitle>Request More Information</DialogTitle>
            <DialogDescription>
              Send a message requesting additional documentation or clarification
            </DialogDescription>
          </DialogHeader>
          <div className="py-md">
            <Label htmlFor="admin-note">Message</Label>
            <Textarea
              id="admin-note"
              value={adminNote}
              onChange={(e) => setAdminNote(e.target.value)}
              placeholder="Describe what information is needed..."
              className="mt-sm"
            />
          </div>
          <DialogFooter>
            <Button variant="outline" onClick={() => setNoteDialogOpen(false)}>
              Cancel
            </Button>
            <Button onClick={handleRequestInfo}>Send Request</Button>
          </DialogFooter>
        </DialogContent>
      </Dialog>
    </div>
  );
};

export default DocumentsPage;

import { useState, useEffect } from "react";
import { CheckCircle, XCircle, Eye, Download, Filter } from "lucide-react";
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Badge } from "@/components/ui/badge";
import { Dialog, DialogContent, DialogDescription, DialogFooter, DialogHeader, DialogTitle } from "@/components/ui/dialog";
import { Table, TableBody, TableCell, TableHead, TableHeader, TableRow } from "@/components/ui/table";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { Textarea } from "@/components/ui/textarea";
import { Label } from "@/components/ui/label";
import { toast } from "sonner";
import { useAuth } from "@/context/AuthContext";
import { donationsService, Donation as ApiDonation } from "@/services/donations";

interface Donation {
  id: number;
  donor: string;
  campaign: string;
  amount: number;
  date: string;
  status: 'pending' | 'completed' | 'rejected';
  proofUrl?: string;
  isAnonymous: boolean;
}

export default function DonationManagement() {
  const { user } = useAuth();
  const [donations, setDonations] = useState<Donation[]>([]);
  const [loading, setLoading] = useState(true);
  const [submitting, setSubmitting] = useState(false);

  const [selectedDonation, setSelectedDonation] = useState<Donation | null>(null);
  const [isViewDialogOpen, setIsViewDialogOpen] = useState(false);
  const [isRejectDialogOpen, setIsRejectDialogOpen] = useState(false);
  const [rejectReason, setRejectReason] = useState("");
  const [filterStatus, setFilterStatus] = useState("all");

  const handleConfirm = async (id: number) => {
    try {
      setSubmitting(true);
      await donationsService.confirmDonation(id, 'completed');
      toast.success("Donation confirmed successfully");
      loadDonations();
    } catch (error: any) {
      console.error("Failed to confirm donation:", error);
      toast.error(error.response?.data?.message || "Failed to confirm donation");
    } finally {
      setSubmitting(false);
    }
  };

  const handleReject = async () => {
    if (!selectedDonation || !rejectReason.trim()) {
      toast.error("Please provide a reason for rejection");
      return;
    }

    try {
      setSubmitting(true);
      await donationsService.confirmDonation(selectedDonation.id, 'rejected');
      toast.success("Donation rejected");
      setIsRejectDialogOpen(false);
      setRejectReason("");
      setSelectedDonation(null);
      loadDonations();
    } catch (error: any) {
      console.error("Failed to reject donation:", error);
      toast.error(error.response?.data?.message || "Failed to reject donation");
    } finally {
      setSubmitting(false);
    }
  };

  const handleView = (donation: Donation) => {
    setSelectedDonation(donation);
    setIsViewDialogOpen(true);
  };

  const openRejectDialog = (donation: Donation) => {
    setSelectedDonation(donation);
    setIsRejectDialogOpen(true);
  };

  const getStatusBadge = (status: string) => {
    switch (status) {
      case 'pending':
        return <Badge variant="secondary">Pending</Badge>;
      case 'completed':
        return <Badge className="bg-green-600">Completed</Badge>;
      case 'rejected':
        return <Badge variant="destructive">Rejected</Badge>;
      default:
        return <Badge>{status}</Badge>;
    }
  };

  useEffect(() => {
    loadDonations();
  }, []);

  const loadDonations = async () => {
    try {
      if (!user?.charity?.id) {
        toast.error("No charity found for your account");
        setLoading(false);
        return;
      }

      const response = await donationsService.getCharityDonations(user.charity.id);
      const formattedDonations = response.data.map((donation: ApiDonation) => ({
        id: donation.id,
        donor: donation.donor?.name || "Unknown Donor",
        campaign: donation.campaign?.title || "General Donation",
        amount: donation.amount,
        date: donation.donated_at,
        status: donation.status as 'pending' | 'completed' | 'rejected',
        proofUrl: donation.proof_path,
        isAnonymous: donation.is_anonymous
      }));
      setDonations(formattedDonations);
    } catch (error: any) {
      console.error("Failed to load donations:", error);
      toast.error(error.response?.data?.message || "Failed to load donations");
    } finally {
      setLoading(false);
    }
  };

  const filteredDonations = filterStatus === "all" 
    ? donations 
    : donations.filter(d => d.status === filterStatus);

  const stats = {
    total: donations.filter(d => d.status === 'completed').reduce((sum, d) => sum + d.amount, 0),
    pending: donations.filter(d => d.status === 'pending').length,
    confirmed: donations.filter(d => d.status === 'completed').length,
    rejected: donations.filter(d => d.status === 'rejected').length
  };

  return (
    <div className="max-w-7xl mx-auto px-4 py-6 space-y-6">
      {/* Header */}
      <div className="flex items-center justify-between">
        <div>
          <h1 className="text-2xl font-bold">Donation Management</h1>
          <p className="text-muted-foreground text-sm">
            Review and manage incoming donations
          </p>
        </div>
        <Button variant="outline">
          <Download className="mr-2 h-4 w-4" />
          Export Report
        </Button>
      </div>

      {/* Stats */}
      <div className="grid gap-4 md:grid-cols-4">
        <Card>
          <CardHeader className="pb-2">
            <CardTitle className="text-sm font-medium">Total Confirmed</CardTitle>
          </CardHeader>
          <CardContent>
            <div className="text-2xl font-bold">{stats.total.toLocaleString()}</div>
          </CardContent>
        </Card>
        <Card>
          <CardHeader className="pb-2">
            <CardTitle className="text-sm font-medium">Pending Review</CardTitle>
          </CardHeader>
          <CardContent>
            <div className="text-2xl font-bold">{stats.pending}</div>
          </CardContent>
        </Card>
        <Card>
          <CardHeader className="pb-2">
            <CardTitle className="text-sm font-medium">Confirmed</CardTitle>
          </CardHeader>
          <CardContent>
            <div className="text-2xl font-bold">{stats.confirmed}</div>
          </CardContent>
        </Card>
        <Card>
          <CardHeader className="pb-2">
            <CardTitle className="text-sm font-medium">Rejected</CardTitle>
          </CardHeader>
          <CardContent>
            <div className="text-2xl font-bold">{stats.rejected}</div>
          </CardContent>
        </Card>
      </div>

      {/* Donations Table */}
      <Card>
        <CardHeader>
          <div className="flex items-center justify-between">
            <div>
              <CardTitle>All Donations</CardTitle>
              <CardDescription>Review and confirm donation submissions</CardDescription>
            </div>
            <Select value={filterStatus} onValueChange={setFilterStatus}>
              <SelectTrigger className="w-[180px]">
                <Filter className="mr-2 h-4 w-4" />
                <SelectValue placeholder="Filter by status" />
              </SelectTrigger>
              <SelectContent>
                <SelectItem value="all">All Status</SelectItem>
                <SelectItem value="pending">Pending</SelectItem>
                <SelectItem value="completed">Completed</SelectItem>
                <SelectItem value="rejected">Rejected</SelectItem>
              </SelectContent>
            </Select>
          </div>
        </CardHeader>
        <CardContent>
          {loading ? (
            <div className="py-12 text-center text-muted-foreground">Loading donations...</div>
          ) : filteredDonations.length === 0 ? (
            <div className="py-12 text-center text-muted-foreground">
              {donations.length === 0 
                ? "No donations yet. Donations will appear here when donors contribute to your campaigns."
                : "No donations match the selected filter."}
            </div>
          ) : (
            <Table>
              <TableHeader>
                <TableRow>
                  <TableHead>Donor</TableHead>
                  <TableHead>Campaign</TableHead>
                  <TableHead>Amount</TableHead>
                  <TableHead>Date</TableHead>
                  <TableHead>Status</TableHead>
                  <TableHead className="text-right">Actions</TableHead>
                </TableRow>
              </TableHeader>
              <TableBody>
                {filteredDonations.map((donation) => (
                  <TableRow key={donation.id}>
                    <TableCell className="font-medium">
                      {donation.isAnonymous ? "Anonymous Donor" : donation.donor}
                    </TableCell>
                    <TableCell>{donation.campaign}</TableCell>
                    <TableCell className="font-bold">{donation.amount.toLocaleString()}</TableCell>
                    <TableCell>{new Date(donation.date).toLocaleDateString()}</TableCell>
                    <TableCell>{getStatusBadge(donation.status)}</TableCell>
                    <TableCell className="text-right">
                      <div className="flex justify-end gap-2">
                        <Button variant="ghost" size="icon" onClick={() => handleView(donation)}>
                          <Eye className="h-4 w-4" />
                        </Button>
                        {donation.status === 'pending' && (
                          <>
                            <Button
                              variant="ghost"
                              size="icon"
                              onClick={() => handleConfirm(donation.id)}
                              className="text-green-600 hover:text-green-700"
                              disabled={submitting}
                            >
                              <CheckCircle className="h-4 w-4" />
                            </Button>
                            <Button
                              variant="ghost"
                              size="icon"
                              onClick={() => openRejectDialog(donation)}
                              className="text-destructive hover:text-destructive"
                              disabled={submitting}
                            >
                              <XCircle className="h-4 w-4" />
                            </Button>
                          </>
                        )}
                      </div>
                    </TableCell>
                  </TableRow>
                ))}
              </TableBody>
            </Table>
          )}
        </CardContent>
      </Card>

      {/* View Donation Dialog */}
      <Dialog open={isViewDialogOpen} onOpenChange={setIsViewDialogOpen}>
        <DialogContent className="max-w-2xl">
          <DialogHeader>
            <DialogTitle>Donation Details</DialogTitle>
            <DialogDescription>Review donation information and proof of payment</DialogDescription>
          </DialogHeader>
          {selectedDonation && (
            <div className="space-y-4">
              <div className="grid grid-cols-2 gap-4">
                <div>
                  <Label>Donor Name</Label>
                  <p className="text-sm font-medium mt-1">
                    {selectedDonation.isAnonymous ? "Anonymous Donor" : selectedDonation.donor}
                  </p>
                </div>
                <div>
                  <Label>Campaign</Label>
                  <p className="text-sm font-medium mt-1">{selectedDonation.campaign}</p>
                </div>
              </div>
              <div className="grid grid-cols-2 gap-4">
                <div>
                  <Label>Amount</Label>
                  <p className="text-lg font-bold text-green-600 mt-1">
                    {selectedDonation.amount.toLocaleString()}
                  </p>
                </div>
                <div>
                  <Label>Date</Label>
                  <p className="text-sm font-medium mt-1">
                    {new Date(selectedDonation.date).toLocaleDateString()}
                  </p>
                </div>
              </div>
              <div>
                <Label>Status</Label>
                <div className="mt-1">{getStatusBadge(selectedDonation.status)}</div>
              </div>
              <div>
                <Label>Proof of Payment</Label>
                <div className="mt-2 border rounded-lg p-4 bg-muted/50">
                  <p className="text-sm text-muted-foreground">
                    {selectedDonation.proofUrl ? (
                      <span>File: {selectedDonation.proofUrl}</span>
                    ) : (
                      "No proof uploaded"
                    )}
                  </p>
                  {selectedDonation.proofUrl && (
                    <Button variant="outline" size="sm" className="mt-2">
                      <Download className="mr-2 h-4 w-4" />
                      Download Proof
                    </Button>
                  )}
                </div>
              </div>
            </div>
          )}
          <DialogFooter>
            {selectedDonation?.status === 'pending' && (
              <>
                <Button variant="outline" onClick={() => openRejectDialog(selectedDonation)}>
                  <XCircle className="mr-2 h-4 w-4" />
                  Reject
                </Button>
                <Button onClick={() => {
                  handleConfirm(selectedDonation.id);
                  setIsViewDialogOpen(false);
                }}>
                  <CheckCircle className="mr-2 h-4 w-4" />
                  Confirm
                </Button>
              </>
            )}
            {selectedDonation?.status !== 'pending' && (
              <Button variant="outline" onClick={() => setIsViewDialogOpen(false)}>Close</Button>
            )}
          </DialogFooter>
        </DialogContent>
      </Dialog>

      {/* Reject Donation Dialog */}
      <Dialog open={isRejectDialogOpen} onOpenChange={setIsRejectDialogOpen}>
        <DialogContent>
          <DialogHeader>
            <DialogTitle>Reject Donation</DialogTitle>
            <DialogDescription>
              Please provide a reason for rejecting this donation
            </DialogDescription>
          </DialogHeader>
          <div className="space-y-4 py-4">
            <div className="space-y-2">
              <Label htmlFor="reason">Rejection Reason *</Label>
              <Textarea
                id="reason"
                value={rejectReason}
                onChange={(e) => setRejectReason(e.target.value)}
                placeholder="e.g., Invalid proof of payment, duplicate submission, etc."
                rows={4}
              />
            </div>
          </div>
          <DialogFooter>
            <Button 
              variant="outline" 
              onClick={() => {
                setIsRejectDialogOpen(false);
                setRejectReason("");
              }}
              disabled={submitting}
            >
              Cancel
            </Button>
            <Button 
              variant="destructive" 
              onClick={handleReject}
              disabled={submitting}
            >
              {submitting ? "Rejecting..." : "Confirm Rejection"}
            </Button>
          </DialogFooter>
        </DialogContent>
      </Dialog>
    </div>
  );
}

import { useEffect, useState } from "react";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Badge } from "@/components/ui/badge";
import { Checkbox } from "@/components/ui/checkbox";
import { Skeleton } from "@/components/ui/skeleton";
import {
  Table,
  TableBody,
  TableCell,
  TableHead,
  TableHeader,
  TableRow,
} from "@/components/ui/table";
import {
  Dialog,
  DialogContent,
  DialogDescription,
  DialogFooter,
  DialogHeader,
  DialogTitle,
} from "@/components/ui/dialog";
import { Textarea } from "@/components/ui/textarea";
import { Label } from "@/components/ui/label";
import {
  listDonations,
  confirmDonation,
  rejectDonation,
  bulkConfirmDonations,
  bulkRejectDonations,
} from "@/services/apiCharity";
import type { Donation } from "@/types/charity";
import { CheckCircle, XCircle, Eye } from "lucide-react";
import { toast } from "@/hooks/use-toast";
import DonationDetail from "@/components/charity/DonationDetail";

/**
 * Donations Inbox Page
 * Table of donations with inline actions and bulk operations
 */
const DonationsInboxPage = () => {
  const [donations, setDonations] = useState<Donation[]>([]);
  const [loading, setLoading] = useState(true);
  const [page, setPage] = useState(1);
  const [totalPages, setTotalPages] = useState(1);
  const [selectedIds, setSelectedIds] = useState<string[]>([]);
  const [detailDrawerOpen, setDetailDrawerOpen] = useState(false);
  const [selectedDonation, setSelectedDonation] = useState<Donation | null>(null);
  const [rejectDialogOpen, setRejectDialogOpen] = useState(false);
  const [rejectReason, setRejectReason] = useState("");
  const [bulkRejectDialogOpen, setBulkRejectDialogOpen] = useState(false);
  const [bulkRejectReason, setBulkRejectReason] = useState("");

  useEffect(() => {
    loadDonations();
  }, [page]);

  const loadDonations = async () => {
    try {
      setLoading(true);
      const response = await listDonations({
        page,
        pageSize: 20,
        status: "pending",
        sortBy: "submittedAt",
        sortOrder: "desc",
      });
      setDonations(response.data);
      setTotalPages(response.pagination.totalPages);
    } catch (err) {
      toast({
        title: "Error",
        description: err instanceof Error ? err.message : "Failed to load donations",
        variant: "destructive",
      });
    } finally {
      setLoading(false);
    }
  };

  const handleConfirm = async (id: string) => {
    try {
      await confirmDonation(id, {});
      toast({ title: "Success", description: "Donation confirmed" });
      loadDonations();
    } catch (err) {
      toast({
        title: "Error",
        description: err instanceof Error ? err.message : "Failed to confirm",
        variant: "destructive",
      });
    }
  };

  const handleReject = async () => {
    if (!selectedDonation || !rejectReason.trim()) {
      toast({
        title: "Error",
        description: "Please provide a reason",
        variant: "destructive",
      });
      return;
    }
    try {
      await rejectDonation(selectedDonation.id, { reason: rejectReason });
      toast({ title: "Success", description: "Donation rejected" });
      setRejectDialogOpen(false);
      setRejectReason("");
      setSelectedDonation(null);
      loadDonations();
    } catch (err) {
      toast({
        title: "Error",
        description: err instanceof Error ? err.message : "Failed to reject",
        variant: "destructive",
      });
    }
  };

  const handleBulkConfirm = async () => {
    if (selectedIds.length === 0) return;
    try {
      await bulkConfirmDonations(selectedIds);
      toast({
        title: "Success",
        description: `${selectedIds.length} donations confirmed`,
      });
      setSelectedIds([]);
      loadDonations();
    } catch (err) {
      toast({
        title: "Error",
        description: err instanceof Error ? err.message : "Bulk confirm failed",
        variant: "destructive",
      });
    }
  };

  const handleBulkReject = async () => {
    if (selectedIds.length === 0 || !bulkRejectReason.trim()) {
      toast({
        title: "Error",
        description: "Please provide a reason",
        variant: "destructive",
      });
      return;
    }
    try {
      await bulkRejectDonations(selectedIds, bulkRejectReason);
      toast({
        title: "Success",
        description: `${selectedIds.length} donations rejected`,
      });
      setBulkRejectDialogOpen(false);
      setBulkRejectReason("");
      setSelectedIds([]);
      loadDonations();
    } catch (err) {
      toast({
        title: "Error",
        description: err instanceof Error ? err.message : "Bulk reject failed",
        variant: "destructive",
      });
    }
  };

  const handleSelectAll = (checked: boolean) => {
    if (checked) {
      setSelectedIds(donations.map((d) => d.id));
    } else {
      setSelectedIds([]);
    }
  };

  const handleSelectOne = (id: string, checked: boolean) => {
    if (checked) {
      setSelectedIds([...selectedIds, id]);
    } else {
      setSelectedIds(selectedIds.filter((selectedId) => selectedId !== id));
    }
  };

  if (loading && donations.length === 0) {
    return (
      <div className="p-lg">
        <Card>
          <CardHeader>
            <Skeleton className="h-8 w-48" />
          </CardHeader>
          <CardContent>
            <Skeleton className="h-64 w-full" />
          </CardContent>
        </Card>
      </div>
    );
  }

  return (
    <div className="p-lg space-y-lg">
      <Card>
        <CardHeader>
          <CardTitle>Donations Inbox</CardTitle>
        </CardHeader>
        <CardContent className="space-y-md">
          {/* Bulk Actions */}
          {selectedIds.length > 0 && (
            <div className="flex items-center gap-md p-md bg-muted rounded-md">
              <span className="text-sm font-medium">
                {selectedIds.length} selected
              </span>
              <Button variant="outline" size="sm" onClick={handleBulkConfirm}>
                <CheckCircle className="h-4 w-4 mr-xs" />
                Confirm All
              </Button>
              <Button
                variant="outline"
                size="sm"
                onClick={() => setBulkRejectDialogOpen(true)}
              >
                <XCircle className="h-4 w-4 mr-xs" />
                Reject All
              </Button>
            </div>
          )}

          {/* Table */}
          <div className="border rounded-md">
            <Table>
              <TableHeader>
                <TableRow>
                  <TableHead className="w-12">
                    <Checkbox
                      checked={
                        selectedIds.length === donations.length &&
                        donations.length > 0
                      }
                      onCheckedChange={handleSelectAll}
                    />
                  </TableHead>
                  <TableHead>Donor</TableHead>
                  <TableHead>Amount</TableHead>
                  <TableHead>Method</TableHead>
                  <TableHead>Submitted</TableHead>
                  <TableHead>Checksum</TableHead>
                  <TableHead className="text-right">Actions</TableHead>
                </TableRow>
              </TableHeader>
              <TableBody>
                {donations.length === 0 ? (
                  <TableRow>
                    <TableCell colSpan={7} className="text-center py-lg">
                      <p className="text-muted-foreground">No pending donations</p>
                    </TableCell>
                  </TableRow>
                ) : (
                  donations.map((donation) => (
                    <TableRow key={donation.id}>
                      <TableCell>
                        <Checkbox
                          checked={selectedIds.includes(donation.id)}
                          onCheckedChange={(checked) =>
                            handleSelectOne(donation.id, checked as boolean)
                          }
                        />
                      </TableCell>
                      <TableCell>
                        <div>
                          <p className="font-medium">{donation.donorName}</p>
                          <p className="text-xs text-muted-foreground">
                            {donation.donorEmail}
                          </p>
                        </div>
                      </TableCell>
                      <TableCell className="font-medium">
                        ${donation.amount.toLocaleString()}
                      </TableCell>
                      <TableCell>
                        <Badge variant="outline">{donation.method}</Badge>
                      </TableCell>
                      <TableCell className="text-sm text-muted-foreground">
                        {new Date(donation.submittedAt).toLocaleString()}
                      </TableCell>
                      <TableCell>
                        {donation.proofChecksum ? (
                          <div className="flex items-center gap-xs">
                            <span className="text-xs font-mono">
                              {donation.proofChecksum.slice(0, 8)}...
                            </span>
                            {donation.checksumVerified && (
                              <CheckCircle className="h-3 w-3 text-success" />
                            )}
                          </div>
                        ) : (
                          <span className="text-xs text-muted-foreground">N/A</span>
                        )}
                      </TableCell>
                      <TableCell>
                        <div className="flex justify-end gap-xs">
                          <Button
                            variant="ghost"
                            size="sm"
                            onClick={() => {
                              setSelectedDonation(donation);
                              setDetailDrawerOpen(true);
                            }}
                          >
                            <Eye className="h-4 w-4" />
                          </Button>
                          <Button
                            variant="ghost"
                            size="sm"
                            onClick={() => handleConfirm(donation.id)}
                          >
                            <CheckCircle className="h-4 w-4 text-success" />
                          </Button>
                          <Button
                            variant="ghost"
                            size="sm"
                            onClick={() => {
                              setSelectedDonation(donation);
                              setRejectDialogOpen(true);
                            }}
                          >
                            <XCircle className="h-4 w-4 text-destructive" />
                          </Button>
                        </div>
                      </TableCell>
                    </TableRow>
                  ))
                )}
              </TableBody>
            </Table>
          </div>

          {/* Pagination */}
          <div className="flex items-center justify-between">
            <p className="text-sm text-muted-foreground">
              Page {page} of {totalPages}
            </p>
            <div className="flex gap-sm">
              <Button
                variant="outline"
                size="sm"
                disabled={page === 1}
                onClick={() => setPage(page - 1)}
              >
                Previous
              </Button>
              <Button
                variant="outline"
                size="sm"
                disabled={page === totalPages}
                onClick={() => setPage(page + 1)}
              >
                Next
              </Button>
            </div>
          </div>
        </CardContent>
      </Card>

      {/* Detail Drawer */}
      {selectedDonation && (
        <DonationDetail
          donationId={selectedDonation.id}
          open={detailDrawerOpen}
          onOpenChange={setDetailDrawerOpen}
          onUpdate={loadDonations}
        />
      )}

      {/* Reject Dialog */}
      <Dialog open={rejectDialogOpen} onOpenChange={setRejectDialogOpen}>
        <DialogContent>
          <DialogHeader>
            <DialogTitle>Reject Donation</DialogTitle>
            <DialogDescription>
              Please provide a reason for rejecting this donation.
            </DialogDescription>
          </DialogHeader>
          <div className="space-y-md py-md">
            <div>
              <Label htmlFor="reject-reason">Reason</Label>
              <Textarea
                id="reject-reason"
                value={rejectReason}
                onChange={(e) => setRejectReason(e.target.value)}
                placeholder="Enter rejection reason..."
                className="mt-sm"
              />
            </div>
          </div>
          <DialogFooter>
            <Button variant="outline" onClick={() => setRejectDialogOpen(false)}>
              Cancel
            </Button>
            <Button onClick={handleReject}>Reject</Button>
          </DialogFooter>
        </DialogContent>
      </Dialog>

      {/* Bulk Reject Dialog */}
      <Dialog open={bulkRejectDialogOpen} onOpenChange={setBulkRejectDialogOpen}>
        <DialogContent>
          <DialogHeader>
            <DialogTitle>Reject {selectedIds.length} Donations</DialogTitle>
            <DialogDescription>
              Please provide a reason for rejecting these donations.
            </DialogDescription>
          </DialogHeader>
          <div className="space-y-md py-md">
            <div>
              <Label htmlFor="bulk-reject-reason">Reason</Label>
              <Textarea
                id="bulk-reject-reason"
                value={bulkRejectReason}
                onChange={(e) => setBulkRejectReason(e.target.value)}
                placeholder="Enter rejection reason..."
                className="mt-sm"
              />
            </div>
          </div>
          <DialogFooter>
            <Button
              variant="outline"
              onClick={() => setBulkRejectDialogOpen(false)}
            >
              Cancel
            </Button>
            <Button onClick={handleBulkReject}>Reject All</Button>
          </DialogFooter>
        </DialogContent>
      </Dialog>
    </div>
  );
};

