import { useState, useEffect } from "react";
import { Search, Heart, MapPin, CheckCircle, Eye, Filter, SortAsc, UserPlus, UserMinus } from "lucide-react";
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Badge } from "@/components/ui/badge";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { Dialog, DialogContent, DialogDescription, DialogHeader, DialogTitle } from "@/components/ui/dialog";
import { Tabs, TabsContent, TabsList, TabsTrigger } from "@/components/ui/tabs";
import { useNavigate } from "react-router-dom";
import { toast } from "sonner";
import { authService } from "@/services/auth";

interface Charity {
  id: number;
  name: string;
  mission?: string;
  vision?: string;
  category?: string;
  region?: string;
  municipality?: string;
  logo_path?: string;
  verification_status: string;
  created_at: string;
}

interface ApiResponse {
  charities: {
    data: Charity[];
    current_page: number;
    last_page: number;
    per_page: number;
    total: number;
  };
  filters: {
    categories: string[];
    regions: string[];
  };
}

export default function BrowseCharities() {
  const navigate = useNavigate();
  const [searchTerm, setSearchTerm] = useState("");
  const [categoryFilter, setCategoryFilter] = useState("all");
  const [regionFilter, setRegionFilter] = useState("all");
  const [sortBy, setSortBy] = useState("name");
  const [selectedCharity, setSelectedCharity] = useState<Charity | null>(null);
  const [isDetailsOpen, setIsDetailsOpen] = useState(false);
  const [charities, setCharities] = useState<Charity[]>([]);
  const [filters, setFilters] = useState({ categories: [], regions: [] });

  // Handle donation navigation
  const handleDonate = (charityId: number) => {
    navigate(`/donor/donate/${charityId}`);
  };
  const [loading, setLoading] = useState(true);
  const [currentPage, setCurrentPage] = useState(1);
  const [totalPages, setTotalPages] = useState(1);

  // Add missing reset function to avoid runtime ReferenceError
  const resetFilters = () => {
    setSearchTerm("");
    setCategoryFilter("all");
    setRegionFilter("all");
    setSortBy("name");
    setCurrentPage(1);
  };

  useEffect(() => {
    fetchCharities();
  }, [searchTerm, categoryFilter, regionFilter, sortBy, currentPage]);

  const fetchCharities = async () => {
    try {
      setLoading(true);
      const params = new URLSearchParams({
        page: currentPage.toString(),
        ...(searchTerm && { q: searchTerm }),
        ...(categoryFilter !== 'all' && { category: categoryFilter }),
        ...(regionFilter !== 'all' && { region: regionFilter }),
        ...(sortBy && { sort: sortBy }),
      });

      const response = await fetch(`${import.meta.env.VITE_API_URL}/api/charities?${params}`);
      if (!response.ok) throw new Error('Failed to fetch charities');

      const data: ApiResponse = await response.json();
      setCharities(data.charities.data || []);
      setFilters(data.filters || { categories: [], regions: [] });
      setTotalPages(data.charities.last_page);
    } catch (error) {
      console.error('Error fetching charities:', error);
      toast.error('Failed to load charities');
      setCharities([]);
      setFilters({ categories: [], regions: [] });
    } finally {
      setLoading(false);
    }
  };

  const handleViewDetails = (charity: Charity) => {
    setSelectedCharity(charity);
    setIsDetailsOpen(true);
  };

  const [followStatus, setFollowStatus] = useState<{[key: number]: boolean}>({});
  const [followedCharities, setFollowedCharities] = useState<number[]>([]);

  useEffect(() => {
    if (charities.length > 0) {
      fetchFollowStatuses();
    }
  }, [charities]);

  const fetchFollowStatuses = async () => {
    try {
      const token = authService.getToken();
      if (!token) return;

      const statusPromises = charities.map(async (charity) => {
        try {
          const response = await fetch(`${import.meta.env.VITE_API_URL}/api/charities/${charity.id}/follow-status`, {
            headers: {
              'Authorization': `Bearer ${token}`,
            },
          });
          if (response.ok) {
            const data = await response.json();
            return { charityId: charity.id, isFollowing: data.is_following };
          }
        } catch (error) {
          console.error(`Error fetching follow status for charity ${charity.id}:`, error);
        }
        return { charityId: charity.id, isFollowing: false };
      });

      const statuses = await Promise.all(statusPromises);
      const statusMap: {[key: number]: boolean} = {};
      statuses.forEach(status => {
        statusMap[status.charityId] = status.isFollowing;
      });
      setFollowStatus(statusMap);
    } catch (error) {
      console.error('Error fetching follow statuses:', error);
    }
  };

  const handleToggleFollow = async (charityId: number) => {
    try {
      const token = authService.getToken();
      if (!token) {
        toast.error('Please login to follow charities');
        return;
      }

      const response = await fetch(`${import.meta.env.VITE_API_URL}/api/charities/${charityId}/follow`, {
        method: 'POST',
        headers: {
          'Authorization': `Bearer ${token}`,
        },
      });

      if (!response.ok) {
        const errorData = await response.json();
        throw new Error(errorData.message || 'Failed to update follow status');
      }

      const data = await response.json();
      setFollowStatus(prev => ({
        ...prev,
        [charityId]: data.is_following
      }));

      toast.success(data.message);

    } catch (error) {
      console.error('Error toggling follow:', error);
      toast.error(error instanceof Error ? error.message : 'Failed to update follow status');
    }
  };

  return (
    <div>
      {/* Hero Section */}
      <div className="bg-gradient-to-r from-primary/10 via-primary/5 to-background border-b">
        <div className="max-w-7xl mx-auto px-4 py-12">
          <h1 className="text-4xl font-bold mb-4">Browse Charities</h1>
          <p className="text-xl text-muted-foreground mb-8">
            Discover verified organizations making a difference
          </p>

          {/* Search and Filter */}
          <div className="flex flex-col gap-4">
            <div className="flex gap-4">
              <div className="relative flex-1">
                <Search className="absolute left-3 top-1/2 -translate-y-1/2 h-4 w-4 text-muted-foreground" />
                <Input
                  placeholder="Search charities..."
                  value={searchTerm}
                  onChange={(e) => setSearchTerm(e.target.value)}
                  className="pl-9"
                />
              </div>
              <Select value={categoryFilter} onValueChange={setCategoryFilter}>
                <SelectTrigger className="w-[200px]">
                  <SelectValue placeholder="Category" />
                </SelectTrigger>
                <SelectContent>
                  <SelectItem value="all">All Categories</SelectItem>
                  {filters.categories && filters.categories.map(cat => (
                    <SelectItem key={cat} value={cat}>{cat}</SelectItem>
                  ))}
                </SelectContent>
              </Select>
              <Select value={regionFilter} onValueChange={setRegionFilter}>
                <SelectTrigger className="w-[200px]">
                  <SelectValue placeholder="Region" />
                </SelectTrigger>
                <SelectContent>
                  <SelectItem value="all">All Regions</SelectItem>
                  {filters.regions && filters.regions.map(region => (
                    <SelectItem key={region} value={region}>{region}</SelectItem>
                  ))}
                </SelectContent>
              </Select>
              <Select value={sortBy} onValueChange={setSortBy}>
                <SelectTrigger className="w-[200px]">
                  <SelectValue placeholder="Sort by" />
                </SelectTrigger>
                <SelectContent>
                  <SelectItem value="name">Name</SelectItem>
                  <SelectItem value="newest">Newest</SelectItem>
                  <SelectItem value="total_received">Most Raised</SelectItem>
                </SelectContent>
              </Select>
            </div>
            <div className="flex justify-between items-center">
              <Button variant="outline" onClick={resetFilters}>
                <Filter className="mr-2 h-4 w-4" />
                Reset Filters
              </Button>
            </div>
          </div>
        </div>
      </div>

      {/* Main Content */}
      <div className="max-w-7xl mx-auto px-4 py-12">
        {/* Results Count and Loading */}
        <div className="mb-6 flex justify-between items-center">
          <p className="text-muted-foreground">
            {loading ? 'Loading...' : `Showing ${charities.length} of ${filters.categories.length > 0 ? 'many' : '0'} charities`}
          </p>
          {charities.length > 0 && (
            <div className="flex gap-2">
              <Button
                variant="outline"
                size="sm"
                onClick={() => setCurrentPage(prev => Math.max(1, prev - 1))}
                disabled={currentPage === 1}
              >
                Previous
              </Button>
              <span className="px-3 py-2 text-sm">
                Page {currentPage} of {totalPages}
              </span>
              <Button
                variant="outline"
                size="sm"
                onClick={() => setCurrentPage(prev => Math.min(totalPages, prev + 1))}
                disabled={currentPage === totalPages}
              >
                Next
              </Button>
            </div>
          )}
        </div>

        {/* Charities Grid */}
        {loading ? (
          <div className="grid gap-6 md:grid-cols-2 lg:grid-cols-3">
            {[...Array(6)].map((_, i) => (
              <Card key={i} className="animate-pulse">
                <div className="h-48 bg-muted rounded-t-lg"></div>
                <CardContent className="p-6">
                  <div className="h-4 bg-muted rounded mb-2"></div>
                  <div className="h-3 bg-muted rounded mb-4"></div>
                  <div className="h-8 bg-muted rounded"></div>
                </CardContent>
              </Card>
            ))}
          </div>
        ) : (
          <div className="grid gap-6 md:grid-cols-2 lg:grid-cols-3">
            {charities.map((charity) => (
              <Card key={charity.id} className="overflow-hidden hover:shadow-lg transition-shadow">
                <div className="h-48 overflow-hidden relative">
                  <img
                    src={charity.logo_path ? `${import.meta.env.VITE_API_URL}/storage/${charity.logo_path}` : "https://images.unsplash.com/photo-1488521787991-ed7bbaae773c?w=600"}
                    alt={charity.name}
                    className="w-full h-full object-cover"
                  />
                  {charity.verification_status === 'approved' && (
                    <Badge className="absolute top-2 right-2 bg-green-600">
                      <CheckCircle className="mr-1 h-3 w-3" />
                      Verified
                    </Badge>
                  )}
                </div>
                <CardHeader>
                  <CardTitle className="text-xl">{charity.name}</CardTitle>
                  <CardDescription className="line-clamp-2">
                    {charity.mission || 'Making a difference in our community'}
                  </CardDescription>
                </CardHeader>
                <CardContent className="space-y-4">
                  <div className="flex items-center gap-2 text-sm text-muted-foreground">
                    <MapPin className="h-4 w-4" />
                    {charity.region && charity.municipality ? `${charity.municipality}, ${charity.region}` : charity.region || 'Location not specified'}
                  </div>
                  {charity.category && (
                    <Badge variant="outline">{charity.category}</Badge>
                  )}
                  <div className="text-sm">
                    <p className="text-muted-foreground">Verified</p>
                    <p className="text-lg font-bold text-green-600">
                      {charity.verification_status === 'approved' ? ' Approved' : ' Pending'}
                    </p>
                  </div>
                  <div className="flex gap-2">
                    <Button
                      className="flex-1"
                      onClick={() => handleDonate(charity.id)}
                    >
                      <Heart className="mr-2 h-4 w-4" />
                      Donate
                    </Button>
                    <Button
                      variant={followStatus[charity.id] ? "default" : "outline"}
                      onClick={() => handleToggleFollow(charity.id)}
                    >
                      {followStatus[charity.id] ? (
                        <>
                          <UserMinus className="mr-2 h-4 w-4" />
                          Unfollow
                        </>
                      ) : (
                        <>
                          <UserPlus className="mr-2 h-4 w-4" />
                          Follow
                        </>
                      )}
                    </Button>
                    <Button
                      variant="outline"
                      onClick={() => navigate(`/donor/charities/${charity.id}`)}
                    >
                      <Eye className="h-4 w-4" />
                    </Button>
                  </div>
                </CardContent>
              </Card>
            ))}
          </div>
        )}

        {/* Empty State */}
        {!loading && charities.length === 0 && (
          <Card className="p-12 text-center">
            <p className="text-muted-foreground">
              No charities found matching your criteria. Try adjusting your search or filters.
            </p>
          </Card>
        )}
      </div>

      {/* Charity Details Dialog */}
      <Dialog open={isDetailsOpen} onOpenChange={setIsDetailsOpen}>
        <DialogContent className="max-w-3xl max-h-[90vh] overflow-y-auto">
          <DialogHeader>
            <DialogTitle className="text-2xl">{selectedCharity?.name}</DialogTitle>
            <DialogDescription>
              {selectedCharity?.verification_status === 'approved' && (
                <Badge className="bg-green-600 mt-2">
                  <CheckCircle className="mr-1 h-3 w-3" />
                  Verified Organization
                </Badge>
              )}
            </DialogDescription>
          </DialogHeader>
          {selectedCharity && (
            <div className="space-y-6">
              <img
                src={selectedCharity.logo_path ? `${import.meta.env.VITE_API_URL}/storage/${selectedCharity.logo_path}` : "https://images.unsplash.com/photo-1488521787991-ed7bbaae773c?w=600"}
                alt={selectedCharity.name}
                className="w-full h-64 object-cover rounded-lg"
              />

              <Tabs defaultValue="about" className="w-full">
                <TabsList className="grid w-full grid-cols-3">
                  <TabsTrigger value="about">About</TabsTrigger>
                  <TabsTrigger value="impact">Impact</TabsTrigger>
                  <TabsTrigger value="contact">Contact</TabsTrigger>
                </TabsList>

                <TabsContent value="about" className="space-y-4 mt-4">
                  <div>
                    <h3 className="font-semibold mb-2">Mission</h3>
                    <p className="text-muted-foreground">{selectedCharity.mission || 'Mission statement not available'}</p>
                  </div>
                  <div>
                    <h3 className="font-semibold mb-2">Vision</h3>
                    <p className="text-muted-foreground">{selectedCharity.vision || 'Vision statement not available'}</p>
                  </div>
                  <div className="grid grid-cols-2 gap-4">
                    {selectedCharity.category && (
                      <div>
                        <h3 className="font-semibold mb-2">Category</h3>
                        <Badge variant="outline">{selectedCharity.category}</Badge>
                      </div>
                    )}
                    <div>
                      <h3 className="font-semibold mb-2">Location</h3>
                      <p className="text-muted-foreground flex items-center gap-1">
                        <MapPin className="h-4 w-4" />
                        {selectedCharity.region && selectedCharity.municipality ? `${selectedCharity.municipality}, ${selectedCharity.region}` : selectedCharity.region || 'Location not specified'}
                      </p>
                    </div>
                  </div>
                </TabsContent>

                <TabsContent value="impact" className="space-y-4 mt-4">
                  <div>
                    <h3 className="font-semibold mb-2">Verification Status</h3>
                    <p className="text-muted-foreground">
                      {selectedCharity.verification_status === 'approved'
                        ? 'This charity has been verified and approved by our administrators.'
                        : 'This charity is pending verification.'}
                    </p>
                  </div>
                  <div>
                    <h3 className="font-semibold mb-2">Member Since</h3>
                    <p className="text-muted-foreground">
                      {new Date(selectedCharity.created_at).toLocaleDateString()}
                    </p>
                  </div>
                </TabsContent>

                <TabsContent value="contact" className="space-y-4 mt-4">
                  <div>
                    <h3 className="font-semibold mb-2">Contact Information</h3>
                    <p className="text-muted-foreground">
                      Contact details are available to verified donors.
                    </p>
                  </div>
                </TabsContent>
              </Tabs>

              <div className="flex gap-2 pt-4 border-t">
                <Button className="flex-1" onClick={() => {
                  handleDonate(selectedCharity.id);
                  setIsDetailsOpen(false);
                }}>
                  <Heart className="mr-2 h-4 w-4" />
                  Donate Now
                </Button>
                <Button
                  variant={followStatus[selectedCharity.id] ? "default" : "outline"}
                  onClick={() => {
                    handleToggleFollow(selectedCharity.id);
                    setIsDetailsOpen(false);
                  }}
                >
                  {followStatus[selectedCharity.id] ? (
                    <>
                      <UserMinus className="mr-2 h-4 w-4" />
                      Unfollow
                    </>
                  ) : (
                    <>
                      <UserPlus className="mr-2 h-4 w-4" />
                      Follow
                    </>
                  )}
                </Button>
                <Button variant="outline" onClick={() => setIsDetailsOpen(false)}>
                  Close
                </Button>
              </div>
            </div>
          )}
        </DialogContent>
      </Dialog>
    </div>
  );
}

import { useState, useEffect } from "react";
import { useParams, useNavigate } from "react-router-dom";
import { Heart, MessageCircle, Share2, MapPin, Globe, Phone, Mail, Calendar, Award, Users, TrendingUp, FileText } from "lucide-react";
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Badge } from "@/components/ui/badge";
import { Avatar, AvatarFallback, AvatarImage } from "@/components/ui/avatar";
import { Tabs, TabsContent, TabsList, TabsTrigger } from "@/components/ui/tabs";
import { toast } from "sonner";
import { authService } from "@/services/auth";

interface CharityProfile {
  id: number;
  name: string;
  acronym?: string;
  mission: string;
  vision?: string;
  goals?: string;
  category?: string;
  region?: string;
  municipality?: string;
  logo_path?: string;
  cover_image?: string;
  website?: string;
  contact_email?: string;
  contact_phone?: string;
  address?: string;
  verification_status: string;
  verified_at?: string;
  total_received?: number;
  documents?: any[];
}

interface Post {
  id: number;
  title: string;
  content: string;
  image_path?: string;
  status: 'draft' | 'published';
  published_at?: string;
  created_at: string;
  charity?: {
    id: number;
    name: string;
    logo_path?: string;
  };
}

export default function CharityProfile() {
  const { id } = useParams<{ id: string }>();
  const navigate = useNavigate();
  const [charity, setCharity] = useState<CharityProfile | null>(null);
  const [posts, setPosts] = useState<Post[]>([]);
  const [loading, setLoading] = useState(true);
  const [isFollowing, setIsFollowing] = useState(false);

  useEffect(() => {
    if (id) {
      loadCharityProfile();
      loadCharityPosts();
    }
  }, [id]);

  const loadCharityProfile = async () => {
    try {
      const response = await fetch(`${import.meta.env.VITE_API_URL}/api/charities/${id}`);
      if (response.ok) {
        const data = await response.json();
        setCharity(data);
      }
    } catch (error) {
      console.error('Error loading charity profile:', error);
      toast.error('Failed to load charity profile');
    }
  };

  const loadCharityPosts = async () => {
    try {
      const response = await fetch(`${import.meta.env.VITE_API_URL}/api/charities/${id}/posts`);
      if (response.ok) {
        const data = await response.json();
        setPosts(data.data || []);
      }
    } catch (error) {
      console.error('Error loading charity posts:', error);
    } finally {
      setLoading(false);
    }
  };

  const handleFollowToggle = async () => {
    try {
      const token = authService.getToken();
      if (!token) {
        toast.error('Please login to follow charities');
        return;
      }

      const response = await fetch(`${import.meta.env.VITE_API_URL}/api/charities/${id}/follow`, {
        method: 'POST',
        headers: { Authorization: `Bearer ${token}` }
      });

      if (response.ok) {
        const data = await response.json();
        setIsFollowing(data.is_following);
        toast.success(data.message);
      }
    } catch (error) {
      console.error('Error toggling follow:', error);
      toast.error('Failed to update follow status');
    }
  };

  if (loading) {
    return (
      <div className="min-h-screen flex items-center justify-center">
        <div className="text-center">
          <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-primary mx-auto mb-4"></div>
          <p className="text-muted-foreground">Loading charity profile...</p>
        </div>
      </div>
    );
  }

  if (!charity) {
    return (
      <div className="min-h-screen flex items-center justify-center">
        <div className="text-center">
          <h1 className="text-2xl font-bold text-muted-foreground mb-2">Charity Not Found</h1>
          <p className="text-muted-foreground">The charity you're looking for doesn't exist or isn't available.</p>
          <Button className="mt-4" onClick={() => navigate('/donor/charities')}>
            Back to Charities
          </Button>
        </div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-background">
      {/* Cover Image */}
      <div className="relative h-64 md:h-80 bg-gradient-to-r from-primary/20 to-secondary/20">
        {charity.cover_image && (
          <img
            src={`${import.meta.env.VITE_API_URL}/storage/${charity.cover_image}`}
            alt="Cover"
            className="w-full h-full object-cover"
          />
        )}
        <div className="absolute inset-0 bg-black/20"></div>
      </div>

      {/* Profile Header */}
      <div className="relative px-4 pb-6">
        <div className="max-w-6xl mx-auto">
          <div className="flex flex-col md:flex-row gap-6 -mt-16 md:-mt-20">
            {/* Avatar */}
            <div className="flex-shrink-0">
              <div className="w-32 h-32 md:w-40 md:h-40 rounded-full border-4 border-background bg-background p-2">
                {charity.logo_path ? (
                  <img
                    src={`${import.meta.env.VITE_API_URL}/storage/${charity.logo_path}`}
                    alt={charity.name}
                    className="w-full h-full rounded-full object-cover"
                  />
                ) : (
                  <div className="w-full h-full rounded-full bg-muted flex items-center justify-center">
                    <span className="text-2xl font-bold text-muted-foreground">
                      {charity.acronym || charity.name.charAt(0)}
                    </span>
                  </div>
                )}
              </div>
            </div>

            {/* Charity Info */}
            <div className="flex-1 pt-4 md:pt-8">
              <div className="flex flex-col md:flex-row md:items-center justify-between gap-4">
                <div>
                  <h1 className="text-2xl md:text-3xl font-bold">{charity.name}</h1>
                  {charity.acronym && (
                    <p className="text-lg text-muted-foreground">{charity.acronym}</p>
                  )}
                  <div className="flex items-center gap-2 mt-2">
                    {charity.verification_status === 'approved' && (
                      <Badge className="bg-green-600">
                        <Award className="w-3 h-3 mr-1" />
                        Verified
                      </Badge>
                    )}
                    {charity.category && (
                      <Badge variant="outline">{charity.category}</Badge>
                    )}
                  </div>
                </div>

                <div className="flex gap-2">
                  <Button variant="outline" onClick={handleFollowToggle}>
                    <Heart className={`w-4 h-4 mr-2 ${isFollowing ? 'fill-current text-red-500' : ''}`} />
                    {isFollowing ? 'Following' : 'Follow'}
                  </Button>
                  <Button variant="outline">
                    <Share2 className="w-4 h-4 mr-2" />
                    Share
                  </Button>
                </div>
              </div>

              {/* Stats */}
              <div className="grid grid-cols-3 gap-4 mt-6">
                <div className="text-center">
                  <div className="text-2xl font-bold text-green-600">
                    {charity.total_received?.toLocaleString() || '0'}
                  </div>
                  <div className="text-sm text-muted-foreground">Total Raised</div>
                </div>
                <div className="text-center">
                  <div className="text-2xl font-bold">0</div>
                  <div className="text-sm text-muted-foreground">Active Campaigns</div>
                </div>
                <div className="text-center">
                  <div className="text-2xl font-bold">0</div>
                  <div className="text-sm text-muted-foreground">Followers</div>
                </div>
              </div>
            </div>
          </div>

          {/* Mission & Vision */}
          <div className="mt-8 grid md:grid-cols-2 gap-6">
            <Card>
              <CardHeader>
                <CardTitle className="flex items-center gap-2">
                  <Award className="w-5 h-5" />
                  Mission
                </CardTitle>
              </CardHeader>
              <CardContent>
                <p className="text-muted-foreground">{charity.mission}</p>
              </CardContent>
            </Card>

            {charity.vision && (
              <Card>
                <CardHeader>
                  <CardTitle className="flex items-center gap-2">
                    <TrendingUp className="w-5 h-5" />
                    Vision
                  </CardTitle>
                </CardHeader>
                <CardContent>
                  <p className="text-muted-foreground">{charity.vision}</p>
                </CardContent>
              </Card>
            )}
          </div>

          {/* Contact Info */}
          <Card className="mt-6">
            <CardHeader>
              <CardTitle>Contact Information</CardTitle>
            </CardHeader>
            <CardContent>
              <div className="grid md:grid-cols-2 gap-4">
                {charity.contact_email && (
                  <div className="flex items-center gap-2">
                    <Mail className="w-4 h-4 text-muted-foreground" />
                    <span>{charity.contact_email}</span>
                  </div>
                )}
                {charity.contact_phone && (
                  <div className="flex items-center gap-2">
                    <Phone className="w-4 h-4 text-muted-foreground" />
                    <span>{charity.contact_phone}</span>
                  </div>
                )}
                {charity.website && (
                  <div className="flex items-center gap-2">
                    <Globe className="w-4 h-4 text-muted-foreground" />
                    <a href={charity.website} target="_blank" rel="noopener noreferrer" className="text-primary hover:underline">
                      {charity.website}
                    </a>
                  </div>
                )}
                {charity.address && (
                  <div className="flex items-center gap-2">
                    <MapPin className="w-4 h-4 text-muted-foreground" />
                    <span>{charity.address}</span>
                  </div>
                )}
              </div>
            </CardContent>
          </Card>

          {/* Documents */}
          {charity.documents && charity.documents.length > 0 && (
            <Card className="mt-6">
              <CardHeader>
                <CardTitle className="flex items-center gap-2">
                  <FileText className="w-5 h-5" />
                  Documents & Certificates
                </CardTitle>
              </CardHeader>
              <CardContent>
                <div className="grid gap-3">
                  {charity.documents.map((doc: any) => (
                    <div key={doc.id} className="flex items-center justify-between p-3 border rounded-lg">
                      <div>
                        <p className="font-medium">{doc.doc_type}</p>
                        <p className="text-sm text-muted-foreground">Uploaded {new Date(doc.created_at).toLocaleDateString()}</p>
                      </div>
                      <Button variant="outline" size="sm">
                        <FileText className="w-4 h-4 mr-2" />
                        View
                      </Button>
                    </div>
                  ))}
                </div>
              </CardContent>
            </Card>
          )}

          {/* Posts Tab */}
          <Card className="mt-6">
            <CardHeader>
              <CardTitle>Recent Posts</CardTitle>
              <CardDescription>Latest updates and campaigns from {charity.name}</CardDescription>
            </CardHeader>
            <CardContent>
              {posts.length === 0 ? (
                <div className="text-center py-8 text-muted-foreground">
                  <p>No posts yet. Check back later for updates!</p>
                </div>
              ) : (
                <div className="space-y-4">
                  {posts.slice(0, 5).map((post) => (
                    <Card key={post.id}>
                      <CardHeader>
                        <div className="flex items-center gap-3">
                          <Avatar className="w-8 h-8">
                            <AvatarImage src={charity.logo_path ? `${import.meta.env.VITE_API_URL}/storage/${charity.logo_path}` : undefined} />
                            <AvatarFallback>{charity.acronym || charity.name.charAt(0)}</AvatarFallback>
                          </Avatar>
                          <div>
                            <p className="font-semibold">{charity.name}</p>
                            <p className="text-sm text-muted-foreground">
                              {post.published_at ? new Date(post.published_at).toLocaleDateString() : new Date(post.created_at).toLocaleDateString()}
                            </p>
                          </div>
                        </div>
                      </CardHeader>
                      <CardContent>
                        <h3 className="font-semibold mb-2">{post.title}</h3>
                        <p className="text-muted-foreground line-clamp-3">{post.content}</p>
                        {post.image_path && (
                          <img
                            src={`${import.meta.env.VITE_API_URL}/storage/${post.image_path}`}
                            alt={post.title}
                            className="mt-3 rounded-lg max-h-64 w-full object-cover"
                          />
                        )}
                        <div className="flex items-center gap-4 mt-3 pt-3 border-t">
                          <Button variant="ghost" size="sm">
                            <Heart className="w-4 h-4 mr-2" />
                            Like
                          </Button>
                          <Button variant="ghost" size="sm">
                            <MessageCircle className="w-4 h-4 mr-2" />
                            Comment
                          </Button>
                          <Button variant="ghost" size="sm">
                            <Share2 className="w-4 h-4 mr-2" />
                            Share
                          </Button>
                        </div>
                      </CardContent>
                    </Card>
                  ))}
                </div>
              )}
            </CardContent>
          </Card>
        </div>
      </div>
    </div>
  );
}

import { useEffect, useState } from "react";
import { Download, Eye, Filter, Calendar } from "lucide-react";
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Badge } from "@/components/ui/badge";
import { Table, TableBody, TableCell, TableHead, TableHeader, TableRow } from "@/components/ui/table";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { Dialog, DialogContent, DialogDescription, DialogFooter, DialogHeader, DialogTitle } from "@/components/ui/dialog";
import { Label } from "@/components/ui/label";
import { toast } from "sonner";
import { authService } from "@/services/auth";

interface APIDonation {
  id: number;
  amount: number;
  status: 'pending' | 'scheduled' | 'completed' | 'rejected';
  is_recurring: boolean;
  purpose: 'general' | 'project' | 'emergency';
  donated_at: string;
  receipt_no?: string | null;
  charity: { id: number; name: string };
  campaign?: { id: number; title: string } | null;
}

interface DonationRow {
  id: number;
  charity: string;
  campaign: string;
  amount: number;
  date: string;
  status: 'pending' | 'scheduled' | 'completed' | 'rejected';
  type: 'one-time' | 'recurring';
  hasReceipt: boolean;
}

export default function DonationHistory() {
  const [donations, setDonations] = useState<DonationRow[]>([]);
  const [loading, setLoading] = useState<boolean>(true);

  const [filterStatus, setFilterStatus] = useState("all");
  const [selectedDonation, setSelectedDonation] = useState<DonationRow | null>(null);
  const [isDetailsOpen, setIsDetailsOpen] = useState(false);

  const API_URL = import.meta.env.VITE_API_URL;

  useEffect(() => {
    fetchDonations();
  }, []);

  const fetchDonations = async () => {
    try {
      setLoading(true);
      const token = authService.getToken();
      const res = await fetch(`${API_URL}/api/me/donations`, {
        headers: {
          Accept: 'application/json',
          Authorization: token ? `Bearer ${token}` : ''
        }
      });
      if (!res.ok) throw new Error('Failed to load donations');
      const payload = await res.json();
      const items: APIDonation[] = payload.data ?? payload; // handle paginate or array
      const rows: DonationRow[] = items.map((d) => ({
        id: d.id,
        charity: d.charity?.name ?? 'Unknown Charity',
        campaign: d.campaign?.title ?? 'General Fund',
        amount: d.amount,
        date: d.donated_at ?? new Date().toISOString(),
        status: d.status,
        type: d.is_recurring ? 'recurring' : 'one-time',
        hasReceipt: !!d.receipt_no && d.status === 'completed',
      }));
      setDonations(rows);
    } catch (e) {
      toast.error(e instanceof Error ? e.message : 'Unable to fetch donations');
    } finally {
      setLoading(false);
    }
  };

  const downloadReceipt = async (donationId: number) => {
    try {
      const token = authService.getToken();
      const res = await fetch(`${API_URL}/api/donations/${donationId}/receipt`, {
        headers: {
          Authorization: token ? `Bearer ${token}` : ''
        }
      });
      if (res.status === 422) {
        const data = await res.json();
        throw new Error(data.message || 'Receipt not available');
      }
      if (!res.ok) throw new Error('Failed to download receipt');

      const blob = await res.blob();
      const url = window.URL.createObjectURL(blob);
      const a = document.createElement('a');
      a.href = url;
      a.download = `donation-receipt-${donationId}.pdf`;
      document.body.appendChild(a);
      a.click();
      a.remove();
      window.URL.revokeObjectURL(url);
      toast.success('Receipt download started');
    } catch (e) {
      toast.error(e instanceof Error ? e.message : 'Unable to download receipt');
    }
  };

  const filteredDonations = filterStatus === "all"
    ? donations
    : donations.filter(d => d.status === filterStatus);

  const totalDonated = donations
    .filter(d => d.status === 'completed')
    .reduce((sum, d) => sum + d.amount, 0);

  const getStatusBadge = (status: string) => {
    switch (status) {
      case 'pending':
        return <Badge variant="secondary">Pending</Badge>;
      case 'completed':
        return <Badge className="bg-green-600">Completed</Badge>;
      case 'scheduled':
        return <Badge variant="outline">Scheduled</Badge>;
      case 'rejected':
        return <Badge variant="destructive">Rejected</Badge>;
      default:
        return <Badge>{status}</Badge>;
    }
  };

  const handleViewDetails = (donation: DonationRow) => {
    setSelectedDonation(donation);
    setIsDetailsOpen(true);
  };

  return (
    <div>
      {/* Hero Section */}
      <div className="bg-gradient-to-r from-primary/10 via-primary/5 to-background border-b">
        <div className="max-w-7xl mx-auto px-4 py-12">
          <h1 className="text-4xl font-bold mb-4">Donation History</h1>
          <p className="text-xl text-muted-foreground">
            Track all your contributions and their impact
          </p>
        </div>
      </div>

      {/* Main Content */}
      <div className="max-w-7xl mx-auto px-4 py-12 space-y-6">
        {/* Stats */}
        <div className="grid gap-4 md:grid-cols-4">
          <Card>
            <CardHeader className="pb-2">
              <CardTitle className="text-sm font-medium">Total Donated</CardTitle>
            </CardHeader>
            <CardContent>
              <div className="text-2xl font-bold text-green-600">
                {totalDonated.toLocaleString()}
              </div>
            </CardContent>
          </Card>
          <Card>
            <CardHeader className="pb-2">
              <CardTitle className="text-sm font-medium">Total Donations</CardTitle>
            </CardHeader>
            <CardContent>
              <div className="text-2xl font-bold">{donations.length}</div>
            </CardContent>
          </Card>
          <Card>
            <CardHeader className="pb-2">
              <CardTitle className="text-sm font-medium">Charities Supported</CardTitle>
            </CardHeader>
            <CardContent>
              <div className="text-2xl font-bold">
                {new Set(donations.map(d => d.charity)).size}
              </div>
            </CardContent>
          </Card>
          <Card>
            <CardHeader className="pb-2">
              <CardTitle className="text-sm font-medium">Recurring Donations</CardTitle>
            </CardHeader>
            <CardContent>
              <div className="text-2xl font-bold">
                {donations.filter(d => d.type === 'recurring').length}
              </div>
            </CardContent>
          </Card>
        </div>

        {/* Donations Table */}
        <Card>
          <CardHeader>
            <div className="flex items-center justify-between">
              <div>
                <CardTitle>All Donations</CardTitle>
                <CardDescription>Your complete donation history</CardDescription>
              </div>
              <div className="flex gap-2">
                <Select value={filterStatus} onValueChange={setFilterStatus}>
                  <SelectTrigger className="w-[180px]">
                    <Filter className="mr-2 h-4 w-4" />
                    <SelectValue placeholder="Filter by status" />
                  </SelectTrigger>
                  <SelectContent>
                    <SelectItem value="all">All Status</SelectItem>
                    <SelectItem value="pending">Pending</SelectItem>
                    <SelectItem value="completed">Completed</SelectItem>
                    <SelectItem value="scheduled">Scheduled</SelectItem>
                    <SelectItem value="rejected">Rejected</SelectItem>
                  </SelectContent>
                </Select>
                <Button variant="outline">
                  <Download className="mr-2 h-4 w-4" />
                  Export
                </Button>
              </div>
            </div>
          </CardHeader>
          <CardContent>
            <Table>
              <TableHeader>
                <TableRow>
                  <TableHead>Date</TableHead>
                  <TableHead>Charity</TableHead>
                  <TableHead>Campaign</TableHead>
                  <TableHead>Amount</TableHead>
                  <TableHead>Type</TableHead>
                  <TableHead>Status</TableHead>
                  <TableHead className="text-right">Actions</TableHead>
                </TableRow>
              </TableHeader>
              <TableBody>
                {filteredDonations.map((donation) => (
                  <TableRow key={donation.id}>
                    <TableCell>
                      <div className="flex items-center gap-2">
                        <Calendar className="h-4 w-4 text-muted-foreground" />
                        {new Date(donation.date).toLocaleDateString()}
                      </div>
                    </TableCell>
                    <TableCell className="font-medium">{donation.charity}</TableCell>
                    <TableCell>{donation.campaign}</TableCell>
                    <TableCell className="font-bold text-green-600">
                      {donation.amount.toLocaleString()}
                    </TableCell>
                    <TableCell>
                      <Badge variant="outline" className="capitalize">
                        {donation.type === 'recurring' ? 'Recurring' : 'One-time'}
                      </Badge>
                    </TableCell>
                    <TableCell>{getStatusBadge(donation.status)}</TableCell>
                    <TableCell className="text-right">
                      <div className="flex justify-end gap-2">
                        <Button
                          variant="ghost"
                          size="icon"
                          onClick={() => handleViewDetails(donation)}
                        >
                          <Eye className="h-4 w-4" />
                        </Button>
                        {donation.hasReceipt && (
                          <Button variant="ghost" size="icon" onClick={() => downloadReceipt(donation.id)}>
                            <Download className="h-4 w-4" />
                          </Button>
                        )}
                      </div>
                    </TableCell>
                  </TableRow>
                ))}
              </TableBody>
            </Table>
          </CardContent>
        </Card>

        {/* Empty State */}
        {filteredDonations.length === 0 && (
          <Card className="p-12 text-center">
            <p className="text-muted-foreground">
              No donations found matching your criteria.
            </p>
          </Card>
        )}
      </div>

      {/* Donation Details Dialog */}
      <Dialog open={isDetailsOpen} onOpenChange={setIsDetailsOpen}>
        <DialogContent className="max-w-2xl">
          <DialogHeader>
            <DialogTitle>Donation Details</DialogTitle>
            <DialogDescription>Complete information about your donation</DialogDescription>
          </DialogHeader>
          {selectedDonation && (
            <div className="space-y-4">
              <div className="grid grid-cols-2 gap-4">
                <div>
                  <Label>Charity</Label>
                  <p className="text-sm font-medium mt-1">{selectedDonation.charity}</p>
                </div>
                <div>
                  <Label>Campaign</Label>
                  <p className="text-sm font-medium mt-1">{selectedDonation.campaign}</p>
                </div>
              </div>
              <div className="grid grid-cols-2 gap-4">
                <div>
                  <Label>Amount</Label>
                  <p className="text-lg font-bold text-green-600 mt-1">
                    {selectedDonation.amount.toLocaleString()}
                  </p>
                </div>
                <div>
                  <Label>Date</Label>
                  <p className="text-sm font-medium mt-1">
                    {new Date(selectedDonation.date).toLocaleDateString()}
                  </p>
                </div>
              </div>
              <div className="grid grid-cols-2 gap-4">
                <div>
                  <Label>Type</Label>
                  <Badge variant="outline" className="capitalize mt-1">
                    {selectedDonation.type}
                  </Badge>
                </div>
                <div>
                  <Label>Status</Label>
                  <div className="mt-1">{getStatusBadge(selectedDonation.status)}</div>
                </div>
              </div>
              {selectedDonation.hasReceipt && (
                <div>
                  <Label>Receipt</Label>
                  <div className="mt-2">
                    <Button variant="outline" size="sm" onClick={() => downloadReceipt(selectedDonation.id)}>
                      <Download className="mr-2 h-4 w-4" />
                      Download Receipt
                    </Button>
                  </div>
                </div>
              )}
            </div>
          )}
          <DialogFooter>
            <Button variant="outline" onClick={() => setIsDetailsOpen(false)}>
              Close
            </Button>
          </DialogFooter>
        </DialogContent>
      </Dialog>
    </div>
  );
}

import { useEffect, useState } from "react";
import { TrendingUp, Download, Eye } from "lucide-react";
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Badge } from "@/components/ui/badge";
import { Progress } from "@/components/ui/progress";
import { Tabs, TabsContent, TabsList, TabsTrigger } from "@/components/ui/tabs";
import { Table, TableBody, TableCell, TableHead, TableHeader, TableRow } from "@/components/ui/table";
import { Dialog, DialogContent, DialogDescription, DialogHeader, DialogTitle } from "@/components/ui/dialog";
import { toast } from "sonner";
import { authService } from "@/services/auth";

interface FundUsage {
  id: number;
  charity: string;
  campaign: string;
  category: string;
  amount: number;
  description: string;
  date: string;
  receiptUrl?: string;
}

interface Campaign {
  id: number;
  title: string;
  charity: string;
  target_amount: number;
  current_amount: number;
  spent_amount: number;
}

export default function FundTransparency() {
  const [selectedCharity, setSelectedCharity] = useState("all");
  const [selectedUsage, setSelectedUsage] = useState<FundUsage | null>(null);
  const [isDetailsOpen, setIsDetailsOpen] = useState(false);
  const [campaigns, setCampaigns] = useState<Campaign[]>([]);
  const [fundUsages, setFundUsages] = useState<FundUsage[]>([]);
  const API_URL = import.meta.env.VITE_API_URL;

  useEffect(() => {
    fetchTransparency();
  }, []);

  const fetchTransparency = async () => {
    try {
      const token = authService.getToken();
      if (!token) {
        toast.info('Please log in to view your transparency dashboard');
        return;
      }
      const res = await fetch(`${API_URL}/api/me/transparency`, {
        headers: { Authorization: `Bearer ${token}`, Accept: 'application/json' }
      });
      if (!res.ok) throw new Error('Failed to load transparency data');
      const payload = await res.json();

      const apiCampaigns = (payload.campaigns ?? payload.data?.campaigns ?? []) as any[];
      const apiUsages = (payload.fund_usages ?? payload.data?.fund_usages ?? []) as any[];

      setCampaigns(apiCampaigns.map((c) => ({
        id: c.id,
        title: c.title ?? c.name ?? 'Campaign',
        charity: c.charity?.name ?? c.charity_name ?? 'Charity',
        target_amount: c.target_amount ?? c.target ?? 0,
        current_amount: c.current_amount ?? c.raised ?? 0,
        spent_amount: c.spent_amount ?? c.spent ?? 0,
      })));

      setFundUsages(apiUsages.map((u) => ({
        id: u.id,
        charity: u.charity?.name ?? u.charity_name ?? 'Charity',
        campaign: u.campaign?.title ?? u.campaign_name ?? 'General',
        category: u.category ?? 'General',
        amount: u.amount ?? 0,
        description: u.description ?? '',
        date: u.date ?? u.created_at ?? new Date().toISOString(),
        receiptUrl: u.receipt_url ?? u.receiptUrl ?? null,
      })));
    } catch (e) {
      toast.error(e instanceof Error ? e.message : 'Unable to load transparency data');
    }
  };

  const charities = ["all", ...new Set(fundUsages.map(f => f.charity))];

  const filteredUsages = selectedCharity === "all"
    ? fundUsages
    : fundUsages.filter(f => f.charity === selectedCharity);

  const getProgress = (raised: number, target: number) => {
    return (raised / target) * 100;
  };

  const getUtilization = (spent: number, raised: number) => {
    return raised > 0 ? (spent / raised) * 100 : 0;
  };

  return (
    <div>
      {/* Hero Section */}
      <div className="bg-gradient-to-r from-primary/10 via-primary/5 to-background border-b">
        <div className="max-w-7xl mx-auto px-4 py-12">
          <h1 className="text-4xl font-bold mb-4">Fund Transparency</h1>
          <p className="text-xl text-muted-foreground">
            See exactly how your donations are being used
          </p>
        </div>
      </div>

      {/* Main Content */}
      <div className="max-w-7xl mx-auto px-4 py-12 space-y-8">
        {/* Campaign Progress */}
        <div>
          <h2 className="text-2xl font-bold mb-4">Campaign Progress</h2>
          <div className="grid gap-4 md:grid-cols-2">
            {campaigns.map((campaign) => (
              <Card key={campaign.id}>
                <CardHeader>
                  <CardTitle className="text-lg">{campaign.title}</CardTitle>
                  <CardDescription>{campaign.charity}</CardDescription>
                </CardHeader>
                <CardContent className="space-y-4">
                  <div>
                    <div className="flex justify-between text-sm mb-2">
                      <span>Fundraising Progress</span>
                      <span className="font-medium">
                        {getProgress(campaign.current_amount, campaign.target_amount).toFixed(1)}%
                      </span>
                    </div>
                    <Progress value={getProgress(campaign.current_amount, campaign.target_amount)} />
                    <div className="flex justify-between text-xs text-muted-foreground mt-1">
                      <span>{campaign.current_amount.toLocaleString()} raised</span>
                      <span>{campaign.target_amount.toLocaleString()} goal</span>
                    </div>
                  </div>

                  <div>
                    <div className="flex justify-between text-sm mb-2">
                      <span>Fund Utilization</span>
                      <span className="font-medium">
                        {getUtilization(campaign.spent_amount, campaign.current_amount).toFixed(1)}%
                      </span>
                    </div>
                    <Progress value={getUtilization(campaign.spent_amount, campaign.current_amount)} className="bg-green-100" />
                    <div className="flex justify-between text-xs text-muted-foreground mt-1">
                      <span>{campaign.spent_amount.toLocaleString()} spent</span>
                      <span>{campaign.current_amount.toLocaleString()} available</span>
                    </div>
                  </div>

                  <div className="grid grid-cols-3 gap-2 pt-2 border-t">
                    <div>
                      <p className="text-xs text-muted-foreground">Target</p>
                      <p className="font-bold text-sm">{campaign.target_amount.toLocaleString()}</p>
                    </div>
                    <div>
                      <p className="text-xs text-muted-foreground">Raised</p>
                      <p className="font-bold text-sm text-green-600">{campaign.current_amount.toLocaleString()}</p>
                    </div>
                    <div>
                      <p className="text-xs text-muted-foreground">Spent</p>
                      <p className="font-bold text-sm text-blue-600">{campaign.spent_amount.toLocaleString()}</p>
                    </div>
                  </div>
                </CardContent>
              </Card>
            ))}
          </div>
        </div>

        {/* Fund Usage Details */}
        <Card>
          <CardHeader>
            <div className="flex items-center justify-between">
              <div>
                <CardTitle>Fund Usage Details</CardTitle>
                <CardDescription>Detailed breakdown of how funds are being used</CardDescription>
              </div>
              <div className="flex gap-2">
                <Tabs value={selectedCharity} onValueChange={setSelectedCharity}>
                  <TabsList>
                    {charities.map(charity => (
                      <TabsTrigger key={charity} value={charity}>
                        {charity === "all" ? "All Charities" : charity}
                      </TabsTrigger>
                    ))}
                  </TabsList>
                </Tabs>
              </div>
            </div>
          </CardHeader>
          <CardContent>
            <Table>
              <TableHeader>
                <TableRow>
                  <TableHead>Date</TableHead>
                  <TableHead>Charity</TableHead>
                  <TableHead>Campaign</TableHead>
                  <TableHead>Category</TableHead>
                  <TableHead>Description</TableHead>
                  <TableHead>Amount</TableHead>
                  <TableHead className="text-right">Receipt</TableHead>
                </TableRow>
              </TableHeader>
              <TableBody>
                {filteredUsages.map((usage) => (
                  <TableRow key={usage.id}>
                    <TableCell>{new Date(usage.date).toLocaleDateString()}</TableCell>
                    <TableCell className="font-medium">{usage.charity}</TableCell>
                    <TableCell>{usage.campaign}</TableCell>
                    <TableCell>
                      <Badge variant="outline">{usage.category}</Badge>
                    </TableCell>
                    <TableCell className="max-w-xs">
                      <p className="text-sm line-clamp-2">{usage.description}</p>
                    </TableCell>
                    <TableCell className="font-bold">{usage.amount.toLocaleString()}</TableCell>
                    <TableCell className="text-right">
                      <div className="flex justify-end gap-2">
                        <Button
                          variant="ghost"
                          size="icon"
                          onClick={() => {
                            setSelectedUsage(usage);
                            setIsDetailsOpen(true);
                          }}
                        >
                          <Eye className="h-4 w-4" />
                        </Button>
                        {usage.receiptUrl && (
                          <Button variant="ghost" size="icon">
                            <Download className="h-4 w-4" />
                          </Button>
                        )}
                      </div>
                    </TableCell>
                  </TableRow>
                ))}
              </TableBody>
            </Table>
          </CardContent>
        </Card>

        {/* Transparency Score removed until real data is available */}
      </div>

      {/* Usage Details Dialog */}
      <Dialog open={isDetailsOpen} onOpenChange={setIsDetailsOpen}>
        <DialogContent className="max-w-2xl">
          <DialogHeader>
            <DialogTitle>Fund Usage Details</DialogTitle>
            <DialogDescription>Complete information about this expense</DialogDescription>
          </DialogHeader>
          {selectedUsage && (
            <div className="space-y-4">
              <div className="grid grid-cols-2 gap-4">
                <div>
                  <p className="text-sm font-medium text-muted-foreground">Charity</p>
                  <p className="font-medium">{selectedUsage.charity}</p>
                </div>
                <div>
                  <p className="text-sm font-medium text-muted-foreground">Campaign</p>
                  <p className="font-medium">{selectedUsage.campaign}</p>
                </div>
              </div>
              <div className="grid grid-cols-2 gap-4">
                <div>
                  <p className="text-sm font-medium text-muted-foreground">Category</p>
                  <Badge variant="outline">{selectedUsage.category}</Badge>
                </div>
                <div>
                  <p className="text-sm font-medium text-muted-foreground">Date</p>
                  <p className="font-medium">{new Date(selectedUsage.date).toLocaleDateString()}</p>
                </div>
              </div>
              <div>
                <p className="text-sm font-medium text-muted-foreground">Amount</p>
                <p className="text-2xl font-bold text-green-600">{selectedUsage.amount.toLocaleString()}</p>
              </div>
              <div>
                <p className="text-sm font-medium text-muted-foreground mb-2">Description</p>
                <p className="text-sm">{selectedUsage.description}</p>
              </div>
              {selectedUsage.receiptUrl && (
                <div>
                  <p className="text-sm font-medium text-muted-foreground mb-2">Receipt</p>
                  <Button variant="outline" size="sm">
                    <Download className="mr-2 h-4 w-4" />
                    Download Receipt
                  </Button>
                </div>
              )}
            </div>
          )}
        </DialogContent>
      </Dialog>
    </div>
  );
}

import { useState, useEffect } from "react";
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { Badge } from "@/components/ui/badge";
import { Avatar, AvatarFallback, AvatarImage } from "@/components/ui/avatar";
import { Trophy, Medal, Award, Crown, TrendingUp, Users, Heart, Building } from "lucide-react";
import { toast } from "sonner";
import axios from "axios";

interface LeaderboardEntry {
  id: number;
  name: string;
  profile_image?: string;
  logo_path?: string;
  total_donated?: number;
  total_received?: number;
  donation_count: number;
}

interface LeaderboardStats {
  summary: {
    total_donations: number;
    total_donation_count: number;
    total_donors: number;
    average_donation: number;
  };
  monthly_trends: Array<{
    year: number;
    month: number;
    total_amount: number;
    donation_count: number;
  }>;
}

export default function Leaderboard() {
  const [topDonors, setTopDonors] = useState<LeaderboardEntry[]>([]);
  const [topCharities, setTopCharities] = useState<LeaderboardEntry[]>([]);
  const [stats, setStats] = useState<LeaderboardStats | null>(null);
  const [loading, setLoading] = useState(true);
  const [period, setPeriod] = useState("all_time");
  const [type, setType] = useState("donors");

  useEffect(() => {
    fetchLeaderboardData();
  }, [period, type]);

  const fetchLeaderboardData = async () => {
    try {
      setLoading(true);

      // Fetch period-based leaderboard
      const leaderboardResponse = await axios.get("/api/leaderboard/period", {
        params: { period, type, limit: 20 }
      });

      if (type === "donors") {
        setTopDonors(leaderboardResponse.data.results);
      } else {
        setTopCharities(leaderboardResponse.data.results);
      }

      // Fetch overall stats
      const statsResponse = await axios.get("/api/leaderboard/stats");
      setStats(statsResponse.data);

    } catch (error) {
      toast.error("Failed to fetch leaderboard data");
    } finally {
      setLoading(false);
    }
  };

  const getRankIcon = (rank: number) => {
    switch (rank) {
      case 1:
        return <Crown className="h-6 w-6 text-yellow-500" />;
      case 2:
        return <Trophy className="h-6 w-6 text-gray-400" />;
      case 3:
        return <Medal className="h-6 w-6 text-amber-600" />;
      default:
        return <Award className="h-5 w-5 text-blue-500" />;
    }
  };

  const getRankBadge = (rank: number) => {
    const colors = {
      1: "bg-yellow-100 text-yellow-800 border-yellow-200",
      2: "bg-gray-100 text-gray-800 border-gray-200",
      3: "bg-amber-100 text-amber-800 border-amber-200",
    };

    const color = colors[rank as keyof typeof colors] || "bg-blue-100 text-blue-800 border-blue-200";

    return (
      <Badge className={`${color} border`}>
        #{rank}
      </Badge>
    );
  };

  const formatCurrency = (amount: number) => {
    return new Intl.NumberFormat('en-PH', {
      style: 'currency',
      currency: 'PHP',
      minimumFractionDigits: 0,
    }).format(amount);
  };

  if (loading) {
    return (
      <div className="flex items-center justify-center h-64">
        <div className="animate-spin rounded-full h-8 w-8 border-b-2 border-primary"></div>
      </div>
    );
  }

  const currentData = type === "donors" ? topDonors : topCharities;

  return (
    <div className="space-y-6">
      <div className="flex justify-between items-center">
        <div>
          <h1 className="text-3xl font-bold">Leaderboard</h1>
          <p className="text-muted-foreground">Recognizing our top contributors and performers</p>
        </div>
      </div>

      {/* Statistics Cards */}
      {stats && (
        <div className="grid grid-cols-1 md:grid-cols-4 gap-4">
          <Card>
            <CardHeader className="pb-2">
              <CardTitle className="text-sm font-medium flex items-center gap-2">
                <Heart className="h-4 w-4" />
                Total Donations
              </CardTitle>
            </CardHeader>
            <CardContent>
              <div className="text-2xl font-bold">{formatCurrency(stats.summary.total_donations)}</div>
            </CardContent>
          </Card>
          <Card>
            <CardHeader className="pb-2">
              <CardTitle className="text-sm font-medium flex items-center gap-2">
                <TrendingUp className="h-4 w-4" />
                Total Transactions
              </CardTitle>
            </CardHeader>
            <CardContent>
              <div className="text-2xl font-bold">{stats.summary.total_donation_count.toLocaleString()}</div>
            </CardContent>
          </Card>
          <Card>
            <CardHeader className="pb-2">
              <CardTitle className="text-sm font-medium flex items-center gap-2">
                <Users className="h-4 w-4" />
                Active Donors
              </CardTitle>
            </CardHeader>
            <CardContent>
              <div className="text-2xl font-bold">{stats.summary.total_donors.toLocaleString()}</div>
            </CardContent>
          </Card>
          <Card>
            <CardHeader className="pb-2">
              <CardTitle className="text-sm font-medium flex items-center gap-2">
                <Building className="h-4 w-4" />
                Average Donation
              </CardTitle>
            </CardHeader>
            <CardContent>
              <div className="text-2xl font-bold">{formatCurrency(stats.summary.average_donation)}</div>
            </CardContent>
          </Card>
        </div>
      )}

      {/* Filters */}
      <Card>
        <CardHeader>
          <CardTitle>Filters</CardTitle>
        </CardHeader>
        <CardContent>
          <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
            <div>
              <label className="text-sm font-medium">Time Period</label>
              <Select value={period} onValueChange={setPeriod}>
                <SelectTrigger>
                  <SelectValue />
                </SelectTrigger>
                <SelectContent>
                  <SelectItem value="all_time">All Time</SelectItem>
                  <SelectItem value="this_year">This Year</SelectItem>
                  <SelectItem value="this_month">This Month</SelectItem>
                  <SelectItem value="this_week">This Week</SelectItem>
                </SelectContent>
              </Select>
            </div>
            <div>
              <label className="text-sm font-medium">Category</label>
              <Select value={type} onValueChange={setType}>
                <SelectTrigger>
                  <SelectValue />
                </SelectTrigger>
                <SelectContent>
                  <SelectItem value="donors">Top Donors</SelectItem>
                  <SelectItem value="charities">Top Charities</SelectItem>
                </SelectContent>
              </Select>
            </div>
          </div>
        </CardContent>
      </Card>

      {/* Leaderboard */}
      <Card>
        <CardHeader>
          <CardTitle className="flex items-center gap-2">
            <Trophy className="h-5 w-5" />
            {type === "donors" ? "Top Donors" : "Top Performing Charities"}
          </CardTitle>
          <CardDescription>
            {period.replace("_", " ").replace(/\b\w/g, l => l.toUpperCase())} rankings
          </CardDescription>
        </CardHeader>
        <CardContent>
          <div className="space-y-4">
            {currentData.map((entry, index) => {
              const rank = index + 1;
              return (
                <div key={entry.id} className={`flex items-center gap-4 p-4 rounded-lg border ${rank <= 3 ? 'bg-gradient-to-r from-yellow-50 to-transparent' : 'bg-gray-50'}`}>
                  <div className="flex items-center gap-3">
                    {getRankIcon(rank)}
                    {getRankBadge(rank)}
                  </div>

                  <Avatar className="h-12 w-12">
                    <AvatarImage src={entry.profile_image || entry.logo_path} />
                    <AvatarFallback>
                      {entry.name.split(' ').map(n => n[0]).join('').toUpperCase()}
                    </AvatarFallback>
                  </Avatar>

                  <div className="flex-1">
                    <h3 className="font-semibold">{entry.name}</h3>
                    <p className="text-sm text-muted-foreground">
                      {entry.donation_count} {type === "donors" ? "donations" : "donations received"}
                    </p>
                  </div>

                  <div className="text-right">
                    <div className="text-lg font-bold">
                      {formatCurrency(entry.total_donated || entry.total_received || 0)}
                    </div>
                    {rank <= 3 && (
                      <Badge variant="outline" className="text-xs">
                        {rank === 1 ? "Champion" : rank === 2 ? "Runner-up" : "3rd Place"}
                      </Badge>
                    )}
                  </div>
                </div>
              );
            })}
            {currentData.length === 0 && (
              <div className="text-center py-8 text-muted-foreground">
                <Trophy className="h-12 w-12 mx-auto mb-4 opacity-50" />
                <p>No data available for the selected period.</p>
              </div>
            )}
          </div>
        </CardContent>
      </Card>

      {/* Recognition Message */}
      <Card className="bg-gradient-to-r from-blue-50 to-purple-50 border-blue-200">
        <CardContent className="pt-6">
          <div className="text-center">
            <Heart className="h-8 w-8 mx-auto mb-2 text-red-500" />
            <h3 className="text-lg font-semibold mb-2">Thank You to All Our Contributors!</h3>
            <p className="text-muted-foreground">
              Every donation, no matter the size, makes a difference in someone's life. 
              Together, we're building a better world through generosity and compassion.
            </p>
          </div>
        </CardContent>
      </Card>
    </div>
  );
}

import { useEffect, useState } from "react";
import { Heart, Upload, CheckCircle, CreditCard } from "lucide-react";
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Textarea } from "@/components/ui/textarea";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { RadioGroup, RadioGroupItem } from "@/components/ui/radio-group";
import { Checkbox } from "@/components/ui/checkbox";
import { Badge } from "@/components/ui/badge";
import { toast } from "sonner";
import { useNavigate } from "react-router-dom";
import { authService } from "@/services/auth";

export default function MakeDonation() {
  const navigate = useNavigate();
  const [step, setStep] = useState(1);
  const [formData, setFormData] = useState({
    charityId: "",
    campaignId: "",
    amount: "",
    customAmount: "",
    donationType: "one-time",
    frequency: "monthly",
    paymentMethod: "gcash",
    message: "",
    isAnonymous: false,
    proofOfPayment: null as File | null
  });
  const [charities, setCharities] = useState<Array<{ id: number; name: string }>>([]);
  const [campaigns, setCampaigns] = useState<Array<{ id: number; title: string }>>([]);
  const API_URL = import.meta.env.VITE_API_URL;

  useEffect(() => {
    fetchCharities();
  }, []);

  useEffect(() => {
    if (formData.charityId) fetchCampaigns(parseInt(formData.charityId, 10));
    else setCampaigns([]);
  }, [formData.charityId]);

  const fetchCharities = async () => {
    try {
      const res = await fetch(`${API_URL}/api/charities`);
      if (!res.ok) throw new Error('Failed to load charities');
      const data = await res.json();
      // backend returns { charities: { data: [...] } }
      const charitiesArray = data.charities?.data ?? data.charities ?? data.data ?? data;
      setCharities(Array.isArray(charitiesArray) ? charitiesArray : []);
    } catch (e) {
      toast.error(e instanceof Error ? e.message : 'Unable to load charities');
    }
  };

  const fetchCampaigns = async (charityId: number) => {
    try {
      const res = await fetch(`${API_URL}/api/charities/${charityId}/campaigns`);
      if (!res.ok) throw new Error('Failed to load campaigns');
      const data = await res.json();
      // backend returns paginated data
      const campaignsArray = data.data ?? data;
      setCampaigns(Array.isArray(campaignsArray) ? campaignsArray : []);
    } catch (e) {
      toast.error(e instanceof Error ? e.message : 'Unable to load campaigns');
    }
  };

  const suggestedAmounts = [500, 1000, 2500, 5000, 10000];

  const handleSubmit = async () => {
    if (!formData.charityId || !formData.amount) {
      toast.error("Please fill in all required fields");
      return;
    }

    try {
      const token = authService.getToken();
      if (!token) {
        toast.info('Please log in to donate');
        navigate('/auth/login');
        return;
      }

      const isRecurring = formData.donationType === 'recurring';
      const body = {
        charity_id: parseInt(formData.charityId, 10),
        campaign_id: (formData.campaignId && formData.campaignId !== 'general') ? parseInt(formData.campaignId, 10) : undefined,
        amount: parseFloat(formData.amount),
        purpose: 'general',
        is_anonymous: formData.isAnonymous,
        is_recurring: isRecurring,
        recurring_type: isRecurring ? (formData.frequency as 'weekly'|'monthly'|'quarterly') : undefined,
      } as any;

      const res = await fetch(`${API_URL}/api/donations`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          Authorization: `Bearer ${token}`,
          Accept: 'application/json'
        },
        body: JSON.stringify(body)
      });
      if (!res.ok) throw new Error('Failed to submit donation');
      const donation = await res.json();

      if (!formData.proofOfPayment) {
        toast.success("Donation submitted successfully! Awaiting confirmation.");
        navigate('/donor/history');
        return;
      }

      const fd = new FormData();
      fd.append('file', formData.proofOfPayment);
      fd.append('proof_type', formData.paymentMethod);
      const proofRes = await fetch(`${API_URL}/api/donations/${donation.id}/proof`, {
        method: 'POST',
        headers: {
          Authorization: `Bearer ${token}`
        },
        body: fd
      });
      if (!proofRes.ok) throw new Error('Donation saved but uploading proof failed');

      toast.success("Donation submitted successfully! Proof uploaded.");
      navigate('/donor/history');
    } catch (e) {
      toast.error(e instanceof Error ? e.message : 'Unable to submit donation');
    }
  };

  const handleFileUpload = (e: React.ChangeEvent<HTMLInputElement>) => {
    const file = e.target.files?.[0];
    if (file) {
      setFormData({ ...formData, proofOfPayment: file });
      toast.success("Proof of payment uploaded");
    }
  };

  return (
    <div className="max-w-4xl mx-auto px-4 py-6">
      {/* Header */}
      <div className="mb-8">
        <h1 className="text-3xl font-bold mb-2">Make a Donation</h1>
        <p className="text-muted-foreground">
          Your contribution makes a real difference
        </p>
      </div>

      {/* Progress Steps */}
      <div className="mb-8">
        <div className="flex items-center justify-between max-w-2xl mx-auto">
          <div className="flex flex-col items-center">
            <div className={`w-10 h-10 rounded-full flex items-center justify-center ${step >= 1 ? 'bg-primary text-primary-foreground' : 'bg-muted'}`}>
              1
            </div>
            <span className="text-sm mt-2">Select</span>
          </div>
          <div className={`flex-1 h-1 mx-4 ${step >= 2 ? 'bg-primary' : 'bg-muted'}`} />
          <div className="flex flex-col items-center">
            <div className={`w-10 h-10 rounded-full flex items-center justify-center ${step >= 2 ? 'bg-primary text-primary-foreground' : 'bg-muted'}`}>
              2
            </div>
            <span className="text-sm mt-2">Amount</span>
          </div>
          <div className={`flex-1 h-1 mx-4 ${step >= 3 ? 'bg-primary' : 'bg-muted'}`} />
          <div className="flex flex-col items-center">
            <div className={`w-10 h-10 rounded-full flex items-center justify-center ${step >= 3 ? 'bg-primary text-primary-foreground' : 'bg-muted'}`}>
              3
            </div>
            <span className="text-sm mt-2">Payment</span>
          </div>
        </div>
      </div>

      {/* Step 1: Select Charity & Campaign */}
      {step === 1 && (
        <Card>
          <CardHeader>
            <CardTitle>Select Charity & Campaign</CardTitle>
            <CardDescription>Choose where you want to donate</CardDescription>
          </CardHeader>
          <CardContent className="space-y-4">
            <div className="space-y-2">
              <Label htmlFor="charity">Charity *</Label>
              <Select value={formData.charityId} onValueChange={(value) => setFormData({ ...formData, charityId: value, campaignId: "" })}>
                <SelectTrigger>
                  <SelectValue placeholder="Select a charity" />
                </SelectTrigger>
                <SelectContent>
                  {charities.map(c => (
                    <SelectItem key={c.id} value={String(c.id)}>
                      {c.name}
                    </SelectItem>
                  ))}
                </SelectContent>
              </Select>
            </div>

            <div className="space-y-2">
              <Label htmlFor="campaign">Campaign (Optional)</Label>
              <Select value={formData.campaignId} onValueChange={(value) => setFormData({ ...formData, campaignId: value })}>
                <SelectTrigger>
                  <SelectValue placeholder="General donation or select campaign" />
                </SelectTrigger>
                <SelectContent>
                  <SelectItem value="general">General Donation</SelectItem>
                  {campaigns.map(c => (
                    <SelectItem key={c.id} value={String(c.id)}>
                      {c.title}
                    </SelectItem>
                  ))}
                </SelectContent>
              </Select>
            </div>

            <Button onClick={() => setStep(2)} disabled={!formData.charityId} className="w-full">
              Continue
            </Button>
          </CardContent>
        </Card>
      )}

      {/* Step 2: Amount & Type */}
      {step === 2 && (
        <Card>
          <CardHeader>
            <CardTitle>Donation Amount</CardTitle>
            <CardDescription>Choose your donation amount and type</CardDescription>
          </CardHeader>
          <CardContent className="space-y-6">
            {/* Donation Type */}
            <div className="space-y-3">
              <Label>Donation Type</Label>
              <RadioGroup value={formData.donationType} onValueChange={(value) => setFormData({ ...formData, donationType: value })}>
                <div className="flex items-center space-x-2">
                  <RadioGroupItem value="one-time" id="one-time" />
                  <Label htmlFor="one-time" className="cursor-pointer">One-time Donation</Label>
                </div>
                <div className="flex items-center space-x-2">
                  <RadioGroupItem value="recurring" id="recurring" />
                  <Label htmlFor="recurring" className="cursor-pointer">Recurring Donation</Label>
                </div>
              </RadioGroup>
            </div>

            {/* Frequency (if recurring) */}
            {formData.donationType === 'recurring' && (
              <div className="space-y-2">
                <Label>Frequency</Label>
                <Select value={formData.frequency} onValueChange={(value) => setFormData({ ...formData, frequency: value })}>
                  <SelectTrigger>
                    <SelectValue />
                  </SelectTrigger>
                  <SelectContent>
                    <SelectItem value="weekly">Weekly</SelectItem>
                    <SelectItem value="monthly">Monthly</SelectItem>
                    <SelectItem value="quarterly">Quarterly</SelectItem>
                  </SelectContent>
                </Select>
              </div>
            )}

            {/* Suggested Amounts */}
            <div className="space-y-3">
              <Label>Select Amount ()</Label>
              <div className="grid grid-cols-3 gap-3">
                {suggestedAmounts.map(amount => (
                  <Button
                    key={amount}
                    variant={formData.amount === amount.toString() ? "default" : "outline"}
                    onClick={() => setFormData({ ...formData, amount: amount.toString(), customAmount: "" })}
                  >
                    {amount.toLocaleString()}
                  </Button>
                ))}
              </div>
            </div>

            {/* Custom Amount */}
            <div className="space-y-2">
              <Label htmlFor="customAmount">Or Enter Custom Amount</Label>
              <Input
                id="customAmount"
                type="number"
                placeholder="Enter amount"
                value={formData.customAmount}
                onChange={(e) => setFormData({ ...formData, customAmount: e.target.value, amount: e.target.value })}
              />
            </div>

            {/* Message */}
            <div className="space-y-2">
              <Label htmlFor="message">Message (Optional)</Label>
              <Textarea
                id="message"
                placeholder="Add a message of support..."
                value={formData.message}
                onChange={(e) => setFormData({ ...formData, message: e.target.value })}
                rows={3}
              />
            </div>

            {/* Anonymous */}
            <div className="flex items-center space-x-2">
              <Checkbox
                id="anonymous"
                checked={formData.isAnonymous}
                onCheckedChange={(checked) => setFormData({ ...formData, isAnonymous: checked as boolean })}
              />
              <Label htmlFor="anonymous" className="cursor-pointer">
                Make this donation anonymous
              </Label>
            </div>

            <div className="flex gap-2">
              <Button variant="outline" onClick={() => setStep(1)} className="flex-1">
                Back
              </Button>
              <Button onClick={() => setStep(3)} disabled={!formData.amount} className="flex-1">
                Continue to Payment
              </Button>
            </div>
          </CardContent>
        </Card>
      )}

      {/* Step 3: Payment */}
      {step === 3 && (
        <Card>
          <CardHeader>
            <CardTitle>Payment Method</CardTitle>
            <CardDescription>Complete your donation</CardDescription>
          </CardHeader>
          <CardContent className="space-y-6">
            {/* Summary */}
            <div className="bg-muted p-4 rounded-lg space-y-2">
              <h3 className="font-semibold">Donation Summary</h3>
              <div className="flex justify-between text-sm">
                <span>Charity:</span>
                <span className="font-medium">{charities.find(c => c.id === parseInt(formData.charityId))?.name || 'N/A'}</span>
              </div>
              {formData.campaignId && formData.campaignId !== 'general' && (
                <div className="flex justify-between text-sm">
                  <span>Campaign:</span>
                  <span className="font-medium">{campaigns.find(c => c.id === parseInt(formData.campaignId))?.title || 'N/A'}</span>
                </div>
              )}
              <div className="flex justify-between text-sm">
                <span>Type:</span>
                <span className="font-medium capitalize">{formData.donationType}</span>
              </div>
              <div className="flex justify-between text-lg font-bold border-t pt-2">
                <span>Amount:</span>
                <span className="text-green-600">{parseFloat(formData.amount).toLocaleString()}</span>
              </div>
            </div>

            {/* Payment Method */}
            <div className="space-y-3">
              <Label>Payment Method</Label>
              <RadioGroup value={formData.paymentMethod} onValueChange={(value) => setFormData({ ...formData, paymentMethod: value })}>
                <div className="flex items-center space-x-2 border p-3 rounded-lg">
                  <RadioGroupItem value="gcash" id="gcash" />
                  <Label htmlFor="gcash" className="cursor-pointer flex-1">GCash</Label>
                  <Badge variant="secondary">Recommended</Badge>
                </div>
                <div className="flex items-center space-x-2 border p-3 rounded-lg">
                  <RadioGroupItem value="paymaya" id="paymaya" />
                  <Label htmlFor="paymaya" className="cursor-pointer flex-1">PayMaya</Label>
                </div>
                <div className="flex items-center space-x-2 border p-3 rounded-lg">
                  <RadioGroupItem value="bank" id="bank" />
                  <Label htmlFor="bank" className="cursor-pointer flex-1">Bank Transfer</Label>
                </div>
              </RadioGroup>
            </div>

            {/* Upload Proof */}
            <div className="space-y-2">
              <Label htmlFor="proof">Upload Proof of Payment *</Label>
              <div className="border-2 border-dashed rounded-lg p-6 text-center">
                <input
                  id="proof"
                  type="file"
                  accept="image/*,.pdf"
                  className="hidden"
                  onChange={handleFileUpload}
                />
                <label htmlFor="proof" className="cursor-pointer">
                  <Upload className="mx-auto h-12 w-12 text-muted-foreground mb-2" />
                  <p className="text-sm text-muted-foreground">
                    {formData.proofOfPayment ? formData.proofOfPayment.name : "Click to upload proof of payment"}
                  </p>
                  <p className="text-xs text-muted-foreground mt-1">
                    PNG, JPG, PDF up to 5MB
                  </p>
                </label>
              </div>
            </div>

            <div className="flex gap-2">
              <Button variant="outline" onClick={() => setStep(2)} className="flex-1">
                Back
              </Button>
              <Button onClick={handleSubmit} disabled={!formData.proofOfPayment} className="flex-1">
                <Heart className="mr-2 h-4 w-4" />
                Submit Donation
              </Button>
            </div>
          </CardContent>
        </Card>
      )}
    </div>
  );
}

import { useEffect, useState } from "react";
import { Bell, Check, CheckCheck, Trash2 } from "lucide-react";
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Badge } from "@/components/ui/badge";
import { Table, TableBody, TableCell, TableHead, TableHeader, TableRow } from "@/components/ui/table";
import { toast } from "sonner";
import { authService } from "@/services/auth";

interface NotificationItem {
  id: number;
  title?: string;
  message: string;
  type?: string; // e.g., donation_confirmed, new_post, reminder
  is_read: boolean;
  created_at: string;
}

export default function Notifications() {
  const [items, setItems] = useState<NotificationItem[]>([]);
  const [loading, setLoading] = useState<boolean>(true);
  const API_URL = import.meta.env.VITE_API_URL;

  useEffect(() => {
    fetchNotifications();
  }, []);

  const fetchNotifications = async () => {
    try {
      setLoading(true);
      const token = authService.getToken();
      if (!token) return;
      const res = await fetch(`${API_URL}/api/me/notifications`, {
        headers: { Authorization: `Bearer ${token}`, Accept: 'application/json' }
      });
      if (!res.ok) throw new Error('Failed to load notifications');
      const payload = await res.json();
      const list: NotificationItem[] = payload.data ?? payload;
      setItems(list);
    } catch (e) {
      toast.error(e instanceof Error ? e.message : 'Unable to load notifications');
    } finally {
      setLoading(false);
    }
  };

  const markAsRead = async (id: number) => {
    try {
      const token = authService.getToken();
      if (!token) return;
      const res = await fetch(`${API_URL}/api/notifications/${id}/read`, {
        method: 'POST',
        headers: { Authorization: `Bearer ${token}` }
      });
      if (!res.ok) throw new Error('Failed to mark as read');
      setItems((prev) => prev.map(n => n.id === id ? { ...n, is_read: true } : n));
    } catch (e) {
      toast.error(e instanceof Error ? e.message : 'Unable to mark as read');
    }
  };

  const markAllAsRead = async () => {
    try {
      const token = authService.getToken();
      if (!token) return;
      const res = await fetch(`${API_URL}/api/notifications/mark-all-read`, {
        method: 'POST',
        headers: { Authorization: `Bearer ${token}` }
      });
      if (!res.ok) throw new Error('Failed to mark all as read');
      setItems((prev) => prev.map(n => ({ ...n, is_read: true })));
      toast.success('All notifications marked as read');
    } catch (e) {
      toast.error(e instanceof Error ? e.message : 'Unable to mark all as read');
    }
  };

  const deleteNotification = async (id: number) => {
    try {
      const token = authService.getToken();
      if (!token) return;
      const res = await fetch(`${API_URL}/api/notifications/${id}`, {
        method: 'DELETE',
        headers: { Authorization: `Bearer ${token}` }
      });
      if (!res.ok) throw new Error('Failed to delete notification');
      setItems((prev) => prev.filter(n => n.id !== id));
      toast.success('Notification deleted');
    } catch (e) {
      toast.error(e instanceof Error ? e.message : 'Unable to delete notification');
    }
  };

  return (
    <div className="max-w-5xl mx-auto px-4 py-8">
      <div className="mb-6">
        <h1 className="text-3xl font-bold mb-2 flex items-center gap-2">
          <Bell className="h-6 w-6" /> Notifications
        </h1>
        <p className="text-muted-foreground">Stay updated with confirmations and updates from charities you follow.</p>
      </div>

      <Card>
        <CardHeader>
          <div className="flex items-center justify-between">
            <div>
              <CardTitle>Inbox</CardTitle>
              <CardDescription>Your latest notifications</CardDescription>
            </div>
            <div className="flex items-center gap-2">
              <Button variant="outline" size="sm" onClick={fetchNotifications} disabled={loading}>
                Refresh
              </Button>
              <Button variant="default" size="sm" onClick={markAllAsRead} disabled={loading || items.every(n => n.is_read)}>
                <CheckCheck className="h-4 w-4 mr-2" /> Mark all as read
              </Button>
            </div>
          </div>
        </CardHeader>
        <CardContent>
          {loading ? (
            <div className="py-12 text-center text-muted-foreground">Loading notifications...</div>
          ) : items.length === 0 ? (
            <div className="py-12 text-center text-muted-foreground">No notifications yet.</div>
          ) : (
            <Table>
              <TableHeader>
                <TableRow>
                  <TableHead>Status</TableHead>
                  <TableHead>Title</TableHead>
                  <TableHead>Message</TableHead>
                  <TableHead>Date</TableHead>
                  <TableHead className="text-right">Actions</TableHead>
                </TableRow>
              </TableHeader>
              <TableBody>
                {items.map((n) => (
                  <TableRow key={n.id} className={!n.is_read ? 'bg-primary/5' : ''}>
                    <TableCell>
                      {n.is_read ? (
                        <Badge variant="secondary">Read</Badge>
                      ) : (
                        <Badge>Unread</Badge>
                      )}
                    </TableCell>
                    <TableCell className="font-medium">{n.title || (n.type ? n.type.replace(/_/g, ' ') : 'Notification')}</TableCell>
                    <TableCell className="max-w-xl">
                      <p className="text-sm line-clamp-2">{n.message}</p>
                    </TableCell>
                    <TableCell>{new Date(n.created_at).toLocaleString()}</TableCell>
                    <TableCell className="text-right">
                      <div className="flex justify-end gap-2">
                        {!n.is_read && (
                          <Button variant="ghost" size="icon" onClick={() => markAsRead(n.id)}>
                            <Check className="h-4 w-4" />
                          </Button>
                        )}
                        <Button variant="ghost" size="icon" onClick={() => deleteNotification(n.id)}>
                          <Trash2 className="h-4 w-4" />
                        </Button>
                      </div>
                    </TableCell>
                  </TableRow>
                ))}
              </TableBody>
            </Table>
          )}
        </CardContent>
      </Card>
    </div>
  );
}

import { useState, useEffect } from "react";
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Textarea } from "@/components/ui/textarea";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { Dialog, DialogContent, DialogDescription, DialogHeader, DialogTitle, DialogTrigger } from "@/components/ui/dialog";
import { Badge } from "@/components/ui/badge";
import { AlertTriangle, Plus, Eye, Clock, CheckCircle, XCircle } from "lucide-react";
import { toast } from "sonner";
import axios from "axios";

interface Report {
  id: number;
  reported_entity_type: string;
  reported_entity_id: number;
  reason: string;
  description: string;
  evidence_path?: string;
  status: string;
  admin_notes?: string;
  reviewed_at?: string;
  action_taken?: string;
  created_at: string;
}

export default function DonorReports() {
  const [reports, setReports] = useState<Report[]>([]);
  const [loading, setLoading] = useState(true);
  const [isCreateOpen, setIsCreateOpen] = useState(false);

  // Form state
  const [formData, setFormData] = useState({
    reported_entity_type: "",
    reported_entity_id: "",
    reason: "",
    description: "",
    evidence: null as File | null,
  });

  useEffect(() => {
    fetchMyReports();
  }, []);

  const fetchMyReports = async () => {
    try {
      const response = await axios.get("/api/me/reports");
      setReports(response.data.data);
    } catch (error) {
      toast.error("Failed to fetch reports");
    } finally {
      setLoading(false);
    }
  };

  const submitReport = async () => {
    if (!formData.reported_entity_type || !formData.reported_entity_id || !formData.reason || !formData.description) {
      toast.error("Please fill in all required fields");
      return;
    }

    try {
      const formDataToSend = new FormData();
      formDataToSend.append("reported_entity_type", formData.reported_entity_type);
      formDataToSend.append("reported_entity_id", formData.reported_entity_id);
      formDataToSend.append("reason", formData.reason);
      formDataToSend.append("description", formData.description);
      if (formData.evidence) {
        formDataToSend.append("evidence", formData.evidence);
      }

      await axios.post("/api/reports", formDataToSend, {
        headers: { "Content-Type": "multipart/form-data" },
      });

      toast.success("Report submitted successfully");
      setIsCreateOpen(false);
      resetForm();
      fetchMyReports();
    } catch (error: any) {
      toast.error(error.response?.data?.message || "Failed to submit report");
    }
  };

  const resetForm = () => {
    setFormData({
      reported_entity_type: "",
      reported_entity_id: "",
      reason: "",
      description: "",
      evidence: null,
    });
  };

  const getStatusBadge = (status: string) => {
    const colors = {
      pending: "bg-red-100 text-red-800",
      under_review: "bg-yellow-100 text-yellow-800",
      resolved: "bg-green-100 text-green-800",
      dismissed: "bg-gray-100 text-gray-800",
    };

    const icons = {
      pending: Clock,
      under_review: Eye,
      resolved: CheckCircle,
      dismissed: XCircle,
    };

    const Icon = icons[status as keyof typeof icons] || Clock;
    const color = colors[status as keyof typeof colors] || colors.pending;

    return (
      <Badge className={color}>
        <Icon className="h-3 w-3 mr-1" />
        {status.replace("_", " ").toUpperCase()}
      </Badge>
    );
  };

  const formatReason = (reason: string) => {
    return reason.replace(/_/g, " ").replace(/\b\w/g, l => l.toUpperCase());
  };

  if (loading) {
    return (
      <div className="flex items-center justify-center h-64">
        <div className="animate-spin rounded-full h-8 w-8 border-b-2 border-primary"></div>
      </div>
    );
  }

  return (
    <div className="space-y-6">
      <div className="flex justify-between items-center">
        <div>
          <h1 className="text-3xl font-bold">My Reports</h1>
          <p className="text-muted-foreground">Track your submitted reports and their status</p>
        </div>
        <Dialog open={isCreateOpen} onOpenChange={setIsCreateOpen}>
          <DialogTrigger asChild>
            <Button>
              <Plus className="h-4 w-4 mr-2" />
              Submit Report
            </Button>
          </DialogTrigger>
          <DialogContent className="max-w-2xl">
            <DialogHeader>
              <DialogTitle>Submit a Report</DialogTitle>
              <DialogDescription>
                Report suspicious activities, fraud, or inappropriate content
              </DialogDescription>
            </DialogHeader>
            <div className="space-y-4">
              <div className="grid grid-cols-2 gap-4">
                <div>
                  <label className="text-sm font-medium">Entity Type</label>
                  <Select value={formData.reported_entity_type} onValueChange={(value) => setFormData({ ...formData, reported_entity_type: value })}>
                    <SelectTrigger>
                      <SelectValue placeholder="What are you reporting?" />
                    </SelectTrigger>
                    <SelectContent>
                      <SelectItem value="charity">Charity</SelectItem>
                      <SelectItem value="campaign">Campaign</SelectItem>
                      <SelectItem value="user">User</SelectItem>
                      <SelectItem value="donation">Donation</SelectItem>
                    </SelectContent>
                  </Select>
                </div>
                <div>
                  <label className="text-sm font-medium">Entity ID</label>
                  <Input
                    type="number"
                    value={formData.reported_entity_id}
                    onChange={(e) => setFormData({ ...formData, reported_entity_id: e.target.value })}
                    placeholder="ID of the item being reported"
                  />
                </div>
              </div>
              <div>
                <label className="text-sm font-medium">Reason</label>
                <Select value={formData.reason} onValueChange={(value) => setFormData({ ...formData, reason: value })}>
                  <SelectTrigger>
                    <SelectValue placeholder="Select reason" />
                  </SelectTrigger>
                  <SelectContent>
                    <SelectItem value="fraud">Fraud</SelectItem>
                    <SelectItem value="fake_proof">Fake Proof</SelectItem>
                    <SelectItem value="inappropriate_content">Inappropriate Content</SelectItem>
                    <SelectItem value="scam">Scam</SelectItem>
                    <SelectItem value="fake_charity">Fake Charity</SelectItem>
                    <SelectItem value="misuse_of_funds">Misuse of Funds</SelectItem>
                    <SelectItem value="spam">Spam</SelectItem>
                    <SelectItem value="harassment">Harassment</SelectItem>
                    <SelectItem value="other">Other</SelectItem>
                  </SelectContent>
                </Select>
              </div>
              <div>
                <label className="text-sm font-medium">Description</label>
                <Textarea
                  value={formData.description}
                  onChange={(e) => setFormData({ ...formData, description: e.target.value })}
                  placeholder="Provide detailed information about the issue..."
                  rows={4}
                />
              </div>
              <div>
                <label className="text-sm font-medium">Evidence (Optional)</label>
                <Input
                  type="file"
                  accept=".jpg,.jpeg,.png,.pdf"
                  onChange={(e) => setFormData({ ...formData, evidence: e.target.files?.[0] || null })}
                />
                <p className="text-xs text-muted-foreground mt-1">
                  Upload screenshots or documents as evidence (JPG, PNG, PDF - Max 5MB)
                </p>
              </div>
              <div className="flex gap-2 justify-end">
                <Button variant="outline" onClick={() => setIsCreateOpen(false)}>
                  Cancel
                </Button>
                <Button onClick={submitReport}>
                  Submit Report
                </Button>
              </div>
            </div>
          </DialogContent>
        </Dialog>
      </div>

      {/* Reports List */}
      <div className="space-y-4">
        {reports.map((report) => (
          <Card key={report.id}>
            <CardHeader>
              <div className="flex justify-between items-start">
                <div className="space-y-1">
                  <div className="flex items-center gap-2">
                    <AlertTriangle className="h-4 w-4 text-red-500" />
                    <CardTitle className="text-lg">Report #{report.id}</CardTitle>
                    {getStatusBadge(report.status)}
                  </div>
                  <CardDescription>
                    {formatReason(report.reason)}  {report.reported_entity_type} #{report.reported_entity_id}
                  </CardDescription>
                </div>
                <div className="text-xs text-muted-foreground">
                  {new Date(report.created_at).toLocaleDateString()}
                </div>
              </div>
            </CardHeader>
            <CardContent className="space-y-3">
              <p className="text-sm bg-gray-50 p-3 rounded">
                {report.description}
              </p>
              {report.evidence_path && (
                <div>
                  <p className="text-sm font-medium">Evidence:</p>
                  <a 
                    href={`/storage/${report.evidence_path}`} 
                    target="_blank" 
                    rel="noopener noreferrer"
                    className="text-blue-600 hover:underline text-sm"
                  >
                    View Evidence File
                  </a>
                </div>
              )}
              {report.admin_notes && (
                <div>
                  <p className="text-sm font-medium">Admin Response:</p>
                  <p className="text-sm bg-blue-50 p-3 rounded">
                    {report.admin_notes}
                  </p>
                  {report.action_taken && (
                    <p className="text-xs text-muted-foreground mt-1">
                      Action taken: {report.action_taken.replace("_", " ")}
                    </p>
                  )}
                </div>
              )}
            </CardContent>
          </Card>
        ))}
        {reports.length === 0 && (
          <Card>
            <CardContent className="text-center py-8">
              <AlertTriangle className="h-12 w-12 mx-auto text-muted-foreground mb-4" />
              <p className="text-muted-foreground">You haven't submitted any reports yet.</p>
              <p className="text-sm text-muted-foreground mt-2">
                Help keep our platform safe by reporting suspicious activities.
              </p>
            </CardContent>
          </Card>
        )}
      </div>
    </div>
  );
}

